// ZnakomstvoPage.js

import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import videoSource from './adaptation/1 day/1 день - Знакомство.mp4';
import './ZnakomstvoPage.css';

const ZnakomstvoPage = () => {
  const videoRef = useRef(null);
  const [maxTime, setMaxTime] = useState(0);
  const [videoEnded, setVideoEnded] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const savedTime = localStorage.getItem('znakomstvo_video_progress');
    if (savedTime && videoRef.current) {
      videoRef.current.currentTime = parseFloat(savedTime);
    }
  }, []);

  const handleTimeUpdate = e => {
    const current = e.target.currentTime;
    if (current > maxTime) setMaxTime(current);
    localStorage.setItem('znakomstvo_video_progress', current);
  };

  const handleSeeking = e => {
    if (e.target.currentTime > maxTime) {
      e.target.currentTime = maxTime;
    }
  };

  const handleVideoEnded = () => setVideoEnded(true);

  const handleSurveySubmit = e => {
    e.preventDefault();
    alert('Спасибо за прохождение опроса!');
  };

  return (
    <div className="znakomstvo-container">
      {/* Кнопка «Назад» */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={() => navigate(-1)}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
            <path d="M12 20l-8-8 8-8" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>
      </div>

      {/* Заголовок */}
      <h1 className="znakomstvo-title">Знакомство с РГСЖ</h1>

      {/* Видео */}
      <video
        ref={videoRef}
        className="znakomstvo-video"
        src={videoSource}
        controls
        onTimeUpdate={handleTimeUpdate}
        onSeeking={handleSeeking}
        onEnded={handleVideoEnded}
      >
        Ваш браузер не поддерживает воспроизведение видео.
      </video>

      {/* Опрос после видео */}
      {videoEnded && (
        <form className="znakomstvo-survey" onSubmit={handleSurveySubmit}>
          <h2>Опрос по пройденному материалу</h2>
          <p>Насколько понятен материал?</p>
          <div className="survey-options">
            <label><input type="radio" name="clarity" value="отлично" required /> Отлично</label>
            <label><input type="radio" name="clarity" value="хорошо" /> Хорошо</label>
            <label><input type="radio" name="clarity" value="удовлетворительно" /> Удовлетворительно</label>
            <label><input type="radio" name="clarity" value="плохо" /> Плохо</label>
          </div>
          <button type="submit">Отправить опрос</button>
        </form>
      )}
    </div>
  );
};

export default ZnakomstvoPage;
