      correct: '2',
    },
    {
      question: 'Как относитесь к деталям в работе?',
      answers: ['Считаю, что именно нюансы придают результату яркость и особое качество', 'Уделяю им внимание, когда это влияет на общее впечатление и удовлетворённость клиентов', 'Главная цель — выполнить работу по основным критериям, не вдаваясь в мелочи'],
      correct: '2',
    },
    // добавьте остальные вопросы
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [shuffledAnswers, setShuffledAnswers] = useState([]);

  const shuffleAnswers = (answers) => {
    return answers.sort(() => Math.random() - 0.5);  // Перемешиваем ответы
  };

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleNext = () => {
    if (selectedAnswer === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    setSelectedAnswer(''); // сбрасываем выбранный ответ
    setCurrentQuestion(currentQuestion + 1);
  };

  const handleFinish = () => {
    if (selectedAnswer === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    // Здесь можно отправить данные на сервер или сохранить их в файл
    alert(`Опрос завершён! Ваш результат: ${score}/${questions.length}`);
    navigate('/');  // Переход на главную или нужную страницу
  };

  useEffect(() => {
    if (questions[currentQuestion]) {
      // Перемешиваем только ответы при переходе на новый вопрос
      const shuffled = shuffleAnswers([...questions[currentQuestion].answers]);
      setShuffledAnswers(shuffled);
    }
  }, [currentQuestion]);

  return (
    <div className="assessment-container">
      <h2>{questions[currentQuestion]?.question}</h2>
      {shuffledAnswers.map((answer, index) => (
        <button
          key={index}
          className={`answer-btn ${selectedAnswer === answer ? 'selected' : ''}`}
          onClick={() => handleAnswerSelect(answer)}
        >
          {answer}
        </button>
      ))}
      <div>
        {currentQuestion === questions.length - 1 ? (
          <button className="btn red-btn" onClick={handleFinish}>Завершить опрос</button>
        ) : (
          <button className="btn red-btn" onClick={handleNext}>Далее</button>
        )}
      </div>
    </div>
  );
};

export default AssessmentPage;
