// src/WelcomePage.js

import React, { useEffect, useRef, useState } from 'react';
import { useNavigate }               from 'react-router-dom';
import { useSwipeable }              from 'react-swipeable';
import { motion, AnimatePresence }   from 'framer-motion';

import './Styles/global.css';      // Montserrat + базовые сбросы
import './Styles/background.css';  // фон-градиент, шум, subtle-dot, pi-иконка
import './Styles/logo.css';        // Анимации логотипа (оставляем без изменений)
import './WelcomePage.css';        // Стили для текста (как выше)

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import piImage         from './components/pi.png';

const WelcomePage = () => {
  const navigate = useNavigate();

  // Флаг, когда текст должен начать exit-анимацию
  const [textExiting, setTextExiting] = useState(false);

  // Рефы на логотип и текст (DOM-элементы)
  const logoRef = useRef(null);
  const textRef = useRef(null);

  // Состояние для приветственного текста
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    // 1) Выбираем приветствие по времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11)       text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else                              text = 'Доброй ночи';
    setGreeting(text);

    // 2) Запускаем entry-анимацию логотипа (добавляем CSS-класс из logo.css)
    if (logoRef.current) {
      logoRef.current.classList.add('animate-logo');
    }

    // 3) Через 2 секунды запускаем exit-анимацию:
    //    • для логотипа: переключаем CSS-классы на animate-logo-exit
    //    • для текста: устанавливаем textExiting=true → Framer Motion запустит exit
    const exitTimer = setTimeout(() => {
      if (logoRef.current) {
        logoRef.current.classList.remove('animate-logo');
        logoRef.current.classList.add('animate-logo-exit');
      }
      setTextExiting(true);
    }, 2000);

    // 4) Через 2.7 секунды переходим на /main-menu
    //    (дарим 0.7 секунды, чтобы exit-анимация логотипа успела проиграться)
    const navTimer = setTimeout(() => {
      navigate('/main-menu');
    }, 2700);

    return () => {
      clearTimeout(exitTimer);
      clearTimeout(navTimer);
    };
  }, [navigate]);

  // Опциональный свайп-обработчик
  const swipeHandlers = useSwipeable({ preventDefault: true });

  // Framer Motion-variants для текста:
  // “initial”  — x: '-100vw' (текст за левым краем экрана), opacity: 0
  // “animate”  — x: '0vw'    (текст ровно по центру экрана),  opacity: 1
  // “exit”     — x: '100vw'  (текст за правым краем экрана), opacity: 0
  const textVariants = {
    initial: {
      x: '-100vw',
      opacity: 0
    },
    animate: {
      x: '0vw',
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: [0.22, 1, 0.36, 1] // плавное ускорение → торможение
      }
    },
    exit: {
      x: '100vw',
      opacity: 0,
      transition: {
        duration: 0.8,
        ease: [0.65, 0.05, 0.36, 1] // плавное торможение → ускорение
      }
    }
  };

  return (
    <div
      className="welcome-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* 10 «subtle-dot» шариков (из background.css) */}
      <div className="subtle-dot dot-1"  />
      <div className="subtle-dot dot-2"  />
      <div className="subtle-dot dot-3"  />
      <div className="subtle-dot dot-4"  />
      <div className="subtle-dot dot-5"  />
      <div className="subtle-dot dot-6"  />
      <div className="subtle-dot dot-7"  />
      <div className="subtle-dot dot-8"  />
      <div className="subtle-dot dot-9"  />
      <div className="subtle-dot dot-10" />

      {/* pi-иконка (из background.css) */}
      <div className="pi-wrapper" style={{ '--pi-move-duration': '70s' }}>
        <img
          src={piImage}
          alt="Pi"
          className="pi-fly"
          style={{ '--pi-rotate-duration': '6s' }}
        />
      </div>

      {/* Полупрозрачный оверлей поверх фона (если нужен) */}
      <div className="mainmenu-overlay" />

      {/* === ЛОГОТИП (код для него в logo.css, без изменений) === */}
      <div ref={logoRef} className="logo-wrapper">
        <img
          src={logoImage}
          alt="Логотип РГС Жизнь"
          className="logo-image"
        />
      </div>

      {/* === ТЕКСТ-ПРИВЕТСТВИЕ (Framer Motion-движение) === */}
      <AnimatePresence>
        {!textExiting && (
          <motion.div
            ref={textRef}
            className="text-wrapper"
            variants={textVariants}
            initial="initial"
            animate="animate"
            exit="exit"
          >
            <h1 className="welcome-title">{greeting}</h1>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default WelcomePage;
