import React, { useState, useEffect } from 'react';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker'; // Ваш колесный выбор даты
import './JustincasePage.css';

const JustincasePage = () => {
  // Данные пользователя из localStorage (пример)
  const [userName, setUserName] = useState('Гость');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    if (storedName) {
      setUserName(storedName);
    }
  }, []);

  // Поля, которые собираем
  const [birthDate, setBirthDate] = useState(null);        // "dd/mm/yyyy"
  const [gender, setGender] = useState(null);              // 'male' / 'female'
  const [insuranceInfo, setInsuranceInfo] = useState(null);// 'yes' / 'no'
  const [insuranceTerm, setInsuranceTerm] = useState('1'); // срок страхования (лет)
  const [insuranceSum, setInsuranceSum] = useState('');     // форматированная сумма
  const [insuranceFrequency, setInsuranceFrequency] = useState('');

  // Шаги: всего 2 шага.
  // Step = 1: (дата рождения, пол, да/нет)
  // Step = 2: (срок, сумма, периодичность)
  const [currentStep, setCurrentStep] = useState(1);

  // ---------- Утилиты ----------
  const parseDate = (dateStr) => {
    // dateStr = "dd/mm/yyyy"
    if (!dateStr) return null;
    const [day, month, year] = dateStr.split('/');
    return new Date(+year, +month - 1, +day);
  };

  const calculateAge = (dateObj) => {
    const today = new Date();
    let age = today.getFullYear() - dateObj.getFullYear();
    const m = today.getMonth() - dateObj.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < dateObj.getDate())) {
      age--;
    }
    return age;
  };

  // Форматируем сумму с точками (1.000.000)
  const formatSum = (rawValue) => {
    let digits = rawValue.replace(/\D/g, '');
    if (!digits) return '';
    let result = '';
    while (digits.length > 3) {
      const chunk = digits.slice(-3);
      digits = digits.slice(0, -3);
      result = '.' + chunk + result;
    }
    result = digits + result;
    return result;
  };

  const handleSumChange = (e) => {
    setInsuranceSum(formatSum(e.target.value));
  };

  const handleTermChange = (e) => {
    setInsuranceTerm(e.target.value);
  };

  const handleFrequencyChange = (e) => {
    setInsuranceFrequency(e.target.value);
  };

  // ---------- Логика перехода шагов ----------
  const handleNext = () => {
    if (currentStep === 1) {
      // Проверяем дату рождения
      if (!birthDate) {
        alert('Пожалуйста, выберите дату рождения');
        return;
      }
      const bdObj = parseDate(birthDate);
      if (!bdObj) {
        alert('Неверный формат даты');
        return;
      }
      const age = calculateAge(bdObj);
      if (age < 18 || age > 70) {
        alert('Возраст должен быть от 18 до 70 лет');
        return;
      }
      // Проверяем пол
      if (!gender) {
        alert('Пожалуйста, выберите пол');
        return;
      }
      // Проверяем да/нет
      if (!insuranceInfo) {
        alert('Укажите, знаете ли Вы срок и сумму');
        return;
      }

      // Если «Нет» – завершаем сразу
      if (insuranceInfo === 'no') {
        handleFinish();
      } else {
        // Иначе идём на шаг 2
        setCurrentStep(2);
      }
    }
    else if (currentStep === 2) {
      // Проверяем срок, сумму, периодичность
      const termNum = Number(insuranceTerm);
      if (termNum < 1 || termNum > 30) {
        alert('Срок страхования должен быть от 1 до 30 лет');
        return;
      }
      const rawDigits = insuranceSum.replace(/\D/g, '');
      if (!rawDigits) {
        alert('Введите страховую сумму');
        return;
      }
      const sumNumber = Number(rawDigits);
      if (sumNumber < 1000000) {
        alert('Страховая сумма должна быть от 1 000 000 руб.');
        return;
      }
      if (!insuranceFrequency) {
        alert('Выберите периодичность оплаты');
        return;
      }

      // Если всё ок
      handleFinish();
    }
  };

  const handleFinish = () => {
    alert(`Данные:
Дата рождения: ${birthDate}
Пол: ${gender}
Срок/сумма известны: ${insuranceInfo}
Срок: ${insuranceTerm} лет
Сумма: ${insuranceSum} руб.
Периодичность: ${insuranceFrequency}
    `);
    // Здесь можно сделать POST запрос или navigate(...)
  };

  // ---------- Рендер шагов ----------
  const renderStepOne = () => (
    <>
      {/* Дата рождения */}
      <div className="form-group">
        <label className="form-label">Дата рождения:</label>
        <DateWheelPicker onChange={(dateString) => setBirthDate(dateString)} />
      </div>
      {/* Пол */}
      <div className="form-group">
        <label className="form-label">Пол:</label>
        <div className="gender-selector">
          <div
            className={`gender-option ${gender === 'male' ? 'selected' : ''}`}
            onClick={() => setGender('male')}
          >
            <div className="gender-label">Мужской</div>
            <div className="gender-icon">👨</div>
          </div>
          <div
            className={`gender-option ${gender === 'female' ? 'selected' : ''}`}
            onClick={() => setGender('female')}
          >
            <div className="gender-label">Женский</div>
            <div className="gender-icon">👩</div>
          </div>
        </div>
      </div>
      {/* Да/Нет */}
      <div className="form-group">
        <label className="form-label">
          Знаете ли Вы на какой срок и на какую сумму оформить страховой полис?
        </label>
        <div className="insurance-options">
          <button
            className={`insurance-btn ${insuranceInfo === 'yes' ? 'selected' : ''}`}
            onClick={() => setInsuranceInfo('yes')}
          >
            Да
          </button>
          <button
            className={`insurance-btn ${insuranceInfo === 'no' ? 'selected' : ''}`}
            onClick={() => setInsuranceInfo('no')}
          >
            Нет
          </button>
        </div>
      </div>
    </>
  );

  const renderStepTwo = () => (
    <>
      {/* Срок страхования */}
      <div className="form-group">
        <label className="form-label">Выберите срок страхования (лет):</label>
        <select
          className="insurance-term-select"
          value={insuranceTerm}
          onChange={handleTermChange}
        >
          {Array.from({ length: 30 }, (_, i) => i + 1).map((year) => (
            <option key={year} value={year}>
              {year}
            </option>
          ))}
        </select>
      </div>
      {/* Страховая сумма */}
      <div className="form-group">
        <label className="form-label">Введите страховую сумму (от 1 млн руб):</label>
        <input
          type="text"
          className="insurance-sum-input"
          placeholder="1.000.000"
          value={insuranceSum}
          onChange={handleSumChange}
        />
      </div>
      {/* Периодичность оплаты */}
      <div className="form-group">
        <label className="form-label">Периодичность оплаты страховой премии:</label>
        <select
          className="insurance-frequency-select"
          value={insuranceFrequency}
          onChange={handleFrequencyChange}
        >
          <option value="">Выберите период</option>
          {['Ежемесячно', 'Ежегодно', 'раз в пол года', 'ежеквартально'].map((option) => (
            <option key={option} value={option}>{option}</option>
          ))}
        </select>
      </div>
    </>
  );

  return (
    <div className="justincase-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <h1 className="justincase-title">
        Расчет по программе "На всякий случай" для сотрудника
      </h1>

      <div className="justincase-form">
        {currentStep === 1 ? renderStepOne() : renderStepTwo()}
      </div>

      {/* Кнопка "Далее" (стрелка) */}
      <div className="top-btn-container">
        <button className="top-btn" onClick={handleNext}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
            <path d="M12 4l8 8-8 8" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default JustincasePage;
