import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './NameInputPage.css';
import logo from './components/logo.png'; // путь к вашему логотипу

const NameInputPage = () => {
  const [fullName, setFullName] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Сохраняем ФИО (например, в localStorage или context)
    localStorage.setItem('userFullName', fullName);
    // Переход на страницу вопросов
    navigate('/questions');
  };

  return (
    <div className="name-input-container">
      <img src={logo} alt="Логотип" className="logo" />
      <h1 className="page-title">Введите ваше ФИО</h1>
      <form onSubmit={handleSubmit} className="name-form">
        <input
          type="text"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          placeholder="ФИО"
          className="name-input"
          required
        />
        <button type="submit" className="submit-btn">Далее</button>
      </form>
    </div>
  );
};

export default NameInputPage;
