import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';

// Данные для автодополнения
import famData from './components/autosuggest/surname.json';
import nameData from './components/autosuggest/firstname.json';
import patrData from './components/autosuggest/lastname.json';

// Фон и логотип
import backgroundImage from './components/background.png';
import logo from './components/logo.png';

// Стили
import './AssessmentPage.css';

function NewAssessmentPage() {
  // Поля ФИО
  const [famValue, setFamValue] = useState('');
  const [nameValue, setNameValue] = useState('');
  const [patrValue, setPatrValue] = useState('');

  // Подсказки для ФИО
  const [famHints, setFamHints] = useState([]);
  const [nameHints, setNameHints] = useState([]);
  const [patrHints, setPatrHints] = useState([]);

  // Списки из JSON
  const famList = famData.map(obj => obj.male || obj.female);
  const nameList = nameData.map(obj =>
    typeof obj === 'string' ? obj : (obj.firstName || obj.name)
  );
  const patrList = patrData.map(obj =>
    typeof obj === 'string' ? obj : (obj.patronymic || obj.name)
  );

  // Функции получения подсказок
  const getFamHints = val => {
    const str = val.trim().toLowerCase();
    if (str.length < 2) return [];
    return famList.filter(x => x.toLowerCase().startsWith(str));
  };
  const getNameHints = val => {
    const str = val.trim().toLowerCase();
    if (str.length < 2) return [];
    return nameList.filter(x => x.toLowerCase().startsWith(str));
  };
  const getPatrHints = val => {
    const str = val.trim().toLowerCase();
    if (str.length < 2) return [];
    return patrList.filter(x => x.toLowerCase().startsWith(str));
  };

  // Настройки Autosuggest
  const famProps = {
    placeholder: 'Введите фамилию',
    value: famValue,
    onChange: (e, { newValue }) =>
      setFamValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };
  const nameProps = {
    placeholder: 'Введите имя',
    value: nameValue,
    onChange: (e, { newValue }) =>
      setNameValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };
  const patrProps = {
    placeholder: 'Введите отчество',
    value: patrValue,
    onChange: (e, { newValue }) =>
      setPatrValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };

  // Состояния опроса
  const [started, setStarted] = useState(false);
  const [finished, setFinished] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  // ======================
  // 4) Вопросы
  // ======================
  const questions = [
    {
      question: "1. Какой формат работы вам ближе?",
      answers: [
        "Когда можно детально разобраться в процессах и построить устойчивую систему",
        "Когда задачи достаточно разнообразны и требуют гибкого подхода",
        "Когда есть чёткий алгоритм действий и понятный конечный результат"
      ],
    },
    {
      question: "2. Если коллега допустил серьёзную ошибку, что для вас логичнее сделать?",
      answers: [
        "Понять, что пошло не так, и вместе найти пути исправления",
        "В первую очередь скорректировать свои планы с учётом возникших проблем",
        "Сделать выводы для себя и минимизировать влияние на мою работу"
      ],
    },
    // ... остальные вопросы ...
  ];

  // Текущий вопрос, ответы пользователя
  const [qIndex, setQIndex] = useState(0);
  const [selected, setSelected] = useState('');
  const [answers, setAnswers] = useState([]);

  // Для анимации и случайного перемешивания
  const [fade, setFade] = useState(false);
  const [shuffled, setShuffled] = useState([]);

  // Перемешиваем ответы при смене вопроса
  useEffect(() => {
    if (started && qIndex < questions.length) {
      const mix = [...questions[qIndex].answers].sort(() => Math.random() - 0.5);
      setShuffled(mix);
      setSelected('');
    }
  }, [qIndex, started]);

  // Кнопка «Начать»
  const startSurvey = () => {
    if (!famValue.trim() || !nameValue.trim() || !patrValue.trim()) {
      setErrorMsg('Пожалуйста, заполните все поля.');
      return;
    }
    setErrorMsg('');
    setStarted(true);
  };

  // Кнопки «Назад», «Далее», «Завершить»
  const goPrev = () => {
    if (qIndex > 0) {
      setQIndex(qIndex - 1);
      setAnswers(prev => prev.slice(0, -1));
      setSelected('');
    }
  };
  const goNext = () => {
    if (!selected) {
      setErrorMsg('Выберите один из вариантов');
      return;
    }
    setErrorMsg('');
    setAnswers(prev => [...prev, selected]);
    setFade(true);
    setTimeout(() => {
      if (qIndex < questions.length - 1) {
        setQIndex(qIndex + 1);
      } else {
        finishSurvey();
      }
      setFade(false);
    }, 400);
  };
  const finishSurvey = async () => {
    setFinished(true);
    const finalArr = [...answers];
    if (selected) finalArr.push(selected);

    // Здесь можно отправить данные на сервер
    const bodyData = {
      surname: famValue,
      firstName: nameValue,
      patronymic: patrValue,
      dateTime: new Date().toISOString(),
      answers: finalArr
    };
    try {
      const response = await fetch('/api/proxy/assessment/candidate/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bodyData)
      });
      if (!response.ok) {
        console.error('Ошибка при сохранении на сервере');
      }
    } catch (err) {
      console.error('Ошибка при отправке:', err);
    }
  };

  // Основная часть: либо форма ФИО, либо вопросы, либо «спасибо»
  const renderContent = () => {
    if (finished) {
      return (
        <div>
          <h2>Спасибо за прохождение опроса, {nameValue}!</h2>
          <p>Мы свяжемся, если ваши взгляды совпадают с нашими.</p>
        </div>
      );
    }
    if (started && qIndex < questions.length) {
      return (
        <div
          className="ap-question-block"
          style={{ opacity: fade ? 0 : 1, transition: 'opacity 0.4s' }}
        >
          <h2 className="ap-question-text">
            {questions[qIndex].question}
          </h2>
          <div className="ap-answers-block">
            {shuffled.map((item, idx) => (
              <button
                key={idx}
                className={`ap-answer-btn ${selected === item ? 'ap-answer-selected' : ''}`}
                onClick={() => setSelected(item)}
              >
                {item}
              </button>
            ))}
          </div>
          {errorMsg && <p className="ap-error-message">{errorMsg}</p>}
        </div>
      );
    }
    return (
      <div>
        <h2 className="ap-fio-title">Введите ваши ФИО</h2>
        {errorMsg && <p className="ap-error-message">{errorMsg}</p>}
        <form className="ap-name-form">
          <div className="ap-input-container autosuggest-container">
            <label>Фамилия</label>
            <Autosuggest
              suggestions={famHints}
              onSuggestionsFetchRequested={({ value }) => setFamHints(getFamiliyaSugg(value))}
              onSuggestionsClearRequested={() => setFamHints([])}
              getSuggestionValue={val => val}
              renderSuggestion={val => <div>{val}</div>}
              inputProps={famProps}
            />
          </div>
          <div className="ap-input-container autosuggest-container">
            <label>Имя</label>
            <Autosuggest
              suggestions={nameHints}
              onSuggestionsFetchRequested={({ value }) => setNameHints(getNameHints(value))}
              onSuggestionsClearRequested={() => setNameHints([])}
              getSuggestionValue={val => val}
              renderSuggestion={val => <div>{val}</div>}
              inputProps={nameProps}
            />
          </div>
          <div className="ap-input-container autosuggest-container">
            <label>Отчество</label>
            <Autosuggest
              suggestions={patrHints}
              onSuggestionsFetchRequested={({ value }) => setPatrHints(getPatrHints(value))}
              onSuggestionsClearRequested={() => setPatrHints([])}
              getSuggestionValue={val => val}
              renderSuggestion={val => <div>{val}</div>}
              inputProps={patrProps}
            />
          </div>
        </form>
      </div>
    );
  };

  return (
    <div className="ap-container">
      <div className="ap-logo-container">
        <img src={logo} alt="Логотип" className="ap-logo" />
      </div>

      <div className="ap-content-wrapper">
        {renderContent()}

        {!finished && (
          <>
            {started && qIndex > 0 && (
              <div className="ap-back-btn-container">
                <button className="ap-back-btn" onClick={goPrev}>
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                    <path
                      d="M12 20l-8-8 8-8"
                      fill="none"
                      stroke="#fff"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                </button>
              </div>
            )}

            <div className="ap-top-btn-container">
              {!started ? (
                <button
                  className="ap-top-btn"
                  onClick={startSurvey}
                  disabled={!famValue || !nameValue || !patrValue}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                    <path
                      d="M12 4l8 8-8 8"
                      fill="none"
                      stroke="#fff"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                </button>
              ) : qIndex < questions.length - 1 ? (
                <button
                  className="ap-top-btn"
                  onClick={goNext}
                  disabled={!selected}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                    <path
                      d="M12 4l8 8-8 8"
                      fill="none"
                      stroke="#fff"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                </button>
              ) : (
                <button
                  className="ap-top-btn finish-btn"
                  onClick={finishSurvey}
                  disabled={!selected}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24" fill="currentColor">
                    <path d="M9 16.17L4.83 12l-1.42 1.41L9 19l10-10-1.41-1.41z" />
                  </svg>
                </button>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default NewAssessmentPage;
