import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [textVisible, setTextVisible] = useState(true);
  const [isMovingUp, setIsMovingUp] = useState(false);
  const [greeting, setGreeting] = useState('Добро пожаловать!');

  useEffect(() => {
    // Определяем текущее время и выбираем соответствующее приветствие
    const now = new Date();
    const hour = now.getHours();
    if (hour >= 6 && hour < 11) {
      setGreeting("Доброе утро");
    } else if (hour >= 11 && hour < 17) {
      setGreeting("Добрый день!");
    } else if (hour >= 17 && hour < 24) {
      setGreeting("Добрый вечер!");
    } else {
      setGreeting("Доброй ночи!");
    }

    // Через 4 секунды запускаем анимацию перемещения логотипа вверх
    const moveUpTimer = setTimeout(() => {
      setIsMovingUp(true);
    }, 4000);

    // Через 5 секунд перенаправляем на страницу регистрации
    const redirectTimer = setTimeout(() => {
      navigate('/register');
    }, 5000);

    return () => {
      clearTimeout(moveUpTimer);
      clearTimeout(redirectTimer);
    };
  }, [navigate]);

  return (
    <div className="welcome-container">
      <div className={`logo ${isMovingUp ? 'move-up' : ''}`} />
      <h1 className={`welcome-text ${textVisible ? '' : 'slideDownDisappear'}`}>
        {greeting}
      </h1>
    </div>
  );
};

export default WelcomePage;
