// src/EmployeePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate }            from 'react-router-dom';
import backgroundImage             from './components/background.png';
import logoImage                   from './components/logo.png';
import './EmployeePage.css';

export default function EmployeePage() {
  const navigate = useNavigate();
  const [theme, setTheme]     = useState('dark');
  const [animate, setAnimate] = useState(false);

  // –ó–∞–ø—É—Å–∫–∞–µ–º –∞–Ω–∏–º–∞—Ü–∏—é –ø–æ—è–≤–ª–µ–Ω–∏—è logo + –∫–Ω–æ–ø–æ–∫
  useEffect(() => {
    setAnimate(true);
  }, []);

  // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ —Ç–µ–º—ã
  const toggleTheme = () => {
    setTheme(prev => (prev === 'dark' ? 'light' : 'dark'));
  };

  // Ripple + –∑–∞–¥–µ—Ä–∂–∫–∞ –ø–µ—Ä–µ–¥ –Ω–∞–≤–∏–≥–∞—Ü–∏–µ–π
  const handleClick = (e, route) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width  = circle.style.height = `${d}px`;
    circle.style.left   = `${e.clientX - btn.offsetLeft  - r}px`;
    circle.style.top    = `${e.clientY - btn.offsetTop   - r}px`;
    circle.classList.add('ripple');
    const oldRipple = btn.getElementsByClassName('ripple')[0];
    if (oldRipple) oldRipple.remove();
    btn.appendChild(circle);

    // –ñ–¥—ë–º, —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å —ç—Ñ—Ñ–µ–∫—Ç
    setTimeout(() => navigate(route), 300);
  };

  // –¢—Ä–∏ –ø—Ä–æ–¥—É–∫—Ç–∞
  const products = [
    { to: '/justincase', label: '–ù–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π' },
    { to: '/snp',         label: '–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç' },
    { to: '/carefuture',  label: '–ó–∞–±–æ—Ç–∞ –æ –±—É–¥—É—â–µ–º' }
  ];

  return (
    <div
      className={`employeepage-container ${theme}`}
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* –ø–æ–ª—É–ø—Ä–æ–∑—Ä–∞—á–Ω–æ–µ —Ä–∞–∑–º—ã—Ç–∏–µ —Ñ–æ–Ω–∞ */}
      <div className="employeepage-overlay" />

      {/* –¢—É–º–±–ª–µ—Ä —Ç–µ–º—ã */}
      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? 'üåû' : 'üåô'}
      </button>

      {/* –ö–Ω–æ–ø–∫–∞ ¬´–î–æ–º–æ–π¬ª */}
      <button className="back-btn" onClick={() => navigate('/main-menu')}>
        {/* SVG-–∏–∫–æ–Ω–∫–∞ –¥–æ–º–∏–∫–∞ */}
        <svg width="24" height="24" viewBox="0 0 24 24">
          <path
            d="M3 11l9-9 9 9v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"
            fill="none"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>

      {/* –õ–æ–≥–æ—Ç–∏–ø */}
      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" className="employee-logo" />
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="employee-title">–í—ã–±–µ—Ä–∏—Ç–µ –ø—Ä–æ–¥—É–∫—Ç</h1>

      {/* –ö–Ω–æ–ø–∫–∏-–ø—Ä–æ–¥—É–∫—Ç—ã */}
      <div className="employee-buttons">
        {products.map((item, idx) => (
          <button
            key={item.to}
            className={`product-btn ${animate ? `animate-btn btn${idx+1}` : ''}`}
            onClick={e => handleClick(e, item.to)}
          >
            {item.label}
          </button>
        ))}
      </div>
    </div>
  );
}
