import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import './EmailInputPage.css';

const EmailInputPage = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (e) => setEmail(e.target.value);

  const handleCheckEmail = async () => {
    setStatus('Проверка e-mail...');
    try {
      // Отправляем e-mail на сервер для проверки и генерации кода
      const response = await fetch('/api/proxy/check-email', {
        method: 'POST',
        body: JSON.stringify({ email }),
        headers: { 'Content-Type': 'application/json' },
      });
      const result = await response.json();

      if (result.success) {
        // Переходим на страницу ввода кода и передаём e-mail, а также уровень (если понадобится)
        navigate('/confirm-code', { state: { email, accessLevel: result.accessLevel } });
      } else {
        setStatus(result.message);
      }
    } catch (error) {
      setStatus('Ошибка при проверке e-mail.');
      console.error(error);
    }
  };

  return (
    <div className="container">
    <div className="logo-email" />
    <h2>Введите ваш e-mail</h2>
    <div className="input-container">
      <input
        type="email"
        className="email-input"
        placeholder="E-mail"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button
        className="check-btn"
        onClick={handleCheckEmail}
        disabled={!email.trim()}
      >
        Проверить e-mail
      </button>
    </div>
    {status && <p className="status-message">{status}</p>}
  </div>
  );
};

export default EmailInputPage;
