import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

const SNPPage = () => {
  const navigate = useNavigate();

  // 1. Хранить birthDate как объект Date или null
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null);
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  // Форматирование суммы с разделителем тысяч
  const formatSum = (raw) => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = (e) => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);

    const numeric = parseInt(formatted.replace(/\./g, '')) || 0;
    if (calcType === 'premium') {
      setAmountError(numeric < 60000 ? 'Введите сумму от 60 000 руб.' : '');
    } else if (calcType === 'sum') {
      setAmountError(numeric < 450000 ? 'Введите сумму от 450 000 руб.' : '');
    }
  };

  const isAmountValid = () => {
    const numeric = parseInt(amount.replace(/\./g, '')) || 0;
    return (
      (calcType === 'premium' && numeric >= 60000) ||
      (calcType === 'sum' && numeric >= 450000)
    );
  };

  // 2. getAge теперь принимает Date
  const getAge = (date) => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) {
      age--;
    }
    return age;
  };

  // 3. canGoNext проверяет, что birthDate — Date, и возраст >=18
  const canGoNext = () => {
    if (
      !(birthDate instanceof Date) ||
      !gender ||
      !calcType ||
      !amount ||
      !isAmountValid()
    ) {
      return false;
    }
    return getAge(birthDate) >= 18;
  };

  const handleNext = async () => {
    if (!canGoNext()) return;

    const dateStr = birthDate
      .toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit', year: 'numeric' });

    const age = getAge(birthDate);

    const payload = {
      birthdate: dateStr,
      age,
      gender,
      payment: calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum: parseInt(amount.replace(/\./g, '')) || 0
    };

    try {
      setIsProcessing(true);
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      setIsProcessing(false);

      if (!res.ok) {
        console.error('Ошибка от сервера:', data);
        return;
      }

      setResultData(data);
    } catch (err) {
      setIsProcessing(false);
      console.error('Ошибка запроса:', err);
    }
  };

  const goToMenu = () => navigate('/Main-Menu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image" />
      </div>
      <h1 className="snp-title">Расчёт по программе "Стратегия на пять. Гарант"</h1>

      {isProcessing ? (
        <div className="processing-container">
          <p>Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      ) : resultData ? (
        <div className="result-container">
          <h2>Результаты расчёта</h2>
          <p><strong>Возраст:</strong> {resultData.age} лет</p>
          <p><strong>Пол:</strong> {resultData.gender}</p>
          <p><strong>Тип расчёта:</strong> {resultData.payment}</p>
          <p><strong>Сумма:</strong> {formatSum(resultData.sum.toString())} руб.</p>
          <p><strong>Итоговая премия:</strong> {formatSum((resultData.totalPremium||0).toString())} руб.</p>
        </div>
      ) : (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">Дата рождения:</label>
            {/* Конвертим любой val в Date */}
            <DateWheelPicker
              onChange={(val) => {
                if (val instanceof Date) {
                  setBirthDate(val);
                } else if (val && val.day && val.month && val.year) {
                  // если вал — объект { day, month, year }
                  setBirthDate(new Date(val.year, val.month - 1, val.day));
                } else {
                  // например, если возвращается строка "DD.MM.YYYY"
                  const [d, m, y] = ('' + val).split('.').map(Number);
                  setBirthDate(new Date(y, m - 1, d));
                }
              }}
            />
          </div>

          <div className="form-group">
            <label className="form-label">Пол:</label>
            <div className="gender-selector">
              {['male', 'female'].map((g) => (
                <div
                  key={g}
                  className={`gender-option ${gender === g ? 'selected' : ''}`}
                  onClick={() => setGender(g)}
                >
                  <div className="gender-label">{g === 'male' ? 'Мужской' : 'Женский'}</div>
                  <div className="gender-icon">{g === 'male' ? '👨' : '👩'}</div>
                </div>
              ))}
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">Расчёт от…</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => {
                  setCalcType('premium');
                  setAmount('');
                  setAmountError('');
                }}
              >
                Страхового взноса
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => {
                  setCalcType('sum');
                  setAmount('');
                  setAmountError('');
                }}
              >
                Страховой суммы
              </button>
            </div>
          </div>

          {calcType && (
            <div className="form-group">
              <label className="form-label">Введите сумму в рублях</label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType === 'premium' ? 'Минимум 60 000' : 'Минимум 450 000'}
              />
              {amountError && <div className="input-error-message">{amountError}</div>}
            </div>
          )}
        </div>
      )}

      <div className="back-btn-container">
        <button className="back-btn mainmenu-btn" onClick={goToMenu}>
          <img src={mainmenuIcon} alt="Menu" />
        </button>
      </div>

      {/* Кнопка «Далее» */}
      {!isProcessing && !resultData && (
        <div className="top-btn-container">
          <button
            className="top-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8" /></svg>
          </button>
        </div>
      )}
    </div>
  );
};

export default SNPPage;
