import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
// Импортируем видео. Если оно лежит в src/adaptation/1 day, путь может быть таким:
import videoSource from './adaptation/1 day/1 день - Знакомство.mp4';
import './ZnakomstvoPage.css';

const ZnakomstvoPage = () => {
  const videoRef = useRef(null);
  const [maxTime, setMaxTime] = useState(0);
  const [videoEnded, setVideoEnded] = useState(false);
  const [loadedProgress, setLoadedProgress] = useState(false);
  const navigate = useNavigate();

  // При монтировании загружаем сохранённое время из localStorage
  useEffect(() => {
    const savedTime = localStorage.getItem('znakomstvo_video_progress');
    if (savedTime && videoRef.current) {
      videoRef.current.currentTime = parseFloat(savedTime);
    }
    setLoadedProgress(true);
  }, []);

  // Обновляем максимальное время воспроизведения и сохраняем прогресс
  const handleTimeUpdate = (e) => {
    const current = e.target.currentTime;
    if (current > maxTime) {
      setMaxTime(current);
    }
    localStorage.setItem('znakomstvo_video_progress', current);
  };

  // Если пользователь пытается перемотать видео вперёд за пределы максимального просмотренного времени, возвращаем назад
  const handleSeeking = (e) => {
    const current = e.target.currentTime;
    if (current > maxTime) {
      e.target.currentTime = maxTime;
    }
  };

  // По окончании видео показываем опрос
  const handleVideoEnded = () => {
    setVideoEnded(true);
  };

  // Пример обработки отправки опроса
  const handleSurveySubmit = (e) => {
    e.preventDefault();
    // Здесь можно сохранить результаты опроса или отправить их на сервер
    alert('Спасибо за прохождение опроса!');
    // Например, можно перенаправить пользователя на следующую страницу:
    // navigate('/nextPage');
  };

  return (
    <div className="znakomstvo-container">
      <h1 className="znakomstvo-title">Знакомство с РГСЖ</h1>
      <video
        ref={videoRef}
        className="znakomstvo-video"
        src={videoSource}
        controls
        onTimeUpdate={handleTimeUpdate}
        onSeeking={handleSeeking}
        onEnded={handleVideoEnded}
      >
        Ваш браузер не поддерживает воспроизведение видео.
      </video>
      {videoEnded && (
        <form className="znakomstvo-survey" onSubmit={handleSurveySubmit}>
          <h2>Опрос по пройденному материалу</h2>
          <p>Насколько понятен материал?</p>
          <div className="survey-options">
            <label>
              <input type="radio" name="clarity" value="отлично" required /> Отлично
            </label>
            <label>
              <input type="radio" name="clarity" value="хорошо" /> Хорошо
            </label>
            <label>
              <input type="radio" name="clarity" value="удовлетворительно" /> Удовлетворительно
            </label>
            <label>
              <input type="radio" name="clarity" value="плохо" /> Плохо
            </label>
          </div>
          <button type="submit">Отправить опрос</button>
        </form>
      )}
    </div>
  );
};

export default ZnakomstvoPage;
