import React, { useState, useEffect } from 'react';
import './AssessmentPage.css';  // Подключаем стили для страницы оценки
import { useNavigate } from 'react-router-dom';

const AssessmentPage = () => {
  const navigate = useNavigate();

  // Массив вопросов (для полноты должно быть 25 вопросов)
  const questions = [
    {
      question: 'Какой язык программирования является самым популярным?',
      answers: ['JavaScript', 'Python', 'C++'],
      correct: 'JavaScript',
    },
    {
      question: 'Какая столица Франции?',
      answers: ['Лондон', 'Париж', 'Берлин'],
      correct: 'Париж',
    },
    {
      question: 'Какое из этих чисел четное?',
      answers: ['1', '2', '3'],
      correct: '2',
    },
    // добавьте остальные вопросы
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);

  const shuffleAnswers = (answers) => {
    return answers.sort(() => Math.random() - 0.5);  // Перемешиваем ответы
  };

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleNext = () => {
    if (selectedAnswer === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    setSelectedAnswer(''); // сбрасываем выбранный ответ
    setCurrentQuestion(currentQuestion + 1);
  };

  const handleFinish = () => {
    if (selectedAnswer === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    // Здесь можно отправить данные на сервер или сохранить их в файл
    alert(`Опрос завершён! Ваш результат: ${score}/${questions.length}`);
    navigate('/');  // Переход на главную или нужную страницу
  };

  useEffect(() => {
    // Шумим только ответы при переходе на новый вопрос
    // Важно: не мутируем исходный массив вопросов, а создаём новый массив
    const shuffledAnswers = shuffleAnswers([...questions[currentQuestion]?.answers]);
    questions[currentQuestion].answers = shuffledAnswers;
  }, [currentQuestion]);

  return (
    <div className="assessment-container">
      <h2>{questions[currentQuestion]?.question}</h2>
      {questions[currentQuestion]?.answers.map((answer, index) => (
        <button
          key={index}
          className={`answer-btn ${selectedAnswer === answer ? 'selected' : ''}`}
          onClick={() => handleAnswerSelect(answer)}
        >
          {answer}
        </button>
      ))}
      <div>
        {currentQuestion === questions.length - 1 ? (
          <button className="btn red-btn" onClick={handleFinish}>Завершить опрос</button>
        ) : (
          <button className="btn red-btn" onClick={handleNext}>Далее</button>
        )}
      </div>
    </div>
  );
};

export default AssessmentPage;
