import React, { useEffect } from 'react';
import logo from './components/logo.png';
import backgroundImage from './components/background.png';
import { useSwipeable } from 'react-swipeable';
import pollIcon from './components/icons/опросы.png';
import { useNavigate } from 'react-router-dom';
import './PollsPage.css';

const PollsPage = () => {
  const navigate = useNavigate();

  // Прокрутка страницы вверх при монтировании компонента
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  // Обработчик свайпов
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => navigate('/register'),
  });

  return (
    <div
      className="container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      <div className="logo">
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <h2>Список опросов</h2>

      <button className="poll-btn" onClick={() => navigate('/assessment')}>
        <div className="button-icon-container">
          <img src={pollIcon} alt="Опросы" className="button-icon" />
        </div>
        <span className="button-text">Оценка кандидата</span>
      </button>

      <button className="poll-btn" onClick={() => navigate('/feedback')}>
        <div className="button-icon-container">
          <img src={pollIcon} alt="Опросы" className="button-icon" />
        </div>
        <span className="button-text">Обратная связь</span>
      </button>
    </div>
  );
};

export default PollsPage;
