// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';  // стили шапки, кнопок и анимаций
import './SNPPage.css';    // наши дополнения

export default function SNPPage() {
  const navigate = useNavigate();

  // состояния
  const [step, setStep]             = useState(1);
  const [email, setEmail]           = useState('');
  const [emailError, setEmailError] = useState('');
  const [birthDate, setBirthDate]   = useState(new Date());
  const [gender, setGender]         = useState(null);   // 'male' | 'female'
  const [calcType, setCalcType]     = useState(null);   // 'premium' | 'sum'
  const [amount, setAmount]         = useState('');
  const [amountError, setAmountError] = useState('');

  // подсчет возраста
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) age--;
    return age;
  };

  // проверка суммы по типу расчета
  const isAmountValid = () => {
    const n = parseInt(amount.replace(/\D/g, ''), 10) || 0;
    return (calcType === 'premium' && n >= 60000) || (calcType === 'sum' && n >= 450000);
  };

  // активность кнопки «Далее»
  const canGoNext = () =>
    birthDate instanceof Date &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amount) &&
    isAmountValid();

  // шаг 1: проверяем почту
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // ввод суммы (цифры без форматирования)
  const onAmountChange = e => {
    const raw = e.target.value.replace(/\D/g, '');
    setAmount(raw);
    const n = parseInt(raw, 10) || 0;
    if (calcType === 'premium' && n < 60000) {
      setAmountError('Мин. 60 000 ₽');
    } else if (calcType === 'sum' && n < 450000) {
      setAmountError('Мин. 450 000 ₽');
    } else {
      setAmountError('');
    }
  };

  // при потере фокуса форматируем с разделителем тысяч
  const onAmountBlur = () => {
    let d = amount;
    let o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmount(d + o);
  };

  // «Далее» (стрелка) — здесь делайте запрос
  const handleNext = () => {
    console.log({ birthDate, gender, calcType, amount });
    // fetch('/api/...', {...})
  };

  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Шапка: Домой, Логотип, Далее → */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="Домой" />
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        {step === 2 && (
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">→</span>
          </button>
        )}
      </div>

      {/* ——— Шаг 1: ввод корпоративной почты ——— */}
      {step === 1 && (
        <>
          <div className="email-step">
            <label className="email-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              Продолжить
            </button>
          </div>
        </>
      )}

      {/* ——— Шаг 2: выбор параметров ——— */}
      {step === 2 && (
        <>
          <h1 className="polls-title animate-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          {/* Дата рождения */}
          <div className="date-section animate-btn btn1">
            <label className="section-label">Дата рождения:</label>
            <DateWheelPicker
              value={birthDate}
              onChange={val => {
                if (val instanceof Date) setBirthDate(val);
                else if (val?.day)
                  setBirthDate(new Date(val.year, val.month - 1, val.day));
              }}
            />
          </div>

          {/* Выберите пол */}
          <div className="section-gap" />
          <div className="section-subtitle">Выберите пол:</div>
          <div className="gender-section animate-btn btn2">
            <button
              className={`insurance-btn ${gender === 'male' ? 'selected' : ''}`}
              onClick={() => setGender(prev => (prev === 'male' ? null : 'male'))}
            >
              <div>Мужской</div>
              <div className="emoji">👨</div>
            </button>
            <button
              className={`insurance-btn ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => setGender(prev => (prev === 'female' ? null : 'female'))}
            >
              <div>Женский</div>
              <div className="emoji">👩</div>
            </button>
          </div>

          {/* Расчёт от… */}
          <div className="section-gap" />
          <div className="section-subtitle">Расчёт от…</div>
          <div className="insurance-options animate-btn btn3">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => setCalcType(prev => (prev === 'premium' ? null : 'premium'))}
            >
              <div>Страхового</div>
              <div>взноса</div>
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => setCalcType(prev => (prev === 'sum' ? null : 'sum'))}
            >
              <div>Страховой</div>
              <div>суммы</div>
            </button>
          </div>

          {/* Сумма */}
          {calcType && (
            <>
              <div className="section-gap" />
              <div className="section-subtitle">Сумма (₽):</div>
              <div className="sum-section animate-btn btn4">
                <input
                  type="text"
                  placeholder={calcType === 'premium' ? 'Мин. 60 000' : 'Мин. 450 000'}
                  value={amount}
                  onChange={onAmountChange}
                  onBlur={onAmountBlur}
                />
              </div>
              {amountError && <div className="input-error">{amountError}</div>}
            </>
          )}
        </>
      )}
    </div>
  );
}
