  useEffect(() => {
    if (isStarted) {
      shuffleAnswers();
    }
  }, [currentQuestion, isStarted]);

  const shuffleAnswers = () => {
    const shuffled = [...questions[currentQuestion].answers].sort(() => Math.random() - 0.5);
    setShuffledAnswers(shuffled);
  };

  const handleStart = () => {
    setIsStarted(true); // Устанавливаем флаг, что опрос начался
  };

  const handleNext = () => {
    if (!selectedAnswer) {
      setErrorMessage('Выберите вариант ответа');
      setTimeout(() => setErrorMessage(''), 3000);
      return;
    }

    setUserAnswers(prev => {
      const newAnswers = [...prev, selectedAnswer];
      console.log('Текущие ответы:', newAnswers); // Логируем массив ответов
      return newAnswers;
    });
    setSelectedAnswer('');

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
    } else {
      setIsFinished(true);  // Завершаем опрос, если мы на последнем вопросе
    }
  };

  const saveLastAnswer = () => {
    console.log('25-й ответ сохранен!');
    // Добавляем последний выбранный ответ в массив ответов
    setUserAnswers(prevAnswers => {
      const updatedAnswers = [...prevAnswers, selectedAnswer];
      console.log('Обновленные ответы:', updatedAnswers);
      return updatedAnswers;
    });
  };

  const handleFinish = async () => {
    console.log('Кнопка нажата!');
    console.log('Завершаем опрос, собираем данные...');  // Логирование для дебага
    const data = {
      fullName: `${surnameValue} ${firstNameValue} ${patronymicValue}`,
      dateTime: new Date().toISOString(),
      answers: userAnswers,
    };

    // Сохраняем 25-й ответ
    saveLastAnswer();

    console.log('Данные:', data);  // Логирование

    console.log('Отправляем данные на сервер...');

    // Задержка перед отправкой данных
    setTimeout(async () => {
      try {
        console.log('Отправка на сервер...');
        const response = await fetch("http://localhost:5000/api/assessment/save", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(data),
        });

        if (response.ok) {
          alert("Опрос завершен! Данные сохранены.");
        } else {
          const result = await response.json();
          alert(`Ошибка при сохранении данных: ${result.error}`);
        }
      } catch (error) {
        console.log("Ошибка при отправке:", error);
        console.error('Ошибка при отправке данных:', error);
        alert("Ошибка соединения с сервером.");
      }

      setIsStarted(false);
    }, 1000);  // 1 секунда задержки
  };

  if (isStarted) {
    return (
      <div className="assessment-container">
        <h2>{questions[currentQuestion].question}</h2>
        <div className="answers-container">
          {shuffledAnswers.map((answer, index) => (
