// MainMenu.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import './MainMenu.css';

const MainMenu = () => {
  const navigate = useNavigate();
  return (
    <div
      className="mainmenu-container"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      <img
        src={logoImage}
        alt="Логотип"
        className="mainmenu-logo"
      />
      <div className="mainmenu-buttons">
        <button className="mainmenu-btn" onClick={() => navigate('/polls')}>
          Опросы
        </button>
        <button className="mainmenu-btn" onClick={() => navigate('/profile')}>
          Профиль
        </button>
        <button className="mainmenu-btn" onClick={() => navigate('/employee')}>
          Сотруднику
        </button>
      </div>
    </div>
  );
};

export default MainMenu;
