import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // — состояния —
  const [step, setStep]                   = useState(1);
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null);    // 'мужской' или 'женский'
  const [calcType, setCalcType]           = useState(null);    // 'premium' или 'sum'
  const [amountRaw, setAmountRaw]         = useState('');      // цифры без разделителей
  const [amountDisplay, setAmountDisplay] = useState('');      // с точками для вида
  const [amountError, setAmountError]     = useState('');
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);

  // вычисление возраста
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) {
      age--;
    }
    return age;
  }

  // проверка суммы
  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) ||
           (calcType === 'sum'     && n >= 450000);
  }

  // активность кнопки «Далее»
  function canGoNext() {
    return (
      step === 2 &&
      getAge(birthDate) >= 18 &&
      Boolean(gender) &&
      Boolean(calcType) &&
      Boolean(amountRaw) &&
      isAmountValid()
    );
  }

  // Шаг 1: проверка корпоративной почты
  function handleEmailNext() {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // сброс предыдущих вводов
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  // форматирование суммы
  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum'     && n < 450000) setAmountError('Мин. 450 000 ₽');
    else                                           setAmountError('');
  }

  // Шаг 2: отправка на прокси
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium'
                  ? 'Страхового взноса'
                  : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка сервера', res.status, await res.text());
        alert(`Ошибка ${res.status}: смотрите консоль`);
      } else {
        setResultData(await res.json());
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  // «Домой»
  function goHome() {
    navigate('/MainMenu');
  }

  // Экран «Выполняется расчёт…»
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <p>Выполняется расчёт...</p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // Экран результата (Параметры)
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });

    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        {/* HEADER */}
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
          <button
            className="repeat-btn"
            onClick={() => {
              setResultData(null);
              setStep(2);
              setGender(null);
              setCalcType(null);
              setAmountRaw('');
              setAmountDisplay('');
              setAmountError('');
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="currentColor"
              />
            </svg>
          </button>
        </div>

        {/* общий контейнер под шапкой */}
        <div className="content-wrapper">
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>
          <div className="params-container">
            <h2>Параметры</h2>
            <p>Дата расчёта: {todayStr}</p>
            <p>Возраст: {getAge(birthDate)} лет</p>
            <p>Пол: {gender}</p>
            <p>
              Вариант расчёта:{' '}
              {calcType === 'premium'
                ? 'Страхового взноса'
                : 'Страховой суммы'}
            </p>
          </div>
        </div>
      </div>
    );
  }

  // Основной UI: Шаг 1 и Шаг 2
  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
          </svg>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        {step === 2 && (
          <button className="forward-btn" onClick={handleNext} disabled={!canGoNext()}>
            <span className="arrow-icon">→</span>
          </button>
        )}
      </div>

      <div className="content-wrapper">
        {/* Шаг 1 */}
        {step === 1 && (
          <>
            <div className="email-step">
              <label className="email-label">Корпоративная почта ВТБ:</label>
              <input
                type="email"
                placeholder="example@vtb.ru"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}
            <button className="polls-btn animate-btn btn1" onClick={handleEmailNext}>
              Продолжить
            </button>
          </>
        )}

        {/* Шаг 2 */}
        {step === 2 && (
          <>
            <h1 className="polls-title">
              Расчёт по программе «Стратегия на пять. Гарант»
            </h1>

            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) setBirthDate(val);
                  else if (val?.day) {
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                  }
                }}
              />
            </div>

            <div className="section-subtitle">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender(g => (g === 'мужской' ? null : 'мужской'))}
              >
                <div>Мужской</div><div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender(g => (g === 'женский' ? null : 'женский'))}
              >
                <div>Женский</div><div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-subtitle">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => setCalcType(c => (c === 'premium' ? null : 'premium'))}
              >
                <div>Страхового</div><div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => setCalcType(c => (c === 'sum' ? null : 'sum'))}
              >
                <div>Страховой</div><div>суммы</div>
              </button>
            </div>

            <div className="section-subtitle">Сумма (₽):</div>
            <div className="sum-section">
              <input
                type="text"
                placeholder={calcType === 'premium' ? 'Мин. 60 000' : 'Мин. 450 000'}
                value={amountDisplay}
                onChange={onAmountChange}
              />
            </div>
            {amountError && <div className="input-error">{amountError}</div>}
          </>
        )}
      </div>
    </div>
  );
}
