// src/PollsPage.js

import React, { useState, useRef } from 'react';
import { useNavigate }              from 'react-router-dom';
import { useSwipeable }             from 'react-swipeable';

import './Styles/global.css';      // Montserrat + сбросы
import './Styles/background.css';  // фон-градиент, шум, subtle-dot, pi-иконка
import './Styles/logo.css';        // лого + его CSS-анимации
import './Styles/Buttons.css';     // стили кнопок + ripple + анимации entry/exit

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import piImage         from './components/pi.png';

export default function PollsPage() {
  const navigate = useNavigate();
  const [animate, setAnimate] = useState(false);

  // Рефы на DOM-элементы
  const logoRef        = useRef(null);
  const buttonRefs     = useRef([]);
  const textTitleRef   = useRef(null);
  const piWrapperRef   = useRef(null);

  // При монтировании: запускаем entry-анимации
  React.useEffect(() => {
    setAnimate(true);

    // Логотип «въезжает» сразу
    if (logoRef.current) {
      logoRef.current.classList.add('animate-logo');
    }

    // Заголовок «Список опросов» въезжает сверху
    if (textTitleRef.current) {
      textTitleRef.current.classList.add('animate-title');
    }

    // Кнопки въезжают снизу с задержками
    buttonRefs.current.forEach((btn, idx) => {
      if (btn) {
        // класс animate-btn и btn{idx+1} берёт правила из Buttons.css
        btn.classList.add(`animate-btn`, `btn${idx + 1}`);
      }
    });

    // «π»-иконка начинает летать и вращаться
    if (piWrapperRef.current) {
      piWrapperRef.current.classList.add('pi-active');
    }
  }, []);

  // Обработчик нажатия на кнопку: запускаем exit-анимации всех элементов, затем navigate
  const handleClick = (e, path) => {
    const btn = e.currentTarget;

    // 1) создаём ripple-эффект
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width = circle.style.height = `${d}px`;
    const rect = btn.getBoundingClientRect();
    circle.style.left = `${e.clientX - rect.left - r}px`;
    circle.style.top  = `${e.clientY - rect.top  - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    // 2) Запускаем exit-анимацию логотипа
    if (logoRef.current) {
      logoRef.current.classList.remove('animate-logo');
      logoRef.current.classList.add('animate-logo-exit');
    }

    // 3) Exit-анимация заголовка
    if (textTitleRef.current) {
      textTitleRef.current.classList.remove('animate-title');
      textTitleRef.current.classList.add('animate-title-exit');
    }

    // 4) Exit-анимация кнопок: в обратном порядке (чтобы последняя скрывалась первой)
    // delay = idx * 0.1s
    buttonRefs.current
      .slice()                // создаём копию массива
      .reverse()              // инвертируем порядок
      .forEach((btn, reverseIdx) => {
        if (!btn) return;
        // удаляем entry-классы
        btn.classList.remove('animate-btn', `btn${polls.length - reverseIdx}`);
        // добавляем exit-анимацию из Buttons.css – slideDownFromTop
        btn.classList.add('animate-btn-exit', `btn-exit${reverseIdx + 1}`);
      });

    // 5) «π»-иконка скрывается (например, можно сделать fade-out)
    if (piWrapperRef.current) {
      piWrapperRef.current.classList.remove('pi-active');
      piWrapperRef.current.classList.add('pi-exit');
    }

    // 6) После задержки (0.8s для exit-анимаций) переходим на страницу
    setTimeout(() => {
      navigate(path);
    }, 800);
  };

  // Свайп-обработчик (опционально)
  const swipeHandlers = useSwipeable({ preventDefault: true });

  const polls = [
    { label: 'Оценка кандидата', path: '/assessment' },
    { label: 'Обратная связь',    path: '/feedback'   },
    { label: 'Маржа продаж',      path: '/marzapoll'  },
  ];

  return (
    <div
      className="polls-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* 10 «subtle-dot» шариков (background.css) */}
      <div className="subtle-dot dot-1"  />
      <div className="subtle-dot dot-2"  />
      <div className="subtle-dot dot-3"  />
      <div className="subtle-dot dot-4"  />
      <div className="subtle-dot dot-5"  />
      <div className="subtle-dot dot-6"  />
      <div className="subtle-dot dot-7"  />
      <div className="subtle-dot dot-8"  />
      <div className="subtle-dot dot-9"  />
      <div className="subtle-dot dot-10" />

      {/* «π»-иконка */}
      <div ref={piWrapperRef} className="pi-wrapper">
        <img src={piImage} alt="Pi" className="pi-fly" />
      </div>

      {/* Полупрозрачный оверлей */}
      <div className="polls-overlay" />

      {/* Заголовок страницы (ждёт entry) */}
      <h2 ref={textTitleRef} className="polls-title">
        Список опросов
      </h2>

      {/* Логотип (entry/exit-классы из logo.css) */}
      <div ref={logoRef} className="logo-wrapper">
        <img src={logoImage} alt="Логотип" className="logo-image" />
      </div>

      {/* Кнопки */}
      <div className="button-container">
        {polls.map((p, i) => (
          <button
            key={p.path}
            ref={el => (buttonRefs.current[i] = el)}
            className="btn-custom"
            onClick={e => handleClick(e, p.path)}
          >
            {p.label}
          </button>
        ))}
      </div>
    </div>
  );
}
