// src/PollsPage.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable';

import './Styles/global.css';      // Montserrat + сбросы
import './Styles/background.css';  // фон-градиент, шум, subtle-dot, pi-иконка
import './Styles/logo.css';        // логотип
import './Styles/Buttons.css';     // стили кнопок
import './PollsPage.css';          // обновлённые стили PollsPage

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';

export default function PollsPage() {
  const navigate = useNavigate();
  const [animate, setAnimate] = useState(false);

  // Реф на логотип
  const logoRef = useRef(null);

  useEffect(() => {
    // 1) Запуск анимации логотипа
    setAnimate(true);
    if (logoRef.current) {
      logoRef.current.classList.add('animate-logo');
    }

    // 2) Запускаем exit-анимацию логотипа через 2 секунды
    const exitLogoTimer = setTimeout(() => {
      if (logoRef.current) {
        logoRef.current.classList.remove('animate-logo');
        logoRef.current.classList.add('animate-logo-exit');
      }
    }, 2000);

    return () => {
      clearTimeout(exitLogoTimer);
    };
  }, []);

  // Обработчик нажатия на кнопку (с эффектом ripple)
  const handleClick = (e, path) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width = circle.style.height = `${d}px`;
    const rect = btn.getBoundingClientRect();
    circle.style.left = `${e.clientX - rect.left - r}px`;
    circle.style.top  = `${e.clientY - rect.top  - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    setTimeout(() => navigate(path), 300);
  };

  // Свайп-хендлер (если нужно)
  const swipeHandlers = useSwipeable({ preventDefault: true });

  // Список кнопок
  const polls = [
    { label: 'Оценка кандидата', path: '/assessment' },
    { label: 'Обратная связь',    path: '/feedback'   },
    { label: 'Маржа продаж',      path: '/marzapoll'  },
  ];

  return (
    <div
      className="polls-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* 10 «subtle-dot» шариков (как на MainMenu) */}
      <div className="subtle-dot dot-1"  />
      <div className="subtle-dot dot-2"  />
      <div className="subtle-dot dot-3"  />
      <div className="subtle-dot dot-4"  />
      <div className="subtle-dot dot-5"  />
      <div className="subtle-dot dot-6"  />
      <div className="subtle-dot dot-7"  />
      <div className="subtle-dot dot-8"  />
      <div className="subtle-dot dot-9"  />
      <div className="subtle-dot dot-10" />

      {/* Полупрозрачный оверлей */}
      <div className="polls-overlay" />

      {/* === ЛОГОТИП === */}
      <div ref={logoRef} className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="Логотип" className="logo-image" />
      </div>

      {/* === ТЕКСТ-ПРИВЕТСТВИЕ === */}
      <h2 className={`polls-title ${animate ? 'animate-title' : ''}`}>
        Список опросов
      </h2>

      {/* === КНОПКИ === */}
      <div className="button-container">
        {polls.map((p, i) => (
          <button
            key={p.path}
            className={`btn-custom ${animate ? `animate-btn btn${i+1}` : ''}`}
            onClick={e => handleClick(e, p.path)}
          >
            {p.label}
          </button>
        ))}
      </div>
    </div>
  );
}
