      ],
    },
    {
      question: 'Как воспринимаете конечных пользователей или клиентов вашей работы?',
      answers: [
        'Стараюсь удивить их качеством и оригинальностью, чтобы оставить сильное впечатление',
        'Важно, чтобы они получили то, что нужно, и остались довольны',
        'Главное — соответствие техническим требованиям и срокам, без лишних нюансов'
      ],
    },
  ];

  const handleNext = () => {
    if (!selectedAnswer) {
      setErrorMessage('Выберите вариант ответа');
      setTimeout(() => setErrorMessage(''), 3000);
      return;
    }

    setUserAnswers(prev => [...prev, selectedAnswer]);
    setSelectedAnswer('');

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
    } else {
      setIsFinished(true);
    }
  };

  const saveAssessmentResults = async () => {
    const data = {
      fullName: `${surnameValue} ${firstNameValue} ${patronymicValue}`,
      dateTime: new Date().toISOString(),
      answers: userAnswers,
    };

    try {
      const response = await fetch("http://localhost:5000/api/assessment/save", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        alert("Данные успешно сохранены!");
      } else {
        const result = await response.json();
        alert(`Ошибка: ${result.error}`);
      }
    } catch (error) {
      console.error("Ошибка при отправке данных:", error);
      alert("Не удалось сохранить данные. Проверьте сервер.");
    }
  };

  const handleFinish = async () => {
    await saveAssessmentResults();
    alert("Опрос завершен! Спасибо за участие.");
    setIsStarted(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!surnameValue || !firstNameValue || !patronymicValue) {
      setErrorMessage('Заполните все поля');
      setTimeout(() => setErrorMessage(''), 3000);
      return;
    }
    setIsStarted(true);
  };

  /*** Если опрос начат, показываем вопросы ***/
  if (isStarted) {
    return (
      <div className="assessment-container">
        <h2>{questions[currentQuestion].question}</h2>
        <div className="answers-container">
          {questions[currentQuestion].answers.map((answer, index) => (
            <button
              key={index}
              className={`answer-btn ${selectedAnswer === answer ? 'selected' : ''}`}
              onClick={() => setSelectedAnswer(answer)}
            >
              {answer}
            </button>
          ))}
        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>}

        {currentQuestion < questions.length - 1 ? (
          <button className="btn" onClick={handleNext}>Далее</button>
        ) : (
          <button className="btn" onClick={handleFinish}>Завершить опрос</button>
        )}
      </div>
    );
  }

  /*** Если опрос не начат, показываем ввод ФИО ***/
  return (
    <div className="assessment-container">
      <h2>Введите ваши ФИО</h2>
      <form onSubmit={handleSubmit} className="name-form">
        <Autosuggest suggestions={surnameSuggestions} inputProps={surnameInputProps} />
        <Autosuggest suggestions={firstNameSuggestions} inputProps={firstNameInputProps} />
        <Autosuggest suggestions={patronymicSuggestions} inputProps={patronymicInputProps} />
