// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const [step, setStep]               = useState(1);
  const [email, setEmail]             = useState('');
  const [emailError, setEmailError]   = useState('');
  const [birthDate, setBirthDate]     = useState(new Date());
  const [gender, setGender]           = useState(null);
  const [calcType, setCalcType]       = useState(null);
  const [amountRaw, setAmountRaw]     = useState('');
  const [amountDisplay, setAmountDisplay] = useState('');
  const [amountError, setAmountError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData]     = useState(null);

  // –≤–æ–∑—Ä–∞—Å—Ç
  const getAge = d => {
    if (!(d instanceof Date)) return 0;
    const t = new Date();
    let age = t.getFullYear() - d.getFullYear();
    if (
      t.getMonth() < d.getMonth() ||
      (t.getMonth() === d.getMonth() && t.getDate() < d.getDate())
    ) age--;
    return age;
  };

  // –≤–∞–ª–∏–¥–∞—Ü–∏—è —Å—É–º–º—ã
  const isAmountValid = () => {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) || (calcType === 'sum' && n >= 450000);
  };

  // –º–æ–∂–Ω–æ –ª–∏ –¥–∞–ª—å—à–µ
  const canGoNext = () =>
    step === 2 &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amountRaw) &&
    isAmountValid();

  // –®–∞–≥ 1 ‚Äì –ø—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ—á—Ç—ã
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('–ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—É—é –ø–æ—á—Ç—É –¥–æ–º–µ–Ω–∞ @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // –≤–≤–æ–¥ —Å—É–º–º—ã ‚Äì raw + display
  const onAmountChange = e => {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    // –≤–∏–∑—É–∞–ª—å–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);
    // –≤–∞–ª–∏–¥–∞—Ü–∏—è
    const n = parseInt(raw, 10) || 0;
    if (calcType === 'premium' && n < 60000)      setAmountError('–ú–∏–Ω. 60 000 ‚ÇΩ');
    else if (calcType === 'sum' && n < 450000)    setAmountError('–ú–∏–Ω. 450 000 ‚ÇΩ');
    else                                          setAmountError('');
  };

  // –®–∞–≥ ¬´–î–∞–ª–µ–µ ‚Üí¬ª
  const handleNext = async () => {
    setIsProcessing(true);
    // —Å–æ–±–∏—Ä–∞–µ–º payload
    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      const data = await res.json();
      setResultData(data);
    } catch (err) {
      console.error(err);
    } finally {
      setIsProcessing(false);
    }
  };

  const goHome = () => navigate('/MainMenu');

  // –ï—Å–ª–∏ –∏–¥—ë—Ç —Ä–∞—Å—á—ë—Ç, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –ø—Ä–æ–º–µ–∂—É—Ç–æ—á–Ω—ã–π —ç–∫—Ä–∞–Ω
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
        <div className="arrow-animation">‚Üí</div>
      </div>
    );
  }

  // –ï—Å–ª–∏ –µ—Å—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã ‚Äì –≤—ã–≤–æ–¥–∏–º (–º–æ–∂–µ—Ç–µ –æ—Ñ–æ—Ä–º–∏—Ç—å –ø–æ —Å–≤–æ–µ–º—É)
  if (resultData) {
    return (
      <div className="result-container">
        <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
        <p><strong>–í–æ–∑—Ä–∞—Å—Ç:</strong> {resultData.age} –ª–µ—Ç</p>
        <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
        <p><strong>–¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞:</strong> {resultData.payment}</p>
        <p><strong>–°—É–º–º–∞:</strong> {amountDisplay} ‚ÇΩ</p>
        <p><strong>–ò—Ç–æ–≥–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {resultData.totalPremium} ‚ÇΩ</p>
      </div>
    );
  }

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* HEADER */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π" />
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" />
        </div>
        {step === 2 && (
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">‚Üí</span>
          </button>
        )}
      </div>

      {/* –®–∞–≥ 1 */}
      {step === 1 && (
        <>
          <div className="email-step">
            <label className="email-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}
          <div className="polls-buttons">
            <button className="polls-btn btn1" onClick={handleEmailNext}>
              –ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å
            </button>
          </div>
        </>
      )}

      {/* –®–∞–≥ 2 */}
      {step === 2 && (
        <>
          <h1 className="polls-title">–†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª</h1>

          {/* –î–∞—Ç–∞ */}
          <div className="date-section">
            <label className="section-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <div className="date-wheels">
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) setBirthDate(val);
                  else if (val?.day)
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                }}
              />
            </div>
          </div>

          <div className="section-gap" />
          <div className="section-subtitle">–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª:</div>
          <div className="gender-section">
            <button
              className={`insurance-btn ${gender === 'male' ? 'selected' : ''}`}
              onClick={() => setGender(prev => (prev === 'male' ? null : 'male'))}
            >
              <div>–ú—É–∂—Å–∫–æ–π</div><div className="emoji">üë®</div>
            </button>
            <button
              className={`insurance-btn ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => setGender(prev => (prev === 'female' ? null : 'female'))}
            >
              <div>–ñ–µ–Ω—Å–∫–∏–π</div><div className="emoji">üë©</div>
            </button>
          </div>

          <div className="section-gap" />
          <div className="section-subtitle">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</div>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() =>
                setCalcType(prev => (prev === 'premium' ? null : 'premium'))
              }
            >
              <div>–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ</div><div>–≤–∑–Ω–æ—Å–∞</div>
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() =>
                setCalcType(prev => (prev === 'sum' ? null : 'sum'))
              }
            >
              <div>–°—Ç—Ä–∞—Ö–æ–≤–æ–π</div><div>—Å—É–º–º—ã</div>
            </button>
          </div>

          {calcType && (
            <>
              <div className="section-gap" />
              <div className="section-subtitle">–°—É–º–º–∞ (‚ÇΩ):</div>
              <div className="sum-section">
                <input
                  type="text"
                  placeholder={calcType === 'premium' ? '–ú–∏–Ω. 60 000' : '–ú–∏–Ω. 450 000'}
                  value={amountDisplay}
                  onChange={onAmountChange}
                />
              </div>
              {amountError && <div className="input-error">{amountError}</div>}
            </>
          )}
        </>
      )}
    </div>
  );
}
