import React, { useState } from 'react';
import './RegisterPage.css';  // Импортируем стили для страницы регистрации
import { useNavigate } from 'react-router-dom';

const RegisterPage = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleRegister = async () => {
    setStatus('Проверка email...');

    // Пример запроса на сервер для проверки email
    const response = await fetch('http://127.0.0.1:5000/check_email', {
      method: 'POST',
      body: JSON.stringify({ email }),
      headers: { 'Content-Type': 'application/json' },
    });
    const result = await response.json();

    if (result.isValid) {
      setStatus('Почта проверена, уровень доступа: ' + result.level);
    } else {
      setStatus('Этот email не зарегистрирован в системе.');
    }
  };

  const handleGuestLogin = () => {
    navigate('/polls'); // Переход на страницу с опросами
  };

  return (
    <div className="register-container">
      <h2>Введите корпоративную почту</h2>
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Введите email"
        className="input-field"
      />
      <button className="btn" onClick={handleRegister}>Зарегистрироваться</button>
      <button className="btn" onClick={handleGuestLogin}>Войти как гость</button>
      <p>{status}</p>
    </div>
  );
};

export default RegisterPage;
