import React, { useEffect, useState } from 'react';
import './NewsTicker.css';

const NewsTicker = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('/api/proxy/telegram-posts');
        const data = await response.json();
        let postsArray = Array.isArray(data.posts) ? data.posts : [];
        if (postsArray.length > 3) {
          postsArray = postsArray.slice(-3);
        }
        // Если получен только один пост – дублируем, чтобы анимация была непрерывной
        if (postsArray.length === 1) {
          postsArray = [...postsArray, ...postsArray];
        }
        setPosts(postsArray);
      } catch (error) {
        console.error("Ошибка при получении новостей:", error);
        setPosts(["Пока нет новостей"]);
      }
    };

    fetchPosts();
    const interval = setInterval(fetchPosts, 60000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="news-ticker-wrapper">
      <div className="news-header">Эксперты по жизни | ДПП РГСЖ</div>
      <div className="news-ticker-container">
        <div className="news-ticker">
          {posts.concat(posts).map((post, index) => (
            <span key={index} className="news-item">
              {post}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default NewsTicker;
