import React, { useState, useMemo } from 'react';
import Select from 'react-select';
import surnames from './components/autosuggest/surname.json'; // Файл с фамилиями
import './AssessmentPage.css';
import { useNavigate } from 'react-router-dom';

const AssessmentPage = () => {
  const navigate = useNavigate();

  const [inputValue, setInputValue] = useState('');
  const [menuIsOpen, setMenuIsOpen] = useState(false);

  const handleInputChange = (newInputValue) => {
    setInputValue(newInputValue);

    // Открываем меню после ввода второй буквы
    if (newInputValue.length > 1) {
      setMenuIsOpen(true);
    } else {
      setMenuIsOpen(false);
    }
  };

  // Преобразуем массив фамилий с данными для мужчин и женщин в формат для react-select
  const surnameOptions = surnames.map(s => ({
    label: s.male || s.female,  // Используем либо male, либо female фамилию
    value: s.male || s.female   // Для value тоже одно значение
  }));

  // Стили для react-select
  const customStyles = {
    control: (provided, state) => ({
      ...provided,
      // Устанавливаем фиксированную высоту, чтобы совпадало с высотой input
      minHeight: '48px',
      borderRadius: '10px',
      borderColor: state.isFocused ? '#666' : '#ccc',
      boxShadow: 'none',
      '&:hover': {
        borderColor: state.isFocused ? '#666' : '#ccc'
      },
    }),
    valueContainer: (provided) => ({
      ...provided,
      padding: '0 1rem',
    }),
    placeholder: (provided) => ({
      ...provided,
      color: '#aaa',
    }),
  };

  // Состояния для ФИО
  const [selectedSurname, setSelectedSurname] = useState(null);
  const [firstName, setFirstName] = useState('');
  const [patronymic, setPatronymic] = useState('');
  const [fullName, setFullName] = useState('');
