// src/MainApp.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import WelcomePage from './WelcomePage';
import RegisterPage from './RegisterPage';
import EmailInputPage from './EmailInputPage';
import ConfirmCodePage from './ConfirmCodePage';
import MainMenu from './MainMenu';  // <-- Добавлен импорт
import PollsPage from './PollsPage';
import AssessmentPage from './AssessmentPage';
import FeedbackPage from './FeedbackPage';
import NewsTicker from './components/NewsTicker';
import './index.css';

function MainApp() {
  return (
    <div className="app-container">
      <Router>
        <Routes>
          <Route path="/" element={<WelcomePage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/email-input" element={<EmailInputPage />} />
          <Route path="/confirm-code" element={<ConfirmCodePage />} />
          <Route path="/main-menu" element={<MainMenu />} />
          <Route path="/polls" element={<PollsPage />} />
          <Route path="/assessment" element={<AssessmentPage />} />
          <Route path="/feedback" element={<FeedbackPage />} />
          <Route path="/home" element={<NewsTicker />} />
        </Routes>
      </Router>
    </div>
  );
}

export default MainApp;
