import React from 'react';
import { useNavigate } from 'react-router-dom';
import './MainMenu.css';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';

// (Опционально) иконки для кнопок
// import pollsIcon    from './components/icons/polls.svg';
// import profileIcon  from './components/icons/profile.svg';
// import employeeIcon from './components/icons/employee.svg';

export default function MainMenu() {
  const navigate = useNavigate();

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* тёмно-прозрачный размытой фон для эффекта glassmorphism */}
      <div className="mainmenu-overlay" />

      {/* логотип с анимацией */}
      <img
        src={logoImage}
        alt="Логотип"
        className="mainmenu-logo"
      />

      {/* кнопки */}
      <div className="mainmenu-buttons">
        {[
          { label: 'Опросы',   to: '/polls'   /*, icon: pollsIcon */ },
          { label: 'Профиль',  to: '/profile' /*, icon: profileIcon */ },
          { label: 'Сотруднику', to: '/employee' /*, icon: employeeIcon */ },
        ].map((btn, i) => (
          <button
            key={btn.label}
            className="mainmenu-btn"
            style={{ animationDelay: `${0.5 + i * 0.15}s` }}
            onClick={() => navigate(btn.to)}
          >
            {/* {btn.icon && <img src={btn.icon} className="btn-icon" alt="" />} */}
            {btn.label}
          </button>
        ))}
      </div>
    </div>
  );
}
