import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';  // Путь к стилям
import './fonts.css';  // Путь к шрифтам

const App = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [userName, setUserName] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    console.log("Компонент App загружен!");

    const getUserData = () => {
      if (window.Telegram && window.Telegram.WebApp) {
        try {
          const user = window.Telegram.WebApp.initDataUnsafe;
          console.log("User Data:", user); // Отладка

          if (user && user.first_name) {
            setUserName(user.first_name);
          } else {
            setUserName('Гость');
          }
        } catch (error) {
          console.warn("Ошибка WebApp.initDataUnsafe:", error);
        }
      } else {
        setUserName('Гость');
      }
    };

    setTimeout(() => {
      setIsLoaded(true);
    }, 1500);

    getUserData();
  }, []);

  useEffect(() => {
    if (!isRegistered) {
      setTimeout(() => {
        console.log("Переход на регистрацию...");
        navigate('/register');
      }, 4000);  // Увеличил задержку перед переходом на регистрацию
    }
  }, [isRegistered, navigate]);

  return (
    <div className="App">
      <img src="/logo.png" alt="Логотип" className="logo" />
      <h1 className={`welcome-text ${isLoaded ? 'move-up' : ''}`}>
        Добро пожаловать, {userName}
      </h1>
    </div>
  );
};

export default App;
