// src/CareFuturePage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './CareFuturePage.css';

export default function CareFuturePage() {
  const navigate = useNavigate();

  // ===== Шаги (stage) =====
  // 'email' → 'form' → 'processing' → 'result' → 'manager' → 'manager-sent'
  const [stage, setStage] = useState('email');

  // ===== Состояния для email-шага =====
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');

  // email валидность: ок, если заканчивается на "@vtb.ru"
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  // ===== Состояния для формы расчёта =====
  const [birthParts, setBirthParts]   = useState({ day: null, month: null, year: null });
  const [birthDate, setBirthDate]     = useState(null);            // Date-объект
  const [gender, setGender]           = useState(null);            // 'мужской' | 'женский'
  const [programTerm, setProgramTerm] = useState(5);               // Срок программы в годах (5–20 лет)
  const [calcType, setCalcType]       = useState(null);            // 'premium' | 'sum'
  const [amountRaw, setAmountRaw]     = useState('');              // цифры без разделителей
  const [amountDisplay, setAmountDisplay] = useState('');          // строка «100.000»
  const [amountError, setAmountError] = useState('');

  // ===== Состояние Processing =====
  const [isProcessing, setIsProcessing] = useState(false);

  // ===== Состояние результата расчёта =====
  const [resultData, setResultData] = useState(null);

  // Индекс для карусели (0,1,2)
  const [carouselIndex, setCarouselIndex] = useState(0);

  // ===== Состояния для «Связаться с менеджером» =====
  const [mgrSurname, setMgrSurname] = useState('');
  const [mgrName, setMgrName]       = useState('');
  const [mgrCity, setMgrCity]       = useState('');
  const [mgrError, setMgrError]     = useState('');
  const [isSendingMgr, setIsSendingMgr] = useState(false);
  const [mgrSent, setMgrSent] = useState(false);

  // ===== HELPERS =====

  // При изменении birthParts рассчитываем birthDate
  useEffect(() => {
    const { day, month, year } = birthParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      // проверяем, что дата корректна (на случай некорректных колесиков)
      if (
        !isNaN(dt.getTime()) &&
        dt.getDate() === d &&
        dt.getMonth() + 1 === m &&
        dt.getFullYear() === y
      ) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthParts]);

  // Рассчитать возраст по Date
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) {
      age--;
    }
    return age;
  }

  // Формат суммы: «100000» → «100.000»
  function formatSum(rawString) {
    const digits = rawString.replace(/\D/g, '');
    if (!digits) return '';
    let s = digits;
    let res = '';
    while (s.length > 3) {
      res = '.' + s.slice(-3) + res;
      s = s.slice(0, -3);
    }
    return s + res;
  }

  // ===== Валидации и условия переходов =====

  // Можно ли нажать «Далее» на формочке?
  function canGoNextForm() {
    if (!birthDate) return false;
    if (!gender) return false;
    if (!calcType) return false;
    if (!amountRaw) return false;
    if (amountError) return false;

    const age = getAge(birthDate);
    if (age < 18) return false;

    // Если возраст + срок ‒ больше 70, запрещаем
    if (age + programTerm > 70) return false;

    return true;
  }

  // ===== Обработчики =====

  // Шаг «email» → «form»
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStage('form');
  }

  // Изменение суммы: чистим не-цифры, форматируем display
  function handleAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    const disp = formatSum(raw);
    setAmountDisplay(disp);

    const num = parseInt(raw, 10) || 0;
    if (num < 100000) {
      setAmountError('Введите сумму от 100 000 ₽');
    } else {
      setAmountError('');
    }
  }

  // Шаг «form» → отправка запроса расчёта на сервер
  async function handleFormNext() {
    if (!canGoNextForm()) return;

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });

    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      term:       programTerm,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    setIsProcessing(true);
    setStage('processing');

    try {
      const res = await fetch('/api/proxy/carefuture/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка расчёта:', res.status, await res.text());
        alert(`Ошибка расчёта: ${res.status}`);
        setIsProcessing(false);
        setStage('form');
        return;
      }
      const data = await res.json();
      /*
        Ожидаемый ответ сервера (пример структуры):
        {
          contribution: <C13>,         // ежегодный взнос
          totalContribution: <C16>,    // общая сумма взносов (считанная из Excel)
          accrualPercent: <H12>,       // уже целое, напр. 44
          accrualAmount: <H13>,
          totalAccrual: <H16>,
          finalAnnual: <M13>,
          finalTotal: <M16>
        }
      */
      setResultData(data);
      setCarouselIndex(0);
      setIsProcessing(false);
      setStage('result');
    } catch (err) {
      console.error('Ошибка сети при расчёте:', err);
      alert('Не удалось выполнить расчёт');
      setIsProcessing(false);
      setStage('form');
    }
  }

  // Шаг «Отправка менеджеру»
  async function handleManagerSubmit() {
    if (!mgrSurname.trim() || !mgrName.trim() || !mgrCity.trim()) {
      setMgrError('Заполните, пожалуйста, все поля');
      return;
    }
    setMgrError('');
    setIsSendingMgr(true);

    const fullName = `${mgrSurname.trim()} ${mgrName.trim()}`;
    const subject = `Заявка на полис "Care Future" - ${fullName}`;
    const bodyLines = [
      'Добрый день, коллега!',
      '',
      'К Вам поступила заявка на полис "Care Future".',
      '',
      `ФИО сотрудника: ${fullName}`,
      `Пол: ${gender}`,
      `Дата рождения: ${birthDate.toLocaleDateString('ru-RU', { day:'2-digit', month:'2-digit', year:'numeric'})}`,
      `Возраст: ${getAge(birthDate)}`,
      `Почта сотрудника: ${email}`,
      `Город работы: ${mgrCity.trim()}`,
      '',
      `Срок программы: ${programTerm} лет`,
      `Сумма взноса: ${formatSum(String(resultData.contribution))} ₽`,
      `Периодичность оплаты: ежегодно`
    ];
    const body = bodyLines.join('\r\n');

    const payload = { subject, body };

    try {
      const res = await fetch('/api/proxy/carefuture/send_manager', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка отправки менеджеру:', res.status, await res.text());
        setMgrError(`Ошибка отправки: ${res.status}`);
        setIsSendingMgr(false);
      } else {
        setIsSendingMgr(false);
        setMgrSent(true);
        setStage('manager-sent');
        // Через 4 секунд вернёмся на главную
        setTimeout(() => navigate('/'), 4000);
      }
    } catch (e) {
      console.error('Ошибка сети при отправке менеджеру:', e);
      setMgrError('Сетевая ошибка при отправке');
      setIsSendingMgr(false);
    }
  }

  // ===== Отрисовка разных экранов =====

  // --- Экран «Выполняется расчёт…» ---
  if (stage === 'processing') {
    return (
      <div className="processing-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <p className="processing-title">
          Выполняется расчёт…
        </p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // --- Экран «Успешная отправка менеджеру» ---
  if (stage === 'manager-sent' && mgrSent) {
    const fullName = `${mgrSurname.trim()} ${mgrName.trim()}`;
    return (
      <div className="processing-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <p className="processing-title">
          {fullName}, наш эксперт свяжется с Вами в ближайшее время!<br />
          Отличного дня, коллега!
        </p>
      </div>
    );
  }

  // --- Экран результаты расчёта («result») ---
  if (stage === 'result' && resultData) {
    // Преобразуем проценты: сервер присылает, например, 44, и выводим «44%»
    const accrualPercentStr = `${resultData.accrualPercent}%`;
    // Собственная формула для общей суммы взносов (на всякий случай, если хотим пересчитать):
    // const totalContribCalc = Number(resultData.contribution) * programTerm;

    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={() => navigate('/')}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>

          <button
            className="repeat-btn"
            onClick={() => {
              // Сброс ко второму шагу
              setStage('form');
              setResultData(null);
              setGender(null);
              setCalcType(null);
              setAmountRaw('');
              setAmountDisplay('');
              setAmountError('');
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="#fff"
              />
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          <h1 className="polls-title">Результаты расчёта по программе "Забота о будущем"</h1>

          <div className="params-container">
            <h2>Параметры</h2>
            <p><strong>Возраст:</strong> {getAge(birthDate)} лет</p>
            <p><strong>Пол:</strong> {gender}</p>
            <p><strong>Срок:</strong> {programTerm} лет</p>
            <p><strong>Периодичность оплаты:</strong> ежегодно</p>
          </div>

          {/* КАРУСЕЛЬ */}
          <div className="carousel-container">
            <div className="carousel-indicators">
              <span className={carouselIndex === 0 ? 'active' : ''}></span>
              <span className={carouselIndex === 1 ? 'active' : ''}></span>
              <span className={carouselIndex === 2 ? 'active' : ''}></span>
            </div>

            <div className="carousel-clipper">
              <div
                className="carousel-wrapper"
                style={{ transform: `translateX(-${carouselIndex * 100}%)` }}
              >
                {/* Страница 1 */}
                <div className="carousel-page">
                  <div className="first-line">В течение {programTerm} лет</div>
                  <div className="unit">Вы вносите средства</div>
                  <div className="value">{formatSum(String(resultData.contribution))} ₽</div>
                  <div className="unit">Ежегодно</div>
                  <div className="value">
                    {/* общая сумма взносов – можно взять с сервера: resultData.totalContribution,
                        или посчитать вручную: */}
                    {formatSum(String(resultData.totalContribution))} ₽
                  </div>
                  <div className="footer-label">Сумма ваших взносов</div>
                </div>

                {/* Страница 2 */}
                <div className="carousel-page">
                  <div className="first-line">Начисляем дополнительно</div>
                  <div className="unit">{accrualPercentStr} от каждого взноса</div>
                  <div className="value">{formatSum(String(resultData.accrualAmount))} ₽</div>
                  <div className="unit">Ежегодно</div>
                  <div className="value">{formatSum(String(resultData.totalAccrual))} ₽</div>
                  <div className="footer-label">Общая сумма начислений</div>
                </div>

                {/* Страница 3 */}
                <div className="carousel-page">
                  <div className="first-line">Итоговая годовая выплата</div>
                  <div className="unit">Сумма с начислениями</div>
                  <div className="value">{formatSum(String(resultData.finalAnnual))} ₽</div>
                  <div className="unit">Сумма за {programTerm} лет</div>
                  <div className="value">{formatSum(String(resultData.finalTotal))} ₽</div>
                  <div className="footer-label">Ваш капитал через {programTerm} лет</div>
                </div>
              </div>
            </div>

            <button
              className="carousel-control prev"
              onClick={() => setCarouselIndex(idx => idx - 1)}
              disabled={carouselIndex === 0}
            >
              ‹
            </button>
            <button
              className="carousel-control next"
              onClick={() => setCarouselIndex(idx => idx + 1)}
              disabled={carouselIndex === 2}
            >
              ›
            </button>
          </div>
          {/* // конец карусели // */}

          <button
            className="contact-btn"
            onClick={() => setStage('manager')}
          >
            Связаться с менеджером
          </button>
        </div>
      </div>
    );
  }

  // --- Экран «Email» (stage='email') ---
  if (stage === 'email') {
    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={() => navigate('/')}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>

          <button
            className="forward-btn"
            onClick={handleEmailNext}
            disabled={!emailValid}
          >
            <span className="arrow-icon">→</span>
          </button>
        </div>

        <div className="content-wrapper">
          <h1 className="polls-title">Забота о будущем</h1>
          <div className="email-step">
            <label className="email-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              inputMode="email"
              autoComplete="email"
              autoCapitalize="none"
              spellCheck="false"
              placeholder="name@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}

          <button
            className="polls-btn"
            onClick={handleEmailNext}
            disabled={!emailValid}
          >
            Продолжить
          </button>
        </div>
      </div>
    );
  }

  // --- Экран «Form» (stage='form') ---
  if (stage === 'form') {
    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={() => setStage('email')}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>

          <button
            className="forward-btn"
            onClick={handleFormNext}
            disabled={!canGoNextForm()}
          >
            <span className="arrow-icon">→</span>
          </button>
        </div>

        <div className="content-wrapper">
          <h1 className="polls-title">Забота о будущем</h1>

          <div className="date-section">
            <label className="section-label">Дата рождения:</label>
            <DateWheelPicker
              value={birthParts}
              onChange={val => {
                if (val instanceof Date) {
                  setBirthParts({
                    day: String(val.getDate()).padStart(2, '0'),
                    month: String(val.getMonth() + 1).padStart(2, '0'),
                    year: String(val.getFullYear())
                  });
                } else if (typeof val === 'object') {
                  setBirthParts(prev => ({
                    ...prev,
                    ...(val.day   != null ? { day: String(val.day).padStart(2, '0') } : {}),
                    ...(val.month != null ? { month: String(val.month).padStart(2, '0') } : {}),
                    ...(val.year  != null ? { year: String(val.year) } : {})
                  }));
                }
              }}
            />
          </div>

          <div className="date-section">
            <label className="section-label">Срок программы (лет):</label>
            <select
              className="date-picker-select"
              value={programTerm}
              onChange={e => setProgramTerm(Number(e.target.value))}
            >
              {Array.from({ length: 16 }, (_, i) => 5 + i).map(v => (
                <option key={v} value={v}>{v} лет</option>
              ))}
            </select>
          </div>

          <div className="section-subtitle">Выберите пол:</div>
          <div className="gender-section">
            <button
              className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
              onClick={() => setGender('мужской')}
            >
              <div>Мужской</div>
              <div className="emoji">👨</div>
            </button>
            <button
              className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
              onClick={() => setGender('женский')}
            >
              <div>Женский</div>
              <div className="emoji">👩</div>
            </button>
          </div>

          <div className="section-subtitle">Расчёт от…</div>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('premium');
                setAmountRaw('');
                setAmountDisplay('');
                setAmountError('');
              }}
            >
              <div>Страхового</div>
              <div>взноса</div>
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('sum');
                setAmountRaw('');
                setAmountDisplay('');
                setAmountError('');
              }}
            >
              <div>Страховой</div>
              <div>суммы</div>
            </button>
          </div>

          {calcType && (
            <>
              <div className="section-subtitle">Введите сумму в рублях:</div>
              <div className="sum-section">
                <input
                  type="text"
                  placeholder="Мин. 100 000"
                  value={amountDisplay}
                  onChange={handleAmountChange}
                />
              </div>
              {amountError && <div className="input-error">{amountError}</div>}
            </>
          )}
        </div>
      </div>
    );
  }

  // --- Экран «Связаться с менеджером» ---
  if (stage === 'manager') {
    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={() => setStage('result')}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
        </div>

        <div className="content-wrapper">
          <h2 className="contact-title">Введите Ваши данные:</h2>
          <div className="contact-form">
            <label>Фамилия:</label>
            <input
              type="text"
              placeholder="Введите фамилию"
              value={mgrSurname}
              onChange={e => setMgrSurname(e.target.value)}
            />

            <label>Имя:</label>
            <input
              type="text"
              placeholder="Введите имя"
              value={mgrName}
              onChange={e => setMgrName(e.target.value)}
            />

            <label>Город работы:</label>
            <input
              type="text"
              placeholder="Введите город"
              value={mgrCity}
              onChange={e => setMgrCity(e.target.value)}
            />
          </div>
          {mgrError && <div className="input-error">{mgrError}</div>}

          <button
            className="contact-btn"
            onClick={handleManagerSubmit}
            disabled={isSendingMgr}
          >
            Отправить запрос
          </button>
        </div>
      </div>
    );
  }

  // По умолчанию — возвращаем null, так как все стадии обработаны выше
  return null;
}
