import React, { useState } from 'react';
import './Register.css'; // Подключаем стили для страницы регистрации

const Register = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleEmailSubmit = async () => {
    setStatus('Проверка email...');
    // Пример отправки email на сервер
  };

  return (
    <div className="register-page">
      <h2>Введите корпоративную почту</h2>
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Введите email"
      />
      <button onClick={handleEmailSubmit}>Зарегистрироваться</button>
      {status && <p>{status}</p>}
    </div>
  );
};

export default Register;
