import React from 'react';
import { useNavigate } from 'react-router-dom'; // Для навигации
import './App.css'

const GuestPage = () => {
  const navigate = useNavigate();  // Инициализация навигации

  // Обработчик перехода к опросам
  const handleGoToPolls = () => {
    navigate('/polls');  // Переход на страницу с опросами
  };

  return (
    <div className="container">
      <h2>Список опросов</h2>
      <button className="poll-btn" onClick={handleGoToPolls}>
        Оценка кандидата
      </button>
      {/* Здесь можно добавить другие кнопки для других опросов */}
    </div>
  );
};

export default GuestPage;
