// Register.js (добавим кнопку "Войти как гость")
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';  // Используем общие стили


const Register = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const [emailValid, setEmailValid] = useState(false);
  const [verificationCode, setVerificationCode] = useState('');
  const [generatedCode, setGeneratedCode] = useState('');
  const [level, setLevel] = useState(''); // Категория (ГО РГСЖ, Кураторы РН, Партнеры)
  const navigate = useNavigate();  // Навигация для редиректа

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleEmailSubmit = async () => {
    const domain = email.split('@')[1];  // Получаем домен из email

    try {
      // Отправляем запрос на сервер для проверки домена
      const response = await axios.post('http://127.0.0.1:5000/check_email', { email });

      // Если домен найден в одной из категорий
      if (response.data.isValid) {
        setEmailValid(true);
        setLevel(response.data.level);  // Сохраняем уровень доступа
        const code = Math.random().toString(36).slice(-12); // Генерация 12-значного кода
        setGeneratedCode(code);

        sendEmail(email, code);
        setStatus('На ваш email отправлен код регистрации.');
      } else {
        setStatus('Увы, ваш домен не зарегистрирован в нашей системе.');
      }
    } catch (error) {
      console.error('Ошибка при проверке email', error);
      setStatus('Произошла ошибка при проверке email. Попробуйте снова.');
    }
  };

  const handleCodeSubmit = () => {
    if (verificationCode === generatedCode) {
      setStatus('Регистрация успешна!');
      // Присваивание уровня доступа в приложении
      console.log('Пользователь имеет уровень:', level);
      // Дополнительные действия, такие как сохранение данных о пользователе, редирект и т.д.
    } else {
      setStatus('Неверный код, попробуйте снова.');
    }
  };

  const sendEmail = (toEmail, code) => {
    // Логика отправки email
    console.log(`Sending email to: ${toEmail} with code: ${code}`);
  };

  // Функция для перехода к гостю
  const handleGuestLogin = () => {
    navigate('/guest');  // Переход на страницу с опросами
  };

  return (
    <div className="register">
      <h2>Введите вашу корпоративную почту</h2>
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Введите email"
      />
      <button onClick={handleEmailSubmit}>Зарегистрироваться</button>

      {emailValid && (
        <>
          <h3>{status}</h3>
          <input
            type="text"
            value={verificationCode}
            onChange={(e) => setVerificationCode(e.target.value)}
            placeholder="Введите код"
          />
          <button onClick={handleCodeSubmit}>Подтвердить код</button>
        </>
      )}

      {!emailValid && <h3>{status}</h3>}

      {/* Кнопка "Войти как гость" */}
      <button onClick={handleGuestLogin}>Войти как гость</button>
    </div>
  );
};

export default Register;
