import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import backgroundImage from './components/background.png'; // –§–æ–Ω–æ–≤–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
import happyGif from './components/animations/laughing_2.png';
import loyalGif from './components/animations/smiling.png';
import sadGif from './components/animations/pokerface.png';
import { useSwipeable } from 'react-swipeable';

import './FeedbackPage.css';

// –ò–º–ø–æ—Ä—Ç –¥–∞–Ω–Ω—ã—Ö –ø–∞—Ä—Ç–Ω—ë—Ä–æ–≤ –∏ —Å–ø–∏–∫–µ—Ä–æ–≤
import partners from './components/autosuggest/partners.json';
import speakersData from './components/autosuggest/speakers.json';

const FeedbackPage = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –∏–º–µ–Ω–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è (–±–µ—Ä—ë—Ç—Å—è –∏–∑ localStorage)
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName ? storedName : '–ì–æ—Å—Ç—å');
  }, []);

  // –§—É–Ω–∫—Ü–∏–∏ –æ–±—Ä–∞–±–æ—Ç–∫–∏ —Ñ–æ–∫—É—Å–∞ –¥–ª—è iOS
  const handleFocus = () => {
    document.body.style.overflow = 'hidden';
  };
  const handleBlur = () => {
    document.body.style.overflow = '';
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è —Ü–≤–µ—Ç–∞ NPS –ø–æ –∑–Ω–∞—á–µ–Ω–∏—é
  const getRecommendationColor = (value) => {
    if (value <= 5) return 'red';
    else if (value <= 8) return 'yellow';
    else return 'green';
  };

  /********** –®–∞–≥ 1: –í–≤–æ–¥ –ø–∞—Ä—Ç–Ω—ë—Ä–∞ **********/
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);

  const getPartnerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return partners.filter(
      (p) => typeof p === 'string' && p.toLowerCase().includes(inputValue)
    );
  };

  const handlePartnerChange = (e, { newValue }) => {
    setPartner(newValue);
  };

  const partnerInputProps = {
    placeholder: '–ù–∞–∑–≤–∞–Ω–∏–µ –ø–∞—Ä—Ç–Ω—ë—Ä–∞',
    value: partner,
    onChange: handlePartnerChange,
    autoComplete: 'off',
    onFocus: handleFocus,
    onBlur: handleBlur,
  };

  /********** –®–∞–≥ 2: –í–≤–æ–¥ –§–ò–û —Å–ø–∏–∫–µ—Ä–∞(–æ–≤) **********/
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [speakersSuggestions, setSpeakersSuggestions] = useState([]);

  const getSpeakerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return speakersData.filter(
      (s) => typeof s === 'string' && s.toLowerCase().includes(inputValue)
    );
  };

  const handleSpeakerChange = (index, e, { newValue }) => {
    const updated = [...speakersList];
    updated[index].fullName = newValue;
    setSpeakersList(updated);
  };

  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };

  // –§—É–Ω–∫—Ü–∏—è —É–¥–∞–ª–µ–Ω–∏—è —Å–ø–∏–∫–µ—Ä–∞
  const handleRemoveSpeaker = (index) => {
    setSpeakersList((prev) => prev.filter((_, i) => i !== index));
    setSpeakerQualities((prev) => prev.filter((_, i) => i !== index));
    if (currentSpeakerIndex >= speakersList.length - 1) {
      setCurrentSpeakerIndex(Math.max(0, speakersList.length - 2));
    }
  };

  const speakerInputProps = (index) => ({
    placeholder: '–Ω–∞–ø—Ä–∏–º–µ—Ä, –ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω',
    value: speakersList[index].fullName,
    onChange: (e, { newValue }) => handleSpeakerChange(index, e, { newValue }),
    autoComplete: 'off',
    onFocus: handleFocus,
    onBlur: handleBlur,
  });

  /********** –®–∞–≥ 3: –û—Ü–µ–Ω–∫–∞ –∫–∞—á–µ—Å—Ç–≤ —Å–ø–∏–∫–µ—Ä–∞ **********/
  const qualityRows = [
    { positive: '–≠–∫—Å–ø–µ—Ä—Ç–Ω—ã–π', negative: '–ù–µ–∫–æ–º–ø–µ—Ç–µ–Ω—Ç–Ω—ã–π' },
    { positive: '–≠–Ω–µ—Ä–≥–∏—á–Ω—ã–π', negative: '–ü–∞—Å—Å–∏–≤–Ω—ã–π' },
    { positive: '–ú–æ—Ç–∏–≤–∏—Ä—É—é—â–∏–π', negative: '–ù–µ–≤–¥–æ—Ö–Ω–æ–≤–ª—è—é—â–∏–π' },
    { positive: '–•–∞—Ä–∏–∑–º–∞—Ç–∏—á–Ω—ã–π', negative: '–î—É—à–Ω—ã–π' },
    { positive: '–î–æ—Å—Ç—É–ø–Ω–æ –¥–æ–Ω–æ—Å–∏—Ç –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é', negative: '–ì–æ–≤–æ—Ä–∏—Ç —Å–ª–æ–∂–Ω—ã–º–∏ —Ç–µ—Ä–º–∏–Ω–∞–º–∏' },
  ];

  const [speakerQualities, setSpeakerQualities] = useState([]);
  const [currentSpeakerIndex, setCurrentSpeakerIndex] = useState(0);

  useEffect(() => {
    setSpeakerQualities(speakersList.map(() => Array(5).fill(null)));
  }, [speakersList]);

  const handleQualitySelectForSpeaker = (rowIndex, type) => {
    const updated = [...speakerQualities];
    const selectedText = qualityRows[rowIndex][type]; // 'positive' –∏–ª–∏ 'negative'
    updated[currentSpeakerIndex][rowIndex] =
      updated[currentSpeakerIndex][rowIndex] === selectedText ? null : selectedText;
    setSpeakerQualities(updated);
  };

  const countSelectedQualities = () => {
    if (!speakerQualities[currentSpeakerIndex]) return 0;
    return speakerQualities[currentSpeakerIndex].filter((q) => q !== null).length;
  };

  /********** –®–∞–≥ 4: –§–∞–∫—Ç–∏—á–µ—Å–∫–∞—è —Ü–µ–Ω–Ω–æ—Å—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ **********/
  const [usefulness, setUsefulness] = useState('');

  /********** –®–∞–≥ 5: –Ø—Ä–∫–∏–µ –º—ã—Å–ª–∏ **********/
  const [brightThoughts, setBrightThoughts] = useState('');

  /********** –®–∞–≥ 6: –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è **********/
  const additionalOptions = [
    '–ü—Ä–∞–∫—Ç–∏–∫–∏',
    '–ö–ª–∏–µ–Ω—Ç—Å–∫–∏—Ö –∏—Å—Ç–æ—Ä–∏–π',
    '–°—Ç–∞—Ç–∏—Å—Ç–∏—á–µ—Å–∫–∏—Ö –¥–∞–Ω–Ω—ã—Ö',
    '–§–∏—à–µ–∫ –ø—Ä–æ–¥–∞–∂',
    "–ü–µ—Ä–µ—Ä—ã–≤–æ–≤ / '–ö–æ—Ñ–µ'-–ø–∞—É–∑",
    '–†–∞–∑–±–æ—Ä –ø—Ä–æ–¥—É–∫—Ç–æ–≤–æ–π –ª–∏–Ω–µ–π–∫–∏',
    '–î–µ—Ç–∞–ª—å–Ω—ã–π —Ä–∞–∑–±–æ—Ä –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –ø—Ä–æ–¥—É–∫—Ç–∞',
  ];
  const [additionalSelections, setAdditionalSelections] = useState([]);
  const toggleAdditionalSelection = (option) => {
    if (additionalSelections.includes(option)) {
      setAdditionalSelections(additionalSelections.filter((item) => item !== option));
    } else {
      setAdditionalSelections([...additionalSelections, option]);
    }
  };

  /********** –®–∞–≥ 7: –û–±—â–µ–µ –≤–ø–µ—á–∞—Ç–ª–µ–Ω–∏–µ –æ—Ç —Ç—Ä–µ–Ω–∏–Ω–≥–∞ **********/
  const [impression, setImpression] = useState('');

  /********** –®–∞–≥ 8: –ú–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ –Ω–∞ –º–µ—Ä–æ–ø—Ä–∏—è—Ç–∏–∏ **********/
  const [mood, setMood] = useState('');

  /********** –®–∞–≥ 9: –ì–æ—Ç–æ–≤–Ω–æ—Å—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–æ–≤–∞—Ç—å —Ç—Ä–µ–Ω–∏–Ω–≥ **********/
  const [recommendation, setRecommendation] = useState(null);

  /********** –®–∞–≥ Navigation **********/
  // –®–∞–≥–∏: 1 ‚Äì –ø–∞—Ä—Ç–Ω—ë—Ä, 2 ‚Äì —Å–ø–∏–∫–µ—Ä—ã, 3 ‚Äì –∫–∞—á–µ—Å—Ç–≤–∞, 4 ‚Äì –ø–æ–ª–µ–∑–Ω–æ—Å—Ç—å, 5 ‚Äì —è—Ä–∫–∏–µ –º—ã—Å–ª–∏, 6 ‚Äì –¥–æ–ø. –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è, 7 ‚Äì –æ–±—â–µ–µ –≤–ø–µ—á–∞—Ç–ª–µ–Ω–∏–µ,
  // 8 ‚Äì –º–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ, 9 ‚Äì –≥–æ—Ç–æ–≤–Ω–æ—Å—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–æ–≤–∞—Ç—å. –§–∏–Ω–∞–ª—å–Ω—ã–π —ç–∫—Ä–∞–Ω –±—É–¥–µ—Ç –ø–æ—Å–ª–µ —à–∞–≥–∞ 9.
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);

  const getSpeakersFeedback = () => {
    return speakersList.map((speaker, i) => ({
      fullName: speaker.fullName,
      qualities: speakerQualities[i],
    }));
  };

  const canGoNext = () => {
    switch (currentStep) {
      case 1:
        return partner.trim() !== '' && partners.includes(partner);
      case 2:
        return speakersList.every(
          (s) => s.fullName.trim() !== '' && speakersData.includes(s.fullName)
        );
      case 3:
        return countSelectedQualities() >= 3;
      case 4:
        return (
          usefulness === '–ü–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (–±—É–¥—É –ø—Ä–∏–º–µ–Ω—è—Ç—å –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ)' ||
          usefulness === '–ë–µ—Å–ø–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (—Å –º–æ–∏–º–∏ –∫–ª–∏–µ–Ω—Ç–∞–º–∏ —ç—Ç–æ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç)'
        );
      case 5:
        return brightThoughts.trim().length >= 5;
      case 6:
        return additionalSelections.length > 0;
      case 7:
        return impression.trim() !== '';
      case 8:
        return mood.trim() !== '';
      case 9:
        return recommendation !== null;
      default:
        return true;
    }
  };

  const handleNext = () => {
    if (!canGoNext()) {
      alert('–ù–µ –≤—ã–ø–æ–ª–Ω–µ–Ω—ã —É—Å–ª–æ–≤–∏—è –¥–ª—è –ø–µ—Ä–µ—Ö–æ–¥–∞ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É —à–∞–≥—É');
      return;
    }
    if (currentStep === 3 && currentSpeakerIndex < speakersList.length - 1) {
      setCurrentSpeakerIndex(currentSpeakerIndex + 1);
      return;
    }
    setCurrentStep((prev) => prev + 1);
  };

  const handlePrev = () => {
    if (currentStep === 3 && currentSpeakerIndex > 0) {
      setCurrentSpeakerIndex(currentSpeakerIndex - 1);
      return;
    }
    if (currentStep > 1) {
      setCurrentStep((prev) => prev - 1);
    }
  };

  const handleFinish = async () => {
    if (!canGoNext()) {
      alert('–ù–µ –≤—ã–ø–æ–ª–Ω–µ–Ω—ã —É—Å–ª–æ–≤–∏—è –¥–ª—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –æ–ø—Ä–æ—Å–∞');
      return;
    }
    setIsFinished(true);
    const data = {
      partner,
      dateTime: new Date().toISOString(),
      commonAnswers: {
        usefulness,
        brightThoughts,
        additionalSuggestions: additionalSelections.join(', '),
        impression,
        mood,
        recommendation,
      },
      speakersFeedback: getSpeakersFeedback(),
    };
    try {
      const response = await fetch('https://rgszh-miniapp.org/api/proxy/feedback/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include',
      });
      if (!response.ok) {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö');
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ –¥–∞–Ω–Ω—ã—Ö:', error);
      alert('–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º');
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => {
      if (currentStep < 9) {
        handlePrev();
      }
    },
    onSwipedRight: () => {
      if (currentStep < 9 && canGoNext()) {
        handleNext();
      }
    },
    preventDefault: true,
  });

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="autosuggest-container">
            <h2>–í–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ –ø–∞—Ä—Ç–Ω—ë—Ä–∞</h2>
            <Autosuggest
              suggestions={partnerSuggestions}
              onSuggestionsFetchRequested={({ value }) =>
                setPartnerSuggestions(getPartnerSuggestions(value))
              }
              onSuggestionsClearRequested={() => setPartnerSuggestions([])}
              getSuggestionValue={(suggestion) => suggestion}
              renderSuggestion={(suggestion) => <div>{suggestion}</div>}
              inputProps={partnerInputProps}
            />
          </div>
        );
      case 2:
        return (
          <div>
            <h2>–í–≤–µ–¥–∏—Ç–µ –§–ò–û —Å–ø–∏–∫–µ—Ä–∞</h2>
            {speakersList.map((speaker, index) => (
              <div key={speaker.id} className="speaker-row">
                <Autosuggest
                  suggestions={speakersSuggestions}
                  onSuggestionsFetchRequested={({ value }) =>
                    setSpeakersSuggestions(getSpeakerSuggestions(value))
                  }
                  onSuggestionsClearRequested={() => setSpeakersSuggestions([])}
                  getSuggestionValue={(suggestion) => suggestion}
                  renderSuggestion={(suggestion) => <div>{suggestion}</div>}
                  inputProps={speakerInputProps(index)}
                />
                {speakersList.length > 1 && (
                  <button
                    type="button"
                    className="delete-speaker-btn"
                    onClick={() => handleRemoveSpeaker(index)}
                  >
                    –£–¥–∞–ª–∏—Ç—å
                  </button>
                )}
              </div>
            ))}
            <button type="button" className="add-speaker-btn" onClick={handleAddSpeaker}>
              <span className="add-speaker-symbol">+</span>
            </button>
          </div>
        );
      case 3:
        return (
          <div>
            <h2>–û—Ü–µ–Ω–∏—Ç–µ –∫–∞—á–µ—Å—Ç–≤–∞ —Å–ø–∏–∫–µ—Ä–∞</h2>
            <h3>{speakersList[currentSpeakerIndex].fullName || '–°–ø–∏–∫–µ—Ä'}</h3>
            {qualityRows.map((row, index) => {
              const currentValue = speakerQualities[currentSpeakerIndex]?.[index];
              return (
                <div key={index} className="quality-row">
                  <button
                    type="button"
                    className={`quality-option-btn ${
                      currentValue === row.positive ? 'positive-selected' : ''
                    }`}
                    onClick={() => handleQualitySelectForSpeaker(index, 'positive')}
                  >
                    {row.positive}
                  </button>
                  <button
                    type="button"
                    className={`quality-option-btn ${
                      currentValue === row.negative ? 'negative-selected' : ''
                    }`}
                    onClick={() => handleQualitySelectForSpeaker(index, 'negative')}
                  >
                    {row.negative}
                  </button>
                </div>
              );
            })}
            {countSelectedQualities() < 3 && (
              <p className="quality-warning">–í—ã–±–µ—Ä–∏—Ç–µ –º–∏–Ω–∏–º—É–º 3 –∫–∞—á–µ—Å—Ç–≤–∞</p>
            )}
          </div>
        );
      case 4:
        return (
          <div>
            <h2>–û—Ü–µ–Ω–∏—Ç–µ –ø–æ–ª–µ–∑–Ω–æ—Å—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏</h2>
            <div className="usefulness-container">
              <button
                type="button"
                className={`usefulness-btn ${
                  usefulness === '–ü–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (–±—É–¥—É –ø—Ä–∏–º–µ–Ω—è—Ç—å –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ)'
                    ? 'positive-selected'
                    : ''
                }`}
                onClick={() => setUsefulness('–ü–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (–±—É–¥—É –ø—Ä–∏–º–µ–Ω—è—Ç—å –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ)')}
              >
                –ü–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (–±—É–¥—É –ø—Ä–∏–º–µ–Ω—è—Ç—å –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ)
              </button>
              <button
                type="button"
                className={`usefulness-btn ${
                  usefulness === '–ë–µ—Å–ø–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (—Å –º–æ–∏–º–∏ –∫–ª–∏–µ–Ω—Ç–∞–º–∏ —ç—Ç–æ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç)'
                    ? 'negative-selected'
                    : ''
                }`}
                onClick={() =>
                  setUsefulness('–ë–µ—Å–ø–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (—Å –º–æ–∏–º–∏ –∫–ª–∏–µ–Ω—Ç–∞–º–∏ —ç—Ç–æ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç)')
                }
              >
                –ë–µ—Å–ø–æ–ª–µ–∑–Ω–æ –≤ —Ä–∞–±–æ—Ç–µ (—Å –º–æ–∏–º–∏ –∫–ª–∏–µ–Ω—Ç–∞–º–∏ —ç—Ç–æ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç)
              </button>
            </div>
          </div>
        );
      case 5:
        return (
          <div>
            <h2>–Ø—Ä–∫–∏–µ –º—ã—Å–ª–∏</h2>
            <textarea
              className="bright-thoughts-textarea"
              value={brightThoughts}
              onChange={(e) => setBrightThoughts(e.target.value)}
            />
          </div>
        );
      case 6:
        return (
          <div>
            <h2>–î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è</h2>
            {additionalOptions.map((option) => (
              <button
                key={option}
                type="button"
                className={`add-option-btn ${
                  additionalSelections.includes(option) ? 'selected' : ''
                }`}
                onClick={() => toggleAdditionalSelection(option)}
              >
                {option}
              </button>
            ))}
          </div>
        );
      case 7:
        return (
          <div>
            <h2>–û–±—â–µ–µ –≤–ø–µ—á–∞—Ç–ª–µ–Ω–∏–µ –æ—Ç —Ç—Ä–µ–Ω–∏–Ω–≥–∞</h2>
            <div className="impression-container">
              <button
                className={`impression-btn ${
                  impression === 'happy' ? 'happy-selected' : ''
                }`}
                onClick={() => setImpression('happy')}
              >
                <img src={happyGif} alt="Happy" />
              </button>
              <button
                className={`impression-btn ${
                  impression === 'neutral' ? 'neutral-selected' : ''
                }`}
                onClick={() => setImpression('neutral')}
              >
                <img src={loyalGif} alt="Neutral" />
              </button>
              <button
                className={`impression-btn ${
                  impression === 'sad' ? 'sad-selected' : ''
                }`}
                onClick={() => setImpression('sad')}
              >
                <img src={sadGif} alt="Sad" />
              </button>
            </div>
          </div>
        );
      case 8:
        return (
          <div>
            <h2>–ú–æ–µ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ –Ω–∞ –º–µ—Ä–æ–ø—Ä–∏—è—Ç–∏–∏</h2>
            <div className="mood-container">
              <button
                className={`mood-btn ${mood === 'sad' ? 'mood-selected' : ''}`}
                onClick={() => setMood('sad')}
              >
                üò¢
              </button>
              <button
                className={`mood-btn ${mood === 'tired' ? 'mood-selected' : ''}`}
                onClick={() => setMood('tired')}
              >
                üò©
              </button>
              <button
                className={`mood-btn ${mood === 'fun' ? 'mood-selected' : ''}`}
                onClick={() => setMood('fun')}
              >
                üòÑ
              </button>
              <button
                className={`mood-btn ${mood === 'thoughtful' ? 'mood-selected' : ''}`}
                onClick={() => setMood('thoughtful')}
              >
                ü§î
              </button>
              <button
                className={`mood-btn ${mood === 'cringe' ? 'mood-selected' : ''}`}
                onClick={() => setMood('cringe')}
              >
                üò¨
              </button>
            </div>
          </div>
        );
      case 9:
        return (
          <div>
            <h2>–ù–∞—Å–∫–æ–ª—å–∫–æ –≥–æ—Ç–æ–≤—ã —Ä–µ–∫–æ–º–µ–Ω–¥–æ–≤–∞—Ç—å —Ç—Ä–µ–Ω–∏–Ω–≥?</h2>
            <p>–û—Ü–µ–Ω–∏—Ç–µ –ø–æ —à–∫–∞–ª–µ –æ—Ç 0 –¥–æ 10</p>
            <div className="recommendation-slider-container">
              <input
                type="range"
                min="1"
                max="10"
                step="1"
                value={recommendation !== null ? recommendation : 5}
                onChange={(e) =>
                  setRecommendation(parseInt(e.target.value, 10))
                }
                className="recommendation-slider"
              />
              <div
                className="recommendation-value"
                style={{
                  color: getRecommendationColor(
                    recommendation !== null ? recommendation : 5
                  ),
                }}
              >
                {recommendation !== null ? recommendation : 5}
              </div>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  if (isFinished) {
    return (
      <div
        className="thank-you-message"
        style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        <h2 className="thank-you-text">
          –°–ø–∞—Å–∏–±–æ –∑–∞ –ø—Ä–æ—Ö–æ–∂–¥–µ–Ω–∏–µ –æ–ø—Ä–æ—Å–∞{userName ? `, ${userName}` : ''}!
        </h2>
      </div>
    );
  }

  return (
    <div
      className="feedback-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="content-wrapper" {...swipeHandlers}>
        {renderStep()}
        <div className="top-btn-container">
          {currentStep < 9 ? (
            <button
              className="top-btn"
              onClick={handleNext}
              disabled={!canGoNext()}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 24 24"
                fill="none"
              >
                <path d="M12 4l8 8-8 8" />
              </svg>
            </button>
          ) : (
            <button
              className="top-btn"
              onClick={handleFinish}
              disabled={!canGoNext()}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 24 24"
                fill="none"
              >
                <path d="M5 13l4 4L19 7" />
              </svg>
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default FeedbackPage;
