import React from 'react';
import { useNavigate } from 'react-router-dom';
import './FirstDayAdaptationPage.css';

const FirstDayAdaptationPage = () => {
  const navigate = useNavigate();

  // Функция для обработки нажатия на кнопки.
  // Здесь можно добавить логику навигации для каждой кнопки, подставив нужные маршруты.
  const handleButtonClick = (route) => {
    navigate(route);
  };

  return (
    <div className="first-day-adaptation-container">
      <h1 className="first-day-adaptation-title">Первый день адаптации</h1>
      <div className="first-day-adaptation-buttons">
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route1')}>
          Знакомство с РГСЖ
        </button>
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route2')}>
          Стандарты внешнего вида
        </button>
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route3')}>
          Стандарты работы
        </button>
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route4')}>
          Принципы работы
        </button>
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route5')}>
          Инструменты и виды контроля
        </button>
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route6')}>
          Мисселинг
        </button>
        <button className="first-day-adaptation-btn" onClick={() => handleButtonClick('/route7')}>
          Отчетность и сопровождение продаж
        </button>
      </div>
    </div>
  );
};

export default FirstDayAdaptationPage;
