// src/MainMenu.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';

import './Styles/Buttons.css';       // наш общий файл со стилями кнопок
import './Styles/background.css';    // чтобы градиент фона и переменные цвета были подключены
import './Styles/global.css';        // для шрифта Montserrat (если ещё не импортировано)

// Если у вас был MainMenu.css, уберите оттуда стили кнопок, чтобы не конфликтовали.

export default function MainMenu() {
  const navigate = useNavigate();
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // Запускаем анимацию логотипа (если требуется, например, CSS анимация)
    setAnimate(true);
  }, []);

  // Обработчик клика с ripple-эффектом
  const handleClick = (e, route) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(btn.clientWidth, btn.clientHeight);
    const radius = diameter / 2;
    circle.style.width  = circle.style.height = `${diameter}px`;
    circle.style.left   = `${e.clientX - btn.offsetLeft  - radius}px`;
    circle.style.top    = `${e.clientY - btn.offsetTop   - radius}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    // Навигация через 300ms (чтобы ripple-эффект успел отработать)
    setTimeout(() => navigate(route), 300);
  };

  // Два маршрута: «Опросы» и «Сотруднику»
  const buttons = [
    { to: '/polls',    label: 'Опросы'    },
    { to: '/employee', label: 'Сотруднику' },
  ];

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* При желании оставьте анимацию логотипа: */}
      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img
          src={logoImage}
          alt="Логотип РГС Жизнь"
          className="logo-image"
        />
      </div>

      {/* ===== Контейнер для кнопок (новый) ===== */}
      <div className="button-container">
        {buttons.map((btn) => (
          <button
            key={btn.to}
            className="btn-custom"
            onClick={(e) => handleClick(e, btn.to)}
          >
            {btn.label}
          </button>
        ))}
      </div>
    </div>
  );
}
