import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { CSSTransition } from 'react-transition-group';
import './MainMenu.css';
import logoImage from './components/logo.png';
import surveyIcon from './components/icons/survey.svg';
import profileIcon from './components/icons/profile.svg';
import employeeIcon from './components/icons/employee.svg';

export default function MainMenu() {
  const navigate = useNavigate();
  const [theme, setTheme] = useState('dark');
  const [inProp, setInProp] = useState(false);

  React.useEffect(() => {
    setInProp(true);
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  // ripple effect helper
  const handleRipple = e => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(btn.clientWidth, btn.clientHeight);
    const radius = diameter / 2;
    circle.style.width = circle.style.height = `${diameter}px`;
    circle.style.left = `${e.clientX - btn.offsetLeft - radius}px`;
    circle.style.top = `${e.clientY - btn.offsetTop - radius}px`;
    circle.classList.add('ripple');
    const ripple = btn.getElementsByClassName('ripple')[0];
    if (ripple) ripple.remove();
    btn.appendChild(circle);
  };

  const buttons = [
    { to: '/surveys',   label: '–û–ø—Ä–æ—Å—ã',   icon: surveyIcon },
    { to: '/profile',   label: '–ü—Ä–æ—Ñ–∏–ª—å',   icon: profileIcon },
    { to: '/employee',  label: '–°–æ—Ç—Ä—É–¥–Ω–∏–∫—É',icon: employeeIcon },
  ];

  return (
    <div className={`mainmenu-container ${theme}`}>
      <div className="mainmenu-overlay" />

      {/* theme toggle */}
      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? 'üåû' : 'üåô'}
      </button>

      {/* logo */}
      <CSSTransition in={inProp} timeout={800} classNames="logo-fade" appear>
        <img
          src={logoImage}
          alt="–õ–æ–≥–æ—Ç–∏–ø –†–ì–° –ñ–∏–∑–Ω—å"
          className="mainmenu-logo"
        />
      </CSSTransition>

      {/* buttons */}
      <div className="mainmenu-buttons">
        {buttons.map(({ to, label, icon }) => (
          <CSSTransition key={to} in={inProp} timeout={600} classNames="btn-fade" appear>
            <button
              className="mainmenu-btn"
              onClick={e => { handleRipple(e); navigate(to); }}
            >
              <img src={icon} alt="" className="btn-icon" />
              <span>{label}</span>
            </button>
          </CSSTransition>
        ))}
      </div>
    </div>
  );
}
