import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from './components/logo.png';
import techsupportIcon from './components/icons/техподдержка.png';
import icon1 from './components/icons/настройки.png';
import icon2 from './components/icons/база.png';
import icon3 from './components/icons/рейтинг.png';
import icon4 from './components/icons/чат.png';
import backgroundImage from './components/background.png';
import NewsTicker from './NewsTicker';
import clickSound from './components/sounds/click.mp3';
import IconButton from './IconButton';
import './MainMenu.css';

const MainMenu = () => {
  const navigate = useNavigate();

  // Функция воспроизведения звука при наведении
  const playHoverSound = () => {
    const audio = new Audio(clickSound);
    audio.play();
  };

  // Универсальный обработчик навигации
  const handleNavigate = (route) => {
    navigate(route);
  };

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="logo-mainmenu">
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <NewsTicker />
      <h1 className="mainmenu-title">Главное Меню</h1>
      <div className="mainmenu-buttons">
        <button className="mainmenu-btn" onClick={() => handleNavigate('/polls')}>
          Опросы
        </button>
        <button className="mainmenu-btn" onClick={() => handleNavigate('/profile')}>
          Профиль
        </button>
      </div>
      {/* Нижний закреплённый контейнер с 5 кнопками-иконками */}
      <div className="bottom-container">
        {/* Порядок: 1. техподдержка, 2. база, 3. чат, 4. рейтинг, 5. настройки */}
        <IconButton
          onClick={() => handleNavigate('/support')}
          src={techsupportIcon}
          alt="Техподдержка"
          playHoverSound={playHoverSound}
        />
        <IconButton
          onClick={() => handleNavigate('/KnowledgePage')}
          src={icon2}  // база
          alt="База"
          playHoverSound={playHoverSound}
        />
        <IconButton
          onClick={() => handleNavigate('/ChatPage')}
          src={icon4}  // чат
          alt="Чат"
          playHoverSound={playHoverSound}
        />
        <IconButton
          onClick={() => handleNavigate('/RaitingPage')}
          src={icon3}  // рейтинг
          alt="Рейтинг"
          playHoverSound={playHoverSound}
        />
        <IconButton
          onClick={() => handleNavigate('/ProfilePage')}
          src={icon1}  // настройки
          alt="Настройки"
          playHoverSound={playHoverSound}
        />
      </div>
    </div>
  );
};

export default MainMenu;
