  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);
  const [slideIndex,     setSlideIndex]     = useState(0);

  // --- –¥–ª—è –º–µ–Ω–µ–¥–∂–µ—Ä–∞ ---
  const [mgrSurname, setMgrSurname] = useState('');
  const [mgrName,    setMgrName]    = useState('');
  const [mgrCity,    setMgrCity]    = useState('');
  const [mgrError,   setMgrError]   = useState('');

  // –∫–æ–Ω—Å—Ç–∞–Ω—Ç—ã
  const term        = 5;
  const periodicity = '–µ–∂–µ–≥–æ–¥–Ω–æ';
  const currency    = '—Ä—É–±–ª–µ–π';

  // —Å–æ–±–∏—Ä–∞–µ–º Date –∏–∑ birthDateParts
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = +day, m = +month, y = +year;
      const dt = new Date(y, m-1, d);
      if (!isNaN(dt.getTime()) && dt.getDate()===d && dt.getMonth()+1===m && dt.getFullYear()===y) {
        setBirthDate(dt);
      } else setBirthDate(null);
    } else setBirthDate(null);
  }, [birthDateParts]);

  // —Ñ–æ—Ä–º–∞—Ç —Å—É–º–º—ã
  const formatSum = raw => {
    let digits = raw.replace(/\D/g,'');
    if (!digits) return '';
    let out = '';
    while (digits.length>3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0,-3);
    }
    return digits + out;
  };

  // –≤–≤–æ–¥ —Å—É–º–º—ã + –≤–∞–ª–∏–¥–∞—Ü–∏—è
  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g,''),10)||0;
    if (calcType==='premium') setAmountError(num<60000?'–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60 000 ‚ÇΩ':'');
    else if (calcType==='sum') setAmountError(num<450000?'–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450 000 ‚ÇΩ':'');
  };

  // –≤–æ–∑—Ä–∞—Å—Ç
  const getAge = d => {
    if (!(d instanceof Date)) return 0;
    const t = new Date();
    let age = t.getFullYear()-d.getFullYear();
    const m = t.getMonth()-d.getMonth();
    if (m<0 || (m===0 && t.getDate()<d.getDate())) age--;
    return age;
  };

  // –º–æ–∂–Ω–æ –ª–∏ –ø–µ—Ä–µ–π—Ç–∏ –∫ –∑–∞–ø—Ä–æ—Å—É
  const canGoNext = () =>
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender && calcType && amount && !amountError &&
    getAge(birthDate)>=18;

  // –∫–Ω–æ–ø–∫–∞ ¬´–ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–∞—Å—á–µ—Ç—É¬ª
  const handleEmailNext = () => {
    if (!email.includes('@vtb.ru')) {
      setEmailError('–î–æ–º–µ–Ω –≤–∞—à–µ–π –ø–æ—á—Ç—ã –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—ã–º. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  // –∑–∞–ø—Ä–æ—Å —Ä–∞—Å—á—ë—Ç–∞
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'});
    const age = getAge(birthDate);
    const payload = {
      birthdate: dateRu,
      age,
      gender,
      payment: calcType==='premium'?'–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞':'–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res  = await fetch('/api/snp/save',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify(payload)});
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      }
    } catch(err) {
      console.error(err);
    }
    setIsProcessing(false);
  };

  // —Å–µ–≥–æ–¥–Ω—è—à–Ω–∏–π –¥–µ–Ω—å
  const todayStr = new Date().toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'});

  // —Å–ª–∞–π–¥—ã
  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">–í –¢–ï–ß–ï–ù–ò–ï {term} –õ–ï–¢</div>
      <div className="slide-title">–í—ã –≤–Ω–æ—Å–∏—Ç–µ —Å—Ä–µ–¥—Å—Ç–≤–∞ –≤ –ø—Ä–æ–≥—Ä–∞–º–º—É</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ‚ÇΩ</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">–ù–ê–ß–ò–°–õ–Ø–ï–ú –î–û–ü–û–õ–ù–ò–¢–ï–õ–¨–ù–û</div>
      <div className="slide-title">{resultData.accrualPercent}% –æ—Ç –∫–∞–∂–¥–æ–≥–æ –≤–∑–Ω–æ—Å–∞</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–û–±—â–∞—è —Å—É–º–º–∞ –Ω–∞—á–∏—Å–ª–µ–Ω–∏–π</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ‚ÇΩ</div>
    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">–í–´ –ü–û–õ–£–ß–ê–ï–¢–ï:</div>
      <div className="slide-icon">üí∞</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ‚ÇΩ</div>
      <div className="slide-period">–ï–∂–µ–≥–æ–¥–Ω—ã–µ –Ω–∞–∫–æ–ø–ª–µ–Ω–∏—è</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ‚ÇΩ</div>
      <div className="slide-subtitle">–í–∞—à –∫–∞–ø–∏—Ç–∞–ª —á–µ—Ä–µ–∑ {term} –ª–µ—Ç</div>
    </div>
  ] : [];

  // –æ—Ç–ø—Ä–∞–≤–∫–∞ –º–µ–Ω–µ–¥–∂–µ—Ä—É
  const handleManagerSubmit = () => {
    if (!mgrSurname||!mgrName||!mgrCity) {
      setMgrError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è.');
    } else {
      setMgrError('');
      console.log({ email,mgrSurname,mgrName,mgrCity });
      navigate('/Main-Menu');
    }
  };

  return (
    <div className="snp-container" style={{backgroundImage:`url(${backgroundImage})`}}>

      {/* –î–æ–º–æ–π */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/Main-Menu')}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π"/>
        </button>
      </div>

      {/* –õ–æ–≥–æ */}
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="snp-title">–†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"</h1>

      {/* –®–ê–ì 1: Email */}
      {step==='email' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input
              type="email"
              className="insurance-sum-input"
              value={email}
              onChange={e=>setEmail(e.target.value)}
              placeholder="name@vtb.ru"
            />
          </div>
          <button className="insurance-btn selected" onClick={handleEmailNext}>
            –ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–∞—Å—á–µ—Ç—É
          </button>
          {emailError && (
            <div className="email-error-message">{emailError}</div>
          )}
        </div>
      )}

      {/* –®–ê–ì 2: –§–æ—Ä–º–∞ —Ä–∞—Å—á—ë—Ç–∞ */}
      {step==='form' && !isProcessing && !resultData && (
        <>
          <div className="snp-form">
            {/* ...–∫–∞–∫ –±—ã–ª–æ —Ä–∞–Ω–µ–µ... */}
          </div>
          <div className="top-btn-container">
            <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
              <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
            </button>
          </div>
        </>
      )}

      {/* –®–ê–ì 3: PROCESSING */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}

      {/* –®–ê–ì 4: RESULT */}
      {step==='result' && !isProcessing && resultData && (
        <>
          <div className="result-container">{/*...*/}</div>
          <div className="detail-slider-container">{/*...*/}</div>
          <button className="insurance-btn selected" onClick={()=>setStep('manager')}>
            –°–≤—è–∑–∞—Ç—å—Å—è —Å –º–µ–Ω–µ–¥–∂–µ—Ä–æ–º
          </button>
        </>
      )}

      {/* –®–ê–ì 5: MANAGER */}
      {step==='manager' && (
        <div className="snp-form">{/*...*/}</div>
      )}
    </div>
  );
}
