// src/CareFuturePage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './CareFuturePage.css';
import './SNPPage.css'; // общие стили (фон, кнопки, карусель и др.)

export default function CareFuturePage() {
  const navigate = useNavigate();

  // ===== Состояния =====
  // Шаг: 1=ввод почты, 2=ввод параметров, 3=вывод результата, 4=форма "Связаться с менеджером"
  const [step, setStep]                   = useState(1);

  // Общие поля:
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');

  // Шаг 2: параметры расчёта
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null);      // 'мужской' | 'женский' | null
  const [termYears, setTermYears]         = useState('5');       // срок (строка): '5', '10', '15' или '20'
  const [calcType, setCalcType]           = useState(null);      // 'premium' | 'sum' | null
  const [amountRaw, setAmountRaw]         = useState('');        // число без точек, например "150000"
  const [amountDisplay, setAmountDisplay] = useState('');        // форматированная строка, например "150.000"
  const [amountError, setAmountError]     = useState('');

  // Индикатор загрузки при вызове сервера
  const [isProcessing, setIsProcessing]   = useState(false);

  // Результат из сервера (JSON с ключами, см. далее)
  const [resultData, setResultData]       = useState(null);

  // Карусель: текущая страница (0, 1 или 2)
  const [carouselIndex, setCarouselIndex] = useState(0);

  // Форма "Связаться с менеджером":
  const [contactMode, setContactMode]       = useState(false);
  const [contactSending, setContactSending] = useState(false);
  const [contactSent, setContactSent]       = useState(false);

  const [lastName, setLastName]           = useState('');
  const [firstName, setFirstName]         = useState('');
  const [middleName, setMiddleName]       = useState('');
  const [workCity, setWorkCity]           = useState('');

  // ===== Валидация email =====
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  // ===== Вспомогательные функции =====
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) {
      age--;
    }
    return age;
  }

  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    // Если расчет от взноса → минимум 100 000 ₽, если расчет от суммы (sum) — > 0
    return (calcType === 'premium' && n >= 100000) ||
           (calcType === 'sum'     && n > 0);
  }

  function canGoNext() {
    return (
      step === 2 &&
      getAge(birthDate) >= 18 &&
      gender &&
      calcType &&
      amountRaw &&
      isAmountValid()
    );
  }

  // ===== Обработчик: Шаг 1 → Шаг 2 (ввод email) =====
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // Сбрасываем поля второго шага
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setTermYears('5');
    setStep(2);
  }

  // ===== Обработчик изменения суммы: форматирование "150000" → "150.000" =====
  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if (calcType === 'premium' && n < 100000) {
      setAmountError('Мин. 100 000 ₽');
    } else {
      setAmountError('');
    }
  }

  // ===== Обработчик: Шаг 2 → Вызов сервера ("/api/proxy/carefuture/save") =====
  async function handleCalcNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    // Форматируем дату рождения в строку "дд.мм.гггг"
    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const termNumeric = parseInt(termYears, 10);

    // payload, который сервер ждёт:
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      term:       termNumeric,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/carefuture/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка сервера:', res.status, await res.text());
        alert(`Ошибка ${res.status}: смотрите консоль`);
      } else {
        const data = await res.json();
        /*
          Ожидаемый ответ от server.py (CareFuture):

          {
            "contribution":      <явно записанная пользователем ежегодная сумма>,  // rv("C13")
            "totalContribution": <общая сумма взносов за весь срок ("C16")>,
            "accrualPercent":    <ячейка H12 в Excel (например 44)>,
            "accrualAmount":     <ячейка H13 (начисление за год)>,
            "totalAccrual":      <ячейка H16 (общая сумма начислений)>,
            "finalAnnual":       <ячейка M13 (итоговая годовая выплата)>,
            "finalTotal":        <ячейка M16 (итоговая сумма за срок)>
          }
        */
        setResultData({
          // Возвращённый сервером JSON добавляем к локальным полям:
          age:        payload.age,
          gender:     payload.gender,
          term:       payload.term,
          payment:   (payload.payment === 'Страхового взноса' ? 'Страхового взноса' : 'Страховой суммы'),
          // А остальное лежит в data:
          ...data
        });
        setCarouselIndex(0);
        setStep(3);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  function goHome() {
    navigate(-1);
  }

  // ===== Обработчик: «Отправить запрос менеджеру» =====
  async function handleSendToManager() {
    if (!lastName.trim() || !firstName.trim() || !workCity.trim()) {
      alert('Заполните, пожалуйста, все поля формы.');
      return;
    }
    setContactSending(true);

    const fullName = `${lastName.trim()} ${firstName.trim()} ${middleName.trim()}`;
    const subject = `Заявка на полис "Забота о будущeм" - ${fullName}`;
    const bodyLines = [
      'Добрый день, коллега!',
      '',
      'К Вам поступила заявка на рассмотрение возможности оформления полиса "Забота о будущем"',
      '',
      `ФИО сотрудника: ${fullName}`,
      `Пол: ${gender}`,
      `Дата рождения: ${birthDate.toLocaleDateString('ru-RU', { day:'2-digit', month:'2-digit', year:'numeric' })}`,
      `Возраст: ${getAge(birthDate)}`,
      `Почта сотрудника: ${email}`,
      `Город работы: ${workCity.trim()}`,
      `Срок программы: ${termYears} лет`,
      `Размер взноса: ${amountDisplay} ₽`
    ];
    const body = bodyLines.join('\r\n');

    const payload = { subject, body };

    try {
      const res = await fetch('/api/proxy/carefuture/send_manager', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка сервера при отправке менеджеру:', res.status, await res.text());
        alert(`Ошибка при отправке запроса менеджеру: ${res.status}`);
        setContactSending(false);
      } else {
        setContactSending(false);
        setContactSent(true);
        setTimeout(() => navigate(-1), 4000);
      }
    } catch (err) {
      console.error('Ошибка при запросе менеджеру:', err);
      alert('Не удалось отправить запрос менеджеру');
      setContactSending(false);
    }
  }

  // ===== Экран «Выполняется расчёт…» (аналогично SNPPage) =====
  if (isProcessing) {
    return (
      <div className="processing-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <p className="processing-title">
          Выполняется расчёт по программе «Забота о будущeм»
        </p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // ===== Экран «Отправляется запрос менеджеру…» =====
  if (contactSending) {
    return (
      <div className="processing-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <p className="processing-title">
          Отправляются параметры расчёта менеджеру...
        </p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // ===== Экран «Успех отправки менеджеру» =====
  if (contactSent) {
    const fullName = `${lastName.trim()} ${firstName.trim()} ${middleName.trim()}`;
    return (
      <div className="processing-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <p className="processing-title">
          {fullName}, наш эксперт свяжется с Вами в ближайшее время!<br/>Отличного дня, коллега!
        </p>
      </div>
    );
  }

  // ===== Если resultData есть — показываем результат (шаг 3) или форму контакт (if contactMode) =====
  if (resultData) {
    // Распакуем поля из resultData & локальных state
    const {
      age,
      gender:   resGender,
      term:     resTerm,
      contribution,
      totalContribution,
      accrualPercent,
      accrualAmount,
      totalAccrual,
      finalAnnual,
      finalTotal
    } = resultData;

    // Форматтер «100000» → «100 000»
    function fmt(number) {
      return Number(number).toLocaleString('ru-RU', { minimumFractionDigits: 0 });
    }

    // ===== Если contactMode=true — показываем форму «Введите Ваши данные» =====
    if (contactMode) {
      return (
        <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
          <div className="polls-header">
            <button className="back-btn" onClick={goHome}>
              <svg viewBox="0 0 24 24">
                <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
              </svg>
            </button>
            <div className="logo-wrapper animate-logo">
              <img className="poll-logo" src={logoImage} alt="Логотип" />
            </div>
            <button
              className="forward-btn"
              onClick={() => setContactMode(false)}
            >
              <span className="arrow-icon">←</span>
            </button>
          </div>

          <div className="content-wrapper">
            <h2 className="contact-title">
              Введите Ваши данные:
            </h2>
            <div className="contact-form">
              <label>Фамилия:</label>
              <input
                type="text"
                placeholder="Введите фамилию"
                value={lastName}
                onChange={e => setLastName(e.target.value)}
              />

              <label>Имя:</label>
              <input
                type="text"
                placeholder="Введите имя"
                value={firstName}
                onChange={e => setFirstName(e.target.value)}
              />

              <label>Отчество:</label>
              <input
                type="text"
                placeholder="Введите отчество"
                value={middleName}
                onChange={e => setMiddleName(e.target.value)}
              />

              <label>Город работы:</label>
              <input
                type="text"
                placeholder="Введите город"
                value={workCity}
                onChange={e => setWorkCity(e.target.value)}
              />
            </div>

            <button
              className="contact-btn"
              onClick={handleSendToManager}
            >
              Отправить запрос
            </button>
          </div>
        </div>
      );
    }

    // ===== Иначе — показываем результат расчёта (шаг 3) =====
    return (
      <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
          <button
            className="repeat-btn"
            onClick={() => {
              setResultData(null);
              setGender(null);
              setCalcType(null);
              setAmountRaw('');
              setAmountDisplay('');
              setAmountError('');
              setTermYears('5');
              setStep(2);
              setContactMode(false);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="#fff"
              />
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          <h1 className="polls-title">
            Результаты расчёта по программе «Забота о будущeм»
          </h1>

          <div className="params-container">
            <h2>Параметры</h2>
            <p>Возраст: {age} лет</p>
            <p>Пол: {resGender}</p>
            <p>Срок: {resTerm} лет</p>
            <p>Периодичность оплаты: ежегодно</p>
          </div>

          {/* ==== КАРУСЕЛЬ ==== */}
          <div className="carousel-container">
            <div className="carousel-indicators">
              <span className={carouselIndex === 0 ? 'active' : ''}></span>
              <span className={carouselIndex === 1 ? 'active' : ''}></span>
              <span className={carouselIndex === 2 ? 'active' : ''}></span>
            </div>

            <div className="carousel-clipper">
              <div
                className="carousel-wrapper"
                style={{ transform: `translateX(-${carouselIndex * 100}%)` }}
              >
                {/* Страница 1: Вклад пользователя */}
                <div className="carousel-page">
                  <div className="first-line">В течение {resTerm} лет</div>
                  <div className="unit">Вы вносите средства</div>
                  <div className="value">{fmt(contribution)} ₽</div>
                  <div className="unit">Ежегодно</div>
                  <div className="value">{fmt(totalContribution)} ₽</div>
                  <div className="footer-label">Сумма ваших взносов</div>
                </div>

                {/* Страница 2: Вы накопите */}
                <div className="carousel-page">
                  <div className="first-line">Вы накопите</div>
                  <div className="unit">с учётом фиксированного дохода</div>
                  <div className="value">{fmt(accrualAmount + totalAccrual /* или тут можно вывести accumulateLabel, если нужен текст */)} ₽</div>
                  <div className="unit">Сумма вычета за весь срок</div>
                  <div className="value">{fmt(resultData.totalReduction || 0)} ₽</div>
                  <div className="footer-label"> </div>
                </div>

                {/* Страница 3: Итоговая годовая выплата */}
                <div className="carousel-page">
                  <div className="first-line">Итоговая годовая выплата</div>
                  <div className="unit">Сумма с начислениями</div>
                  <div className="value">{fmt(finalAnnual)} ₽</div>
                  <div className="unit">Сумма за {resTerm} лет</div>
                  <div className="value">{fmt(finalTotal)} ₽</div>
                  <div className="footer-label">Ваш капитал через {resTerm} лет</div>
                </div>
              </div>
            </div>

            <button
              className="carousel-control prev"
              onClick={() => setCarouselIndex(idx => idx - 1)}
              disabled={carouselIndex === 0}
            >
              ‹
            </button>
            <button
              className="carousel-control next"
              onClick={() => setCarouselIndex(idx => idx + 1)}
              disabled={carouselIndex === 2}
            >
              ›
            </button>
          </div>
          {/* ==== /КАРУСЕЛЬ ==== */}

          <button
            className="contact-btn"
            onClick={() => setContactMode(true)}
          >
            Связаться с менеджером
          </button>
        </div>
      </div>
    );
  }

  // ===== Основной UI: шаг 1 и шаг 2 =====
  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
          </svg>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <button
          className="forward-btn"
          onClick={step === 1 ? handleEmailNext : handleCalcNext}
          disabled={step === 1 ? !emailValid : !canGoNext()}
        >
          <span className="arrow-icon">→</span>
        </button>
      </div>

      <div className="content-wrapper">
        {step === 1 && (
          <>
            <div className="email-step">
              <label className="email-label">Корпоративная почта ВТБ:</label>
              <input
                type="email"
                inputMode="email"
                autoComplete="email"
                autoCapitalize="none"
                spellCheck="false"
                placeholder="name@vtb.ru"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}

            <button
              className="polls-btn btn1"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              Продолжить
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <h1 className="polls-title">Забота о будущем</h1>

            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) {
                    setBirthDate(val);
                  } else if (val?.day) {
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                  }
                }}
              />
            </div>

            <div className="section-subtitle">Срок программы (лет):</div>
            <select
              className="term-select"
              value={termYears}
              onChange={e => setTermYears(e.target.value)}
            >
              <option value="5">5 лет</option>
              <option value="10">10 лет</option>
              <option value="15">15 лет</option>
              <option value="20">20 лет</option>
            </select>

            <div className="section-subtitle">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender('мужской')}
              >
                <div>Мужской</div>
                <div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender('женский')}
              >
                <div>Женский</div>
                <div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-subtitle">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => setCalcType('premium')}
              >
                <div>Страхового</div>
                <div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => setCalcType('sum')}
              >
                <div>Страховой</div>
                <div>суммы</div>
              </button>
            </div>

            {calcType && (
              <>
                <div className="section-subtitle">Введите сумму в рублях:</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder="100.000"
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
