import React, { useState, useEffect } from 'react';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './JustincasePage.css';

const JustincasePage = () => {
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || '–ì–æ—Å—Ç—å');
  }, []);

  // –î–∞–Ω–Ω—ã–µ
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [insuranceInfo, setInsuranceInfo] = useState(null);
  const [insuranceTerm, setInsuranceTerm] = useState('1');
  const [insuranceSum, setInsuranceSum] = useState('');
  const [insuranceFrequency, setInsuranceFrequency] = useState('');

  const [accidentPackage, setAccidentPackage] = useState(null);
  const [criticalPackage, setCriticalPackage] = useState(null);
  const [treatmentRegion, setTreatmentRegion] = useState(null);
  const [sportPackage, setSportPackage] = useState(null);

  const [hasJob, setHasJob] = useState(null);
  const [income2021, setIncome2021] = useState('');
  const [income2022, setIncome2022] = useState('');
  const [income2023, setIncome2023] = useState('');
  const [scholarship, setScholarship] = useState('');
  const [unsecuredLoans, setUnsecuredLoans] = useState('');

  const [breadwinnerStatus, setBreadwinnerStatus] = useState(null);
  const [incomeShare, setIncomeShare] = useState('');
  const [childrenCount, setChildrenCount] = useState('0');
  const [specialCareRelatives, setSpecialCareRelatives] = useState(null);

  // –®–∞–≥–∏
  const [currentStep, setCurrentStep] = useState(1);
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ
  const formatSum = (rawValue) => {
    let digits = rawValue.replace(/\D/g, '');
    if (!digits) return '';
    let result = '';
    while (digits.length > 3) {
      const chunk = digits.slice(-3);
      digits = digits.slice(0, -3);
      result = '.' + chunk + result;
    }
    return digits + result;
  };

  const handleSumChange = (e) => {
    setInsuranceSum(formatSum(e.target.value));
  };

  const handleIncome2021Change = (e) => setIncome2021(formatSum(e.target.value));
  const handleIncome2022Change = (e) => setIncome2022(formatSum(e.target.value));
  const handleIncome2023Change = (e) => setIncome2023(formatSum(e.target.value));
  const handleScholarshipChange = (e) => setScholarship(formatSum(e.target.value));
  const handleUnsecuredLoansChange = (e) => setUnsecuredLoans(formatSum(e.target.value));

  // –õ–æ–≥–∏–∫–∞ –ø–µ—Ä–µ—Ö–æ–¥–æ–≤
  const canGoNext = () => {
    if (currentStep === 1) {
      return birthDate && gender && insuranceInfo;
    } else if (currentStep === 2) {
      if (insuranceInfo === 'yes') {
        return insuranceTerm && insuranceSum && insuranceFrequency;
      } else {
        return hasJob && income2021 && income2022 && income2023;
      }
    } else if (currentStep === 3) {
      if (insuranceInfo === 'yes') {
        return accidentPackage && criticalPackage && (criticalPackage === 'no' || treatmentRegion) && sportPackage;
      } else {
        return breadwinnerStatus &&
               (breadwinnerStatus === 'yes' || incomeShare) &&
               childrenCount !== '' &&
               specialCareRelatives;
      }
    } else if (currentStep === 4) {
      return accidentPackage && criticalPackage && (criticalPackage === 'no' || treatmentRegion) && sportPackage;
    }
    return true;
  };

  const handleNext = () => {
    if (!canGoNext()) {
      alert('–ù–µ –≤—ã–ø–æ–ª–Ω–µ–Ω—ã —É—Å–ª–æ–≤–∏—è –¥–ª—è –ø–µ—Ä–µ—Ö–æ–¥–∞');
      return;
    }
    if (currentStep === 1) {
      setCurrentStep(2);
    } else if (currentStep === 2) {
      setCurrentStep(3);
    } else if (currentStep === 3) {
      if (insuranceInfo === 'yes') {
        doCalculation();
      } else {
        setCurrentStep(4);
      }
    } else if (currentStep === 4) {
      doCalculation();
    }
  };

  const doCalculation = () => {
    setIsProcessing(true);
    const calculatorData = {
      birthDate,
      gender,
      insuranceTerm,
      insuranceSum,
      insuranceFrequency,
      accidentPackage,
      criticalPackage,
      treatmentRegion,
      sportPackage,
      hasJob,
      income2021,
      income2022,
      income2023,
      scholarship,
      unsecuredLoans,
      breadwinnerStatus,
      incomeShare,
      childrenCount,
      specialCareRelatives,
      insuranceInfo
    };
    console.log("–û—Ç–ø—Ä–∞–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞:", calculatorData);

    fetch('/api/proxy/calculator/save', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(calculatorData),
      credentials: 'include'
    })
      .then(response => {
        // –ï—Å–ª–∏ —Å–µ—Ä–≤–µ—Ä –≤–µ—Ä–Ω—É–ª –æ—à–∏–±–∫—É (500 –∏ —Ç.–ø.), –ø–æ–ø—Ä–æ–±—É–µ–º –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å
        if (!response.ok) {
          return response.json().then(errData => {
            throw new Error(errData.error || '–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞');
          });
        }
        return response.json();
      })
      .then(data => {
        console.log("–ü–æ–ª—É—á–µ–Ω —Ä–µ–∑—É–ª—å—Ç–∞—Ç —Ä–∞—Å—á—ë—Ç–∞:", data);
        setResultData(data);
        setIsProcessing(false);
        setCurrentStep(5);
      })
      .catch(err => {
        console.error("–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞:", err);
        setIsProcessing(false);
        alert("–û—à–∏–±–∫–∞: " + err.message);
      });
  };

  const handlePrev = () => {
    if (currentStep > 1) setCurrentStep(currentStep - 1);
  };

  const goToMenu = () => {
    window.location.href = '/menu';
  };

  const repeatCalculation = () => {
    setBirthDate(null);
    setGender(null);
    setInsuranceInfo(null);
    setInsuranceTerm('1');
    setInsuranceSum('');
    setInsuranceFrequency('');
    setAccidentPackage(null);
    setCriticalPackage(null);
    setTreatmentRegion(null);
    setSportPackage(null);
    setHasJob(null);
    setIncome2021('');
    setIncome2022('');
    setIncome2023('');
    setScholarship('');
    setUnsecuredLoans('');
    setBreadwinnerStatus(null);
    setIncomeShare('');
    setChildrenCount('0');
    setSpecialCareRelatives(null);
    setResultData(null);
    setIsProcessing(false);
    setCurrentStep(1);
  };

  const renderStep = () => {
    if (isProcessing) {
      return (
        <div className="processing-container">
          <h2>{userName}, –ø—Ä–æ–∏–∑–≤–æ–¥–∏–º —Ä–∞—Å—á–µ—Ç...</h2>
          <div className="loading-spinner">
            {/* –ú–æ–∂–Ω–æ –≤—Å—Ç–∞–≤–∏—Ç—å SVG-–∞–Ω–∏–º–∞—Ü–∏—é */}
          </div>
        </div>
      );
    }
    if (currentStep === 5 && resultData) {
      return (
        <div className="result-container">
          <h2>–í–∞—à–∞ –ø—Ä–æ–≥—Ä–∞–º–º–∞ ¬´–ù–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π¬ª (—Ä–∞—Å—á–µ—Ç –æ—Ç {resultData.calculationDate})</h2>
          <p>–í–æ–∑—Ä–∞—Å—Ç –∫–ª–∏–µ–Ω—Ç–∞: {resultData.clientAge} –ª–µ—Ç</p>
          <p>–ü–æ–ª –∫–ª–∏–µ–Ω—Ç–∞: {resultData.clientGender}</p>
          <p>–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è: {resultData.insuranceTerm} –ª–µ—Ç</p>
          <hr />
          <h3>–û—Å–Ω–æ–≤–Ω–∞—è –ø—Ä–æ–≥—Ä–∞–º–º–∞ (—Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏–µ –Ω–∞ —Å–ª—É—á–∞–π —É—Ö–æ–¥–∞ –∏–∑ –∂–∏–∑–Ω–∏ –∏ –∏–Ω–≤–∞–ª–∏–¥–Ω–æ—Å—Ç–∏ I –∏ II –≥—Ä—É–ø–ø—ã):</h3>
          <p>–°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞: {resultData.baseInsuranceSum}</p>
          <p>–°—Ç—Ä–∞—Ö–æ–≤–∞—è –ø—Ä–µ–º–∏—è: {resultData.basePremium} —Ä—É–±.</p>
          {resultData.accidentPackageIncluded && (
            <>
              <hr />
              <h3>–ü–∞–∫–µ—Ç ¬´–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π¬ª:</h3>
              <p>–°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞: {resultData.accidentInsuranceSum}</p>
              <p>–°—Ç—Ä–∞—Ö–æ–≤–∞—è –ø—Ä–µ–º–∏—è: {resultData.accidentPremium} —Ä—É–±.</p>
            </>
          )}
          {resultData.criticalPackageIncluded && (
            <>
              <hr />
              <h3>–ü–∞–∫–µ—Ç ¬´–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è¬ª:</h3>
              <p>‚Ä¢ –ú–∞–∫—Å–∏–º–∞–ª—å–Ω–∞—è —Å—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞ 60 000 000 —Ä—É–±–ª–µ–π, –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ –ø–æ —Ä–µ–∞–±–∏–ª–∏—Ç–∞—Ü–∏–∏ ‚Äì 100 000 —Ä—É–±–ª–µ–π.</p>
              <p>–°—Ç—Ä–∞—Ö–æ–≤–∞—è –ø—Ä–µ–º–∏—è: {resultData.criticalPremium} —Ä—É–±.</p>
            </>
          )}
          {resultData.sportOptionIncluded && (
            <>
              <hr />
              <h3>–û–ø—Ü–∏—è ¬´–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç¬ª:</h3>
              <p>–í–∫–ª—é—á–µ–Ω–æ</p>
            </>
          )}
          <hr />
          <p><strong>–ò—Ç–æ–≥–æ —Å—Ç—Ä–∞—Ö–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {resultData.totalPremium} —Ä—É–±.</p>
          <p><strong>–ü–æ—Ä—è–¥–æ–∫ –æ–ø–ª–∞—Ç—ã –ø—Ä–µ–º–∏–∏:</strong> {resultData.paymentMethod}</p>
          <hr />
          <p>
            –î–ª—è –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–π –∫–æ–Ω—Å—É–ª—å—Ç–∞—Ü–∏–∏ –∏ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏—è –ø—Ä–æ–≥—Ä–∞–º–º—ã —Å–≤—è–∂–∏—Ç–µ—Å—å —Å –Ω–∞—à–∏–º –º–µ–Ω–µ–¥–∂–µ—Ä–æ–º:&nbsp;
            <a href="/contact">–ù–∞—à –º–µ–Ω–µ–¥–∂–µ—Ä</a>
          </p>
        </div>
      );
    }
    // –®–∞–≥ 1
    if (currentStep === 1) {
      return (
        <>
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker onChange={(val) => setBirthDate(val)} />
          </div>
          <div className="form-group">
            <label className="form-label">–ü–æ–ª:</label>
            <div className="gender-selector">
              <div
                className={`gender-option ${gender === 'male' ? 'selected' : ''}`}
                onClick={() => setGender('male')}
              >
                <div className="gender-label">–ú—É–∂—Å–∫–æ–π</div>
                <div className="gender-icon">üë®</div>
              </div>
              <div
                className={`gender-option ${gender === 'female' ? 'selected' : ''}`}
                onClick={() => setGender('female')}
              >
                <div className="gender-label">–ñ–µ–Ω—Å–∫–∏–π</div>
                <div className="gender-icon">üë©</div>
              </div>
            </div>
          </div>
          <div className="form-group">
            <label className="form-label">–ó–Ω–∞–µ—Ç–µ –ª–∏ –í—ã –Ω–∞ –∫–∞–∫–æ–π —Å—Ä–æ–∫ –∏ –Ω–∞ –∫–∞–∫—É—é —Å—É–º–º—É –æ—Ñ–æ—Ä–º–∏—Ç—å —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø–æ–ª–∏—Å?</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${insuranceInfo === 'yes' ? 'selected' : ''}`}
                onClick={() => setInsuranceInfo('yes')}
              >
                –î–∞
              </button>
              <button
                className={`insurance-btn ${insuranceInfo === 'no' ? 'selected' : ''}`}
                onClick={() => setInsuranceInfo('no')}
              >
                –ù–µ—Ç
              </button>
            </div>
          </div>
        </>
      );
    }
    // –®–∞–≥ 2
    if (currentStep === 2) {
      if (insuranceInfo === 'yes') {
        return (
          <>
            <div className="form-group">
              <label className="form-label">–í—ã–±–µ—Ä–∏—Ç–µ —Å—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–ª–µ—Ç):</label>
              <select
                className="insurance-term-select"
                value={insuranceTerm}
                onChange={(e) => setInsuranceTerm(e.target.value)}
              >
                {Array.from({ length: 30 }, (_, i) => i + 1).map(year => (
                  <option key={year} value={year}>{year}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—Ç—Ä–∞—Ö–æ–≤—É—é —Å—É–º–º—É (–æ—Ç 1 –º–ª–Ω —Ä—É–±):</label>
              <input
                type="text"
                className="insurance-sum-input"
                placeholder="1.000.000"
                value={insuranceSum}
                onChange={handleSumChange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">–ü–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø—Ä–µ–º–∏–∏:</label>
              <select
                className="insurance-frequency-select"
                value={insuranceFrequency}
                onChange={(e) => setInsuranceFrequency(e.target.value)}
              >
                <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –ø–µ—Ä–∏–æ–¥</option>
                {["–ï–∂–µ–º–µ—Å—è—á–Ω–æ", "–ï–∂–µ–≥–æ–¥–Ω–æ", "—Ä–∞–∑ –≤ –ø–æ–ª –≥–æ–¥–∞", "–µ–∂–µ–∫–≤–∞—Ä—Ç–∞–ª—å–Ω–æ"].map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>
          </>
        );
      } else {
        return (
          <>
            <div className="form-group">
              <label className="form-label">–ï—Å—Ç—å —Ä–∞–±–æ—Ç–∞?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${hasJob === 'yes' ? 'selected' : ''}`}
                  onClick={() => setHasJob('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${hasJob === 'no' ? 'selected' : ''}`}
                  onClick={() => setHasJob('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">–î–æ—Ö–æ–¥ 2021, —Ä—É–±.:</label>
              <input
                type="text"
                className="inline-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É"
                value={income2021}
                onChange={handleIncome2021Change}
              />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">–î–æ—Ö–æ–¥ 2022, —Ä—É–±.:</label>
              <input
                type="text"
                className="inline-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É"
                value={income2022}
                onChange={handleIncome2022Change}
              />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">–î–æ—Ö–æ–¥ 2023, —Ä—É–±.:</label>
              <input
                type="text"
                className="inline-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É"
                value={income2023}
                onChange={handleIncome2023Change}
              />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">–°—Ç–∏–ø–µ–Ω–¥–∏—è 2023, —Ä—É–±.:</label>
              <input
                type="text"
                className="inline-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É"
                value={scholarship}
                onChange={handleScholarshipChange}
              />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">–ö—Ä–µ–¥–∏—Ç—ã, —Ä—É–±.:</label>
              <input
                type="text"
                className="inline-input"
                placeholder="–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É"
                value={unsecuredLoans}
                onChange={handleUnsecuredLoansChange}
              />
            </div>
          </>
        );
      }
    }
    // –®–∞–≥ 3
    if (currentStep === 3) {
      if (insuranceInfo === 'yes') {
        return (
          <>
            <div className="form-group">
              <label className="form-label">–ü–æ–¥–∫–ª—é—á–∞–µ–º –ø–∞–∫–µ—Ç ¬´–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π¬ª?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${accidentPackage === 'yes' ? 'selected' : ''}`}
                  onClick={() => setAccidentPackage('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${accidentPackage === 'no' ? 'selected' : ''}`}
                  onClick={() => setAccidentPackage('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">–ü–æ–¥–∫–ª—é—á–∞–µ–º –ø–∞–∫–µ—Ç ¬´–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è¬ª?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${criticalPackage === 'yes' ? 'selected' : ''}`}
                  onClick={() => setCriticalPackage('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${criticalPackage === 'no' ? 'selected' : ''}`}
                  onClick={() => setCriticalPackage('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
            {criticalPackage === 'yes' && (
              <div className="form-group">
                <label className="form-label">–ö–∞–∫–æ–π —Ä–µ–≥–∏–æ–Ω –ª–µ—á–µ–Ω–∏—è –ø–æ–¥–∫–ª—é—á–∞–µ–º?</label>
                <div className="insurance-options">
                  <button
                    className={`insurance-btn ${treatmentRegion === 'russia' ? 'selected' : ''}`}
                    onClick={() => setTreatmentRegion('russia')}
                  >
                    –õ–µ—á–µ–Ω–∏–µ –≤ –†–§
                  </button>
                  <button
                    className={`insurance-btn ${treatmentRegion === 'abroad' ? 'selected' : ''}`}
                    onClick={() => setTreatmentRegion('abroad')}
                  >
                    –õ–µ—á–µ–Ω–∏–µ –∑–∞ —Ä—É–±–µ–∂–æ–º
                  </button>
                </div>
              </div>
            )}
            <div className="form-group">
              <label className="form-label">–ü–æ–¥–∫–ª—é—á–∞–µ–º –æ–ø—Ü–∏—é ¬´–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç¬ª?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${sportPackage === 'yes' ? 'selected' : ''}`}
                  onClick={() => setSportPackage('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${sportPackage === 'no' ? 'selected' : ''}`}
                  onClick={() => setSportPackage('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
          </>
        );
      } else {
        return (
          <>
            <div className="form-group">
              <label className="form-label">–í—ã –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π –∫–æ—Ä–º–∏–ª–µ—Ü –≤ —Å–µ–º—å–µ?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${breadwinnerStatus === 'yes' ? 'selected' : ''}`}
                  onClick={() => setBreadwinnerStatus('yes')}
                >
                  –î–∞
                </button>
                <button
