                  <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –¥–æ–ª—é</option>
                  <option value="–¥–æ 10%">–¥–æ 10%</option>
                  <option value="10-24%">10‚Äì24%</option>
                  <option value="25-49%">25‚Äì49%</option>
                  <option value="50-74%">50‚Äì74%</option>
                  <option value="75-89%">75‚Äì89%</option>
                  <option value="–ë–æ–ª–µ–µ 90%">–ë–æ–ª–µ–µ 90%</option>
                </select>
              </div>
            )}
            <div className="form-group">
              <label className="form-label">–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–µ—Ç–µ–π –Ω–∞ –∏–∂–¥–∏–≤–µ–Ω–∏–∏?</label>
              <select
                className="insurance-term-select"
                value={childrenCount}
                onChange={(e) => setChildrenCount(e.target.value)}
              >
                <option value="0">0</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3 –∏ –±–æ–ª–µ–µ">3 –∏ –±–æ–ª–µ–µ</option>
              </select>
            </div>
            <div className="form-group">
              <label className="form-label">–ï—Å—Ç—å –ª–∏ —Ä–æ–¥—Å—Ç–≤–µ–Ω–Ω–∏–∫–∏, —Ç—Ä–µ–±—É—é—â–∏–µ –æ—Å–æ–±–æ–≥–æ —É—Ö–æ–¥–∞?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${specialCareRelatives === 'yes' ? 'selected' : ''}`}
                  onClick={() => setSpecialCareRelatives('yes')}
                >
                  –î–∞
                </button>
                <button
                  className={`insurance-btn ${specialCareRelatives === 'no' ? 'selected' : ''}`}
                  onClick={() => setSpecialCareRelatives('no')}
                >
                  –ù–µ—Ç
                </button>
              </div>
            </div>
          </>
        );
      }
    }
    // –®–∞–≥ 4 (—Ç–æ–ª—å–∫–æ –¥–ª—è "no")
    if (currentStep === 4) {
      return (
        <>
          <div className="form-group">
            <label className="form-label">–ü–æ–¥–∫–ª—é—á–∞–µ–º –ø–∞–∫–µ—Ç ¬´–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π¬ª?</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${accidentPackage === 'yes' ? 'selected' : ''}`}
                onClick={() => setAccidentPackage('yes')}
              >
                –î–∞
              </button>
              <button
                className={`insurance-btn ${accidentPackage === 'no' ? 'selected' : ''}`}
                onClick={() => setAccidentPackage('no')}
              >
                –ù–µ—Ç
              </button>
            </div>
          </div>
          <div className="form-group">
            <label className="form-label">–ü–æ–¥–∫–ª—é—á–∞–µ–º –ø–∞–∫–µ—Ç ¬´–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è¬ª?</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${criticalPackage === 'yes' ? 'selected' : ''}`}
                onClick={() => setCriticalPackage('yes')}
              >
                –î–∞
              </button>
              <button
                className={`insurance-btn ${criticalPackage === 'no' ? 'selected' : ''}`}
                onClick={() => setCriticalPackage('no')}
              >
                –ù–µ—Ç
              </button>
            </div>
          </div>
          {criticalPackage === 'yes' && (
            <div className="form-group">
              <label className="form-label">–ö–∞–∫–æ–π —Ä–µ–≥–∏–æ–Ω –ª–µ—á–µ–Ω–∏—è –ø–æ–¥–∫–ª—é—á–∞–µ–º?</label>
              <div className="insurance-options">
                <button
                  className={`insurance-btn ${treatmentRegion === 'russia' ? 'selected' : ''}`}
                  onClick={() => setTreatmentRegion('russia')}
                >
                  –õ–µ—á–µ–Ω–∏–µ –≤ –†–§
                </button>
                <button
                  className={`insurance-btn ${treatmentRegion === 'abroad' ? 'selected' : ''}`}
                  onClick={() => setTreatmentRegion('abroad')}
                >
                  –õ–µ—á–µ–Ω–∏–µ –∑–∞ —Ä—É–±–µ–∂–æ–º
                </button>
              </div>
            </div>
          )}
          <div className="form-group">
            <label className="form-label">–ü–æ–¥–∫–ª—é—á–∞–µ–º –æ–ø—Ü–∏—é ¬´–õ—é–±–∏—Ç–µ–ª—å—Å–∫–∏–π —Å–ø–æ—Ä—Ç¬ª?</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${sportPackage === 'yes' ? 'selected' : ''}`}
                onClick={() => setSportPackage('yes')}
              >
                –î–∞
              </button>
              <button
                className={`insurance-btn ${sportPackage === 'no' ? 'selected' : ''}`}
                onClick={() => setSportPackage('no')}
              >
                –ù–µ—Ç
              </button>
            </div>
          </div>
        </>
      );
    }
    return null;
  };

  return (
    <div className="justincase-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {currentStep !== 5 ? (
        <div className="logo-container">
          <img src={logoImage} alt="Logo" className="logo-image" />
        </div>
      ) : (
        <div className="logo-container logo-container-final">
          <img src={logoImage} alt="Logo" className="logo-image" />
        </div>
      )}
      {currentStep !== 5 && (
        <h1 className="justincase-title">
          –†–∞—Å—á–µ—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–ù–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π" –¥–ª—è —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞
        </h1>
      )}
      <div className="justincase-form">{renderStep()}</div>

      {currentStep === 1 && (
        <div className="back-btn-container">
          <button className="back-btn mainmenu-btn" onClick={goToMenu}>
            <img src={mainmenuIcon} alt="Menu" style={{ width: '24px', height: '24px' }} />
          </button>
        </div>
      )}
      {currentStep > 1 && currentStep < 5 && (
        <div className="back-btn-container">
          <button className="back-btn" onClick={handlePrev}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
              <path d="M12 20l-8-8 8-8" />
            </svg>
          </button>
        </div>
      )}
      {currentStep < 5 && (
        <div className="top-btn-container">
          <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
              <path d="M12 4l8 8-8 8" />
            </svg>
          </button>
        </div>
      )}
      {currentStep === 5 && (
        <>
          <div className="back-btn-container">
            <button className="back-btn mainmenu-btn" onClick={goToMenu}>
              <img src={mainmenuIcon} alt="Menu" style={{ width: '24px', height: '24px' }} />
            </button>
          </div>
          <div className="top-btn-container">
            <button className="top-btn" onClick={repeatCalculation}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                <path d="M17.65 6.35A7.95 7.95 0 0012 4a8 8 0 00-8 8h2.08a5.92 5.92 0 015.92-6 6 6 0 015.65 3.65L13 11h7V4l-2.35 2.35zM6.35 17.65A7.95 7.95 0 0012 20a8 8 0 008-8h-2.08a5.92 5.92 0 01-5.92 6 6 6 0 01-5.65-3.65L3.41 16.3 2 17.71l2.35-2.35z" />
              </svg>
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default JustincasePage;
