import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function CareFuturePage() {
  const navigate = useNavigate();

  // –®–∞–≥–∏: email ‚Üí —Ñ–æ—Ä–º–∞ ‚Üí processing ‚Üí —Ä–µ–∑—É–ª—å—Ç–∞—Ç ‚Üí –º–µ–Ω–µ–¥–∂–µ—Ä
  const [step,          setStep]          = useState('email');
  const [email,         setEmail]         = useState('');
  const [emailError,    setEmailError]    = useState('');
  const [birthDateParts,setBirthDateParts]= useState({ day: null, month: null, year: null });
  const [birthDate,     setBirthDate]     = useState(null);
  const [gender,        setGender]        = useState(null);
  const [calcType,      setCalcType]      = useState(null);
  const [amount,        setAmount]        = useState('');
  const [amountError,   setAmountError]   = useState('');
  const [isProcessing,  setIsProcessing]  = useState(false);
  const [resultData,    setResultData]    = useState(null);
  const [slideIndex,    setSlideIndex]    = useState(0);
  const [mgrSurname,    setMgrSurname]    = useState('');
  const [mgrName,       setMgrName]       = useState('');
  const [mgrCity,       setMgrCity]       = useState('');
  const [mgrError,      setMgrError]      = useState('');

  const term        = 5;
  const periodicity = '–µ–∂–µ–≥–æ–¥–Ω–æ';

  // –°–æ–±–∏—Ä–∞–µ–º birthDate –∏–∑ day/month/year
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m-1, d);
      if (!isNaN(dt) &&
          dt.getDate()     === d &&
          dt.getMonth()+1  === m &&
          dt.getFullYear() === y) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g,''),10) || 0;
    if (calcType==='premium') {
      setAmountError(num < 60000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60 000 ‚ÇΩ' : '');
    } else if (calcType==='sum') {
      setAmountError(num < 450000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450 000 ‚ÇΩ' : '');
    }
  };

  const getAge = d => {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    const m = today.getMonth() - d.getMonth();
    if (m<0 || (m===0 && today.getDate()<d.getDate())) age--;
    return age;
  };

  const canGoNext = () =>
    birthDate instanceof Date &&
    !isNaN(birthDate) &&
    gender && calcType &&
    amount && !amountError &&
    getAge(birthDate) >= 18;

  // Email ‚Üí —Ä–∞—Å—á—ë—Ç
  const handleEmailNext = () => {
    if (!email.includes('@vtb.ru')) {
      setEmailError('–î–æ–º–µ–Ω –≤–∞—à–µ–π –ø–æ—á—Ç—ã –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—ã–º. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑.');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  // –ó–∞–ø—É—Å–∫ —Ä–∞—Å—á—ë—Ç–∞
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'});
    const payload = {
      birthdate: dateRu,
      age:        getAge(birthDate),
      gender,
      payment:    calcType==='premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum:        parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res  = await fetch('/api/snp/save',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      }
    } catch(err) {
      console.error(err);
    }
    setIsProcessing(false);
  };

  // –ü–æ–¥—Ä–æ–±–Ω—ã–µ —Å–ª–∞–π–¥—ã
  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">–í –¢–ï–ß–ï–ù–ò–ï {term} –õ–ï–¢</div>
      <div className="slide-title">–í—ã –≤–Ω–æ—Å–∏—Ç–µ —Å—Ä–µ–¥—Å—Ç–≤–∞</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ‚ÇΩ</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">–ù–ê–ß–ò–°–õ–Ø–ï–ú –î–û–ü–û–õ–ù–ò–¢–ï–õ–¨–ù–û</div>
      <div className="slide-title">{`${Math.round(resultData.accrualPercent*100)}% –æ—Ç –∫–∞–∂–¥–æ–≥–æ –≤–∑–Ω–æ—Å–∞`}</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–û–±—â–∞—è —Å—É–º–º–∞ –Ω–∞—á–∏—Å–ª–µ–Ω–∏–π</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ‚ÇΩ</div>
    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">–í–´ –ü–û–õ–£–ß–ê–ï–¢–ï:</div>
      <div className="slide-icon">üí∞</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ‚ÇΩ</div>
      <div className="slide-period">–ï–∂–µ–≥–æ–¥–Ω—ã–µ –Ω–∞–∫–æ–ø–ª–µ–Ω–∏—è</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ‚ÇΩ</div>
      <div className="slide-subtitle">–í–∞—à –∫–∞–ø–∏—Ç–∞–ª —á–µ—Ä–µ–∑ {term} –ª–µ—Ç</div>
    </div>
  ] : [];

  // –ü–µ—Ä–µ—Ö–æ–¥ –∫ —Ñ–æ—Ä–º–µ –º–µ–Ω–µ–¥–∂–µ—Ä–∞
  const handleToManager = () => setStep('manager');

  // –û—Ç–ø—Ä–∞–≤–∫–∞ –º–µ–Ω–µ–¥–∂–µ—Ä—É
  const handleManagerSubmit = async () => {
    if (!mgrSurname||!mgrName||!mgrCity) {
      setMgrError('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è');
      return;
    }
    const payload = {
      surname: mgrSurname.trim(),
      name:    mgrName.trim(),
      city:    mgrCity.trim(),
      email,
      gender,
      birthdate: birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'}),
      age:        getAge(birthDate),
      contribution: resultData.contribution
    };
    try {
      const res = await fetch('/api/proxy/snp/send_manager',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(payload)
      });
      if (res.ok) navigate('/main-menu');
      else       setMgrError('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏');
    } catch {
      setMgrError('–°–µ—Ç–µ–≤–∞—è –æ—à–∏–±–∫–∞');
    }
  };

  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>

      {/* –î–æ–º–æ–π */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/main-menu')}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π"/>
        </button>
      </div>

      {/* –õ–æ–≥–æ—Ç–∏–ø */}
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="snp-title">–ó–∞–±–æ—Ç–∞ –æ –±—É–¥—É—â–µ–º</h1>

      {/* EMAIL */}
      {step==='email' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input
              type="email"
              className="insurance-sum-input"
              value={email}
              onChange={e=>setEmail(e.target.value)}
              placeholder="name@vtb.ru"
            />
          </div>
          <button className="insurance-btn selected" onClick={handleEmailNext}>
            –ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–∞—Å—á—ë—Ç—É
          </button>
          {emailError && <div className="input-error-message">{emailError}</div>}
        </div>
      )}

      {/* –§–û–†–ú–ê —Ä–∞—Å—á—ë—Ç–∞ */}
      {step==='form' && !isProcessing && !resultData && (
        <div className="snp-form">
          {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDateParts}
              onChange={val=>{
                // —Å–∫–æ–ø–∏—Ä–æ–≤–∞—Ç—å –ª–æ–≥–∏–∫—É –∏–∑ SNPPage.js
                if(val instanceof Date){
                  setBirthDateParts({
                    day:   String(val.getDate()).padStart(2,'0'),
                    month: String(val.getMonth()+1).padStart(2,'0'),
                    year:  String(val.getFullYear())
                  });
                } else if(typeof val==='object'){
                  setBirthDateParts(prev=>({
                    ...prev,
                    ...(val.day   != null ? { day:   String(val.day).padStart(2,'0') } : {}),
                    ...(val.month != null ? { month: String(val.month).padStart(2,'0') } : {}),
                    ...(val.year  != null ? { year:  String(val.year)                } : {})
                  }));
                } else if(typeof val==='string'){
                  const parts = val.includes('.') ? val.split('.') : val.includes('-') ? val.split('-').reverse() : [];
                  if(parts.length===3){
                    setBirthDateParts({
                      day:   parts[0].padStart(2,'0'),
                      month: parts[1].padStart(2,'0'),
                      year:  parts[2]
                    });
                  }
                }
              }}
            />
          </div>

          {/* –ü–æ–ª */}
          <div className="form-group">
            <label className="form-label">–ü–æ–ª:</label>
            <div className="gender-selector">
              {['–º—É–∂—Å–∫–æ–π','–∂–µ–Ω—Å–∫–∏–π'].map(v=>(
                <div
                  key={v}
                  className={`gender-option ${gender===v?'selected':''}`}
                  onClick={()=>setGender(v)}
                >
                  <div className="gender-label">{v==='–º—É–∂—Å–∫–æ–π'?'–ú—É–∂—Å–∫–æ–π':'–ñ–µ–Ω—Å–∫–∏–π'}</div>
                  <div className="gender-icon">{v==='–º—É–∂—Å–∫–æ–π'?'üë®':'üë©'}</div>
                </div>
