              <button
                className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                onClick={()=>{ setCalcType('sum');     setAmount(''); setAmountError(''); }}
              >–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã</button>
            </div>
          </div>

          {/* –°—É–º–º–∞ */}
          {calcType && (
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö</label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType==='premium'? '–ú–∏–Ω–∏–º—É–º 60 000' : '–ú–∏–Ω–∏–º—É–º 450 000'}
              />
              {amountError && <div className="input-error-message">{amountError}</div>}
            </div>
          )}

          {/* –î–∞–ª–µ–µ */}
          <button
            className="top-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
          </button>
        </div>
      )}

      {/* Processing */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}

      {/* –†–µ–∑—É–ª—å—Ç–∞—Ç */}
      {step==='result' && resultData && !isProcessing && (
        <>
          <div className="result-container">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
            <p><strong>–í–æ–∑—Ä–∞—Å—Ç:</strong> {resultData.age} –ª–µ—Ç</p>
            <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
            <p><strong>–¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞:</strong> {resultData.payment}</p>
            <p><strong>–°—É–º–º–∞:</strong> {formatSum(String(resultData.sum))} ‚ÇΩ</p>
            <p><strong>–ò—Ç–æ–≥–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {formatSum(String(resultData.totalPremium||0))} ‚ÇΩ</p>
          </div>

          <div className="detail-slider-container">
            {slides[slideIndex]}
            <div className="detail-nav">
              {slideIndex>0 && (
                <button onClick={()=>setSlideIndex(i=>i-1)} className="nav-btn left">‚Üê</button>
              )}
              {slideIndex<slides.length-1 && (
                <button onClick={()=>setSlideIndex(i=>i+1)} className="nav-btn right">‚Üí</button>
              )}
            </div>
          </div>

          <button className="insurance-btn selected" onClick={handleToManager}>
            –°–≤—è–∑–∞—Ç—å—Å—è —Å –º–µ–Ω–µ–¥–∂–µ—Ä–æ–º
          </button>
        </>
      )}

      {/* –§–æ—Ä–º–∞ –º–µ–Ω–µ–¥–∂–µ—Ä–∞ */}
      {step==='manager' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–§–∞–º–∏–ª–∏—è:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrSurname}
              onChange={e=>setMgrSurname(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">–ò–º—è:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrName}
              onChange={e=>setMgrName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">–ì–æ—Ä–æ–¥ —Ä–∞–±–æ—Ç—ã:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrCity}
              onChange={e=>setMgrCity(e.target.value)}
            />
          </div>
          {mgrError && <div className="input-error-message">{mgrError}</div>}
          <button className="top-btn" onClick={handleManagerSubmit}>
            –û—Ç–ø—Ä–∞–≤–∏—Ç—å –º–µ–Ω–µ–¥–∂–µ—Ä—É
          </button>
        </div>
      )}

    </div>
  );
}
