import React, { useState, useEffect } from 'react';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './JustincasePage.css';

const JustincasePage = () => {
  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –≤–æ–ø—Ä–æ—Å–æ–≤
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null); // 'male' –∏–ª–∏ 'female'
  const [insuranceInfo, setInsuranceInfo] = useState(null); // 'yes' –∏–ª–∏ 'no'

  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è "–î–∞"‚Äë–±–ª–æ–∫–∞
  const [insuranceTerm, setInsuranceTerm] = useState('1'); // —Å—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è
  const [insuranceSum, setInsuranceSum] = useState('');    // —Å—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞ (—Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–Ω–∞—è)

  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –∏–º–µ–Ω–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è (–ø—Ä–∏–º–µ—Ä)
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || '–ì–æ—Å—Ç—å');
  }, []);

  // ======== –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –≤—ã–±–æ—Ä–∞ ========

  const handleGenderSelect = (value) => {
    setGender(value);
  };

  const handleInsuranceSelect = (value) => {
    setInsuranceInfo(value);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Å—Ä–æ–∫–∞ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–ø—Ä–æ—Å—Ç–æ–π select)
  const handleTermChange = (e) => {
    setInsuranceTerm(e.target.value);
  };

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã —Å —Ç–æ—á–∫–∞–º–∏ –∫–∞–∂–¥—ã–µ 3 —Å–∏–º–≤–æ–ª–∞ —Å–ø—Ä–∞–≤–∞
  const formatSum = (rawValue) => {
    // –£–¥–∞–ª—è–µ–º –≤—Å–µ –Ω–µ-—Ü–∏—Ñ—Ä—ã
    let digits = rawValue.replace(/\D/g, '');
    if (!digits) return '';
    let result = '';
    // –†–∞–∑–±–∏–≤–∞–µ–º –Ω–∞ –±–ª–æ–∫–∏ –ø–æ 3 —Å–∏–º–≤–æ–ª–∞ —Å –∫–æ–Ω—Ü–∞
    while (digits.length > 3) {
      const chunk = digits.slice(-3);
      digits = digits.slice(0, -3);
      result = '.' + chunk + result;
    }
    // –î–æ–±–∞–≤–ª—è–µ–º –æ—Å—Ç–∞–≤—à–∏–µ—Å—è —Å–∏–º–≤–æ–ª—ã
    result = digits + result;
    return result;
  };

  const handleSumChange = (e) => {
    const formatted = formatSum(e.target.value);
    setInsuranceSum(formatted);
  };

  // ======== –ü—Ä–æ–≤–µ—Ä–∫–∏ –≤–æ–∑—Ä–∞—Å—Ç–∞ –∏ —Å—É–º–º—ã ========

  const parseDate = (dateStr) => {
    const [day, month, year] = dateStr.split('/');
    return new Date(+year, +month - 1, +day);
  };

  const calculateAge = (birthDateObj) => {
    const today = new Date();
    let age = today.getFullYear() - birthDateObj.getFullYear();
    const m = today.getMonth() - birthDateObj.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDateObj.getDate())) {
      age--;
    }
    return age;
  };

  const handleSubmit = () => {
    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–∞—Ç—ã —Ä–æ–∂–¥–µ–Ω–∏—è
    if (!birthDate) {
      alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è');
      return;
    }
    const bd = parseDate(birthDate);
    const age = calculateAge(bd);
    if (age < 18 || age > 70) {
      alert('–í–æ–∑—Ä–∞—Å—Ç –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 18 –¥–æ 70 –ª–µ—Ç');
      return;
    }

    // –ï—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –≤—ã–±—Ä–∞–ª "–î–∞", –ø—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ä–æ–∫ –∏ —Å—É–º–º—É
    if (insuranceInfo === 'yes') {
      // –ü—Ä–æ–≤–µ—Ä–∫–∞ —Å—Ä–æ–∫–∞ (–¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 1 –¥–æ 30)
      const termNum = Number(insuranceTerm);
      if (termNum < 1 || termNum > 30) {
        alert('–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 1 –¥–æ 30 –ª–µ—Ç');
        return;
      }
      // –ü—Ä–æ–≤–µ—Ä–∫–∞ —Å—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
      const rawDigits = insuranceSum.replace(/\D/g, '');
      if (!rawDigits) {
        alert('–í–≤–µ–¥–∏—Ç–µ —Å—Ç—Ä–∞—Ö–æ–≤—É—é —Å—É–º–º—É');
        return;
      }
      const sumNumber = Number(rawDigits);
      if (sumNumber < 1000000) {
        alert('–°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞ –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—Ç 1 000 000 —Ä—É–±–ª–µ–π');
        return;
      }
    }

    // –ï—Å–ª–∏ –≤—Å—ë –æ–∫, –≤—ã–≤–æ–¥–∏–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç (–∏–ª–∏ —É—Ö–æ–¥–∏–º –Ω–∞ —Å–ª–µ–¥—É—é—â–∏–π —à–∞–≥)
    alert(`
–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è: ${birthDate}
–ü–æ–ª: ${gender || '–Ω–µ –≤—ã–±—Ä–∞–Ω'}
–ó–Ω–∞–µ—Ç —Å—Ä–æ–∫/—Å—É–º–º—É: ${insuranceInfo}
–°—Ä–æ–∫ (–ª–µ—Ç): ${insuranceTerm}
–°—É–º–º–∞: ${insuranceSum} —Ä—É–±.
    `);
  };

  return (
    <div className="justincase-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <h1 className="justincase-title">
        –†–∞—Å—á–µ—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–ù–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π" –¥–ª—è —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞
      </h1>

      <div className="justincase-form">
        {/* –í–æ–ø—Ä–æ—Å 1: –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
        <div className="form-group">
          <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
          <DateWheelPicker onChange={(dateString) => setBirthDate(dateString)} />
        </div>

        {/* –í–æ–ø—Ä–æ—Å 2: –ü–æ–ª */}
        <div className="form-group">
          <label className="form-label">–ü–æ–ª:</label>
          <div className="gender-selector">
            <div
              className={`gender-option ${gender === 'male' ? 'selected' : ''}`}
              onClick={() => handleGenderSelect('male')}
            >
              <div className="gender-label">–ú—É–∂—Å–∫–æ–π</div>
              <div className="gender-icon">üë®</div>
            </div>
            <div
              className={`gender-option ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => handleGenderSelect('female')}
            >
              <div className="gender-label">–ñ–µ–Ω—Å–∫–∏–π</div>
              <div className="gender-icon">üë©</div>
            </div>
          </div>
        </div>

        {/* –í–æ–ø—Ä–æ—Å 3: –ó–Ω–∞–µ—Ç–µ –ª–∏ –í—ã... */}
        <div className="form-group">
          <label className="form-label">
            –ó–Ω–∞–µ—Ç–µ –ª–∏ –í—ã –Ω–∞ –∫–∞–∫–æ–π —Å—Ä–æ–∫ –∏ –Ω–∞ –∫–∞–∫—É—é —Å—É–º–º—É –æ—Ñ–æ—Ä–º–∏—Ç—å —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø–æ–ª–∏—Å?
          </label>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${insuranceInfo === 'yes' ? 'selected' : ''}`}
              onClick={() => handleInsuranceSelect('yes')}
            >
              –î–∞
            </button>
            <button
              className={`insurance-btn ${insuranceInfo === 'no' ? 'selected' : ''}`}
              onClick={() => handleInsuranceSelect('no')}
            >
              –ù–µ—Ç
            </button>
          </div>
        </div>

        {/* –î–æ–ø. –≤–æ–ø—Ä–æ—Å—ã, –µ—Å–ª–∏ "–î–∞" */}
        {insuranceInfo === 'yes' && (
          <>
            {/* –°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è */}
            <div className="form-group">
              <label className="form-label">–í—ã–±–µ—Ä–∏—Ç–µ —Å—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–ª–µ—Ç):</label>
              <select className="insurance-term-select" value={insuranceTerm} onChange={handleTermChange}>
                {Array.from({ length: 30 }, (_, i) => i + 1).map((year) => (
                  <option key={year} value={year}>{year}</option>
                ))}
              </select>
            </div>

            {/* –°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞ */}
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—Ç—Ä–∞—Ö–æ–≤—É—é —Å—É–º–º—É (–æ—Ç 1 –º–ª–Ω —Ä—É–±):</label>
              <input
                type="text"
                className="insurance-sum-input"
                placeholder="1.000.000"
                value={insuranceSum}
                onChange={handleSumChange}
              />
            </div>
          </>
        )}
      </div>

      {/* –ö–Ω–æ–ø–∫–∞ "–î–∞–ª–µ–µ" (—Å—Ç—Ä–µ–ª–∫–∞) */}
      <div className="top-btn-container">
        <button className="top-btn" onClick={handleSubmit}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
            <path d="M12 4l8 8-8 8" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default JustincasePage;
