import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import backgroundImage from './components/background.png'; // Фоновое изображение
import happyGif from './components/animations/laughing_2.png';
import loyalGif from './components/animations/smiling.png';
import sadGif from './components/animations/pokerface.png';
import { useSwipeable } from 'react-swipeable';

import './FeedbackPage.css';

// Импорт данных партнёров и спикеров
import partners from './components/autosuggest/partners.json';
import speakersData from './components/autosuggest/speakers.json';

const FeedbackPage = () => {
  // 1) Состояние пользователя (берём из localStorage)
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || 'Гость');
  }, []);

  // 2) Шаги опроса
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);

  // 3) Данные опроса
  // Шаг 1: партнёр
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);

  // Шаг 2 (спикеры) + Шаг 3 (оценка качеств)
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [speakersSuggestions, setSpeakersSuggestions] = useState([]);
  const [speakerQualities, setSpeakerQualities] = useState([]);
  const [currentSpeakerIndex, setCurrentSpeakerIndex] = useState(0);

  // Шаг 4
  const [usefulness, setUsefulness] = useState('');

  // Шаг 5
  const [brightThoughts, setBrightThoughts] = useState('');

  // Шаг 6
  const additionalOptions = [
    'Практики',
    'Клиентских историй',
    'Статистических данных',
    'Фишек продаж',
    "Перерывов / 'Кофе'-пауз",
    'Разбор продуктовой линейки',
    'Детальный разбор параметров продукта',
  ];
  const [additionalSelections, setAdditionalSelections] = useState([]);

  // Шаг 7
  const [impression, setImpression] = useState('');

  // Шаг 8
  const [mood, setMood] = useState('');

  // Шаг 9
  const [recommendation, setRecommendation] = useState(null);

  // useEffect для массива спикеров
  useEffect(() => {
    setSpeakerQualities(speakersList.map(() => Array(5).fill(null)));
  }, [speakersList]);

  // ---------- Swipe ----------
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => {
      // свайп влево = назад
      if (currentStep < 9) handlePrev();
    },
    onSwipedRight: () => {
      // свайп вправо = вперёд
      if (currentStep < 9 && canGoNext()) handleNext();
    },
    preventDefault: true,
  });

  // ---------- Логика шагов ----------
  const qualityRows = [
    { positive: 'Экспертный', negative: 'Некомпетентный' },
    { positive: 'Энергичный', negative: 'Пассивный' },
    { positive: 'Мотивирующий', negative: 'Невдохновляющий' },
    { positive: 'Харизматичный', negative: 'Душный' },
    { positive: 'Доступно доносит информацию', negative: 'Говорит сложными терминами' },
  ];

  const handleQualitySelectForSpeaker = (rowIndex, type) => {
    const updated = [...speakerQualities];
    const selectedText = qualityRows[rowIndex][type];
    updated[currentSpeakerIndex][rowIndex] =
      updated[currentSpeakerIndex][rowIndex] === selectedText ? null : selectedText;
    setSpeakerQualities(updated);
  };

  const countSelectedQualities = () => {
    if (!speakerQualities[currentSpeakerIndex]) return 0;
    return speakerQualities[currentSpeakerIndex].filter(q => q !== null).length;
  };

  const toggleAdditionalSelection = (option) => {
    if (additionalSelections.includes(option)) {
      setAdditionalSelections(additionalSelections.filter(item => item !== option));
    } else {
      setAdditionalSelections([...additionalSelections, option]);
    }
  };

  // Проверка, можем ли идти дальше
  const canGoNext = () => {
    switch (currentStep) {
      case 1:
        return partner.trim() !== '' && partners.includes(partner);
      case 2:
        return speakersList.every(
          s => s.fullName.trim() !== '' && speakersData.includes(s.fullName)
        );
      case 3:
        return countSelectedQualities() >= 3;
      case 4:
        return (
          usefulness === 'Полезно в работе (буду применять на практике)' ||
          usefulness === 'Бесполезно в работе (с моими клиентами это не работает)'
        );
      case 5:
        return brightThoughts.trim().length >= 5;
      case 6:
        return additionalSelections.length > 0;
      case 7:
        return impression.trim() !== '';
      case 8:
        return mood.trim() !== '';
      case 9:
        return recommendation !== null;
      default:
        return true;
    }
  };

  // "Далее"
  const handleNext = () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для перехода к следующему шагу');
      return;
    }
    // Если на шаге 3 есть ещё спикеры
    if (currentStep === 3 && currentSpeakerIndex < speakersList.length - 1) {
      setCurrentSpeakerIndex(currentSpeakerIndex + 1);
      return;
    }
    setCurrentStep(prev => prev + 1);
  };

  // "Назад"
  const handlePrev = () => {
    if (currentStep === 3 && currentSpeakerIndex > 0) {
      setCurrentSpeakerIndex(currentSpeakerIndex - 1);
      return;
    }
    if (currentStep > 1) {
      setCurrentStep(prev => prev - 1);
    }
  };

  // "Завершить"
  const handleFinish = async () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для завершения опроса');
      return;
    }
    setIsFinished(true);

    // Сохраняем на сервер
    const data = {
      partner,
      dateTime: new Date().toISOString(),
      commonAnswers: {
        usefulness,
        brightThoughts,
        additionalSuggestions: additionalSelections.join(', '),
        impression,
        mood,
        recommendation,
      },
      speakersFeedback: speakersList.map((speaker, i) => ({
        fullName: speaker.fullName,
        qualities: speakerQualities[i],
      })),
    };

    try {
      const response = await fetch('https://rgszh-miniapp.org/api/proxy/feedback/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include',
      });
      if (!response.ok) {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка при отправке данных:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  // Экран "Спасибо"
  if (isFinished) {
    return (
      <div className="thank-you-message" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <h2 className="thank-you-text">
          Спасибо за прохождение оценки мероприятия{userName ? `, ${userName}` : ''}!
        </h2>
      </div>
    );
  }

  // Автоподсказки
  const getPartnerSuggestionsFunc = ({ value }) => {
    setPartnerSuggestions(getPartnerSuggestions(value));
  };
  const renderSuggestion = suggestion => <div>{suggestion}</div>;

  // Пошаговый рендер
  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="autosuggest-container">
            <h2>Введите название партнёра</h2>
            <Autosuggest
              suggestions={partnerSuggestions}
              onSuggestionsFetchRequested={getPartnerSuggestionsFunc}
              onSuggestionsClearRequested={() => setPartnerSuggestions([])}
              getSuggestionValue={suggestion => suggestion}
              renderSuggestion={renderSuggestion}
              inputProps={{
                placeholder: 'Название партнёра',
                value: partner,
                onChange: (e, { newValue }) => setPartner(newValue),
                autoComplete: 'off',
                onFocus: handleFocus,
                onBlur: handleBlur,
              }}
            />
          </div>
        );
      case 2:
        return (
          <div>
            <h2>Введите ФИО спикера/ов</h2>
            {speakersList.map((speaker, index) => (
              <div key={speaker.id} className="speaker-row">
                <Autosuggest
                  suggestions={speakersSuggestions}
                  onSuggestionsFetchRequested={({ value }) =>
                    setSpeakersSuggestions(getSpeakerSuggestions(value))
                  }
                  onSuggestionsClearRequested={() => setSpeakersSuggestions([])}
                  getSuggestionValue={renderSuggestion}
                  renderSuggestion={renderSuggestion}
                  inputProps={{
                    placeholder: 'например, Иванов Иван',
                    value: speaker.fullName,
                    onChange: (e, { newValue }) => handleSpeakerChange(index, e, { newValue }),
                    autoComplete: 'off',
                    onFocus: handleFocus,
                    onBlur: handleBlur,
                  }}
                />
                {speakersList.length > 1 && (
                  <button
                    type="button"
                    className="delete-speaker-btn"
                    onClick={() => handleRemoveSpeaker(index)}
                  >
                    Удалить
                  </button>
                )}
              </div>
            ))}
            <button type="button" className="add-speaker-btn" onClick={handleAddSpeaker}>
              <span className="add-speaker-symbol">+</span>
            </button>
          </div>
        );
      case 3:
        return (
          <div>
            <h2>Оцените качества спикера</h2>
            <h3>{speakersList[currentSpeakerIndex].fullName || 'Спикер'}</h3>
            {qualityRows.map((row, idx) => {
              const currentValue = speakerQualities[currentSpeakerIndex]?.[idx];
              return (
                <div key={idx} className="quality-row">
                  <button
                    type="button"
                    className={`quality-option-btn ${currentValue === row.positive ? 'positive-selected' : ''}`}
                    onClick={() => handleQualitySelectForSpeaker(idx, 'positive')}
                  >
                    {row.positive}
                  </button>
                  <button
                    type="button"
                    className={`quality-option-btn ${currentValue === row.negative ? 'negative-selected' : ''}`}
                    onClick={() => handleQualitySelectForSpeaker(idx, 'negative')}
                  >
                    {row.negative}
                  </button>
                </div>
              );
            })}
            {countSelectedQualities() < 3 && (
              <p className="quality-warning">Выберите минимум 3 качества</p>
            )}
          </div>
        );
      case 4:
        return (
          <div>
            <h2>Оцените полезность информации</h2>
            <div className="usefulness-container">
              <button
                type="button"
                className={`usefulness-btn ${usefulness === 'Полезно в работе (буду применять на практике)' ? 'positive-selected' : ''}`}
                onClick={() => setUsefulness('Полезно в работе (буду применять на практике)')}
              >
                Полезно в работе (буду применять на практике)
              </button>
              <button
                type="button"
                className={`usefulness-btn ${usefulness === 'Бесполезно в работе (с моими клиентами это не работает)' ? 'negative-selected' : ''}`}
                onClick={() => setUsefulness('Бесполезно в работе (с моими клиентами это не работает)')}
              >
                Бесполезно в работе (с моими клиентами это не работает)
              </button>
            </div>
          </div>
        );
      case 5:
        return (
          <div>
            <h2>Опишите самые яркие мысли с мероприятия</h2>
            <textarea
              className="bright-thoughts-textarea"
              value={brightThoughts}
              onChange={(e) => setBrightThoughts(e.target.value)}
            />
          </div>
        );
      case 6:
        return (
          <div>
            <h2>Что хотелось бы добавить к мероприятию?</h2>
            {additionalOptions.map(option => (
              <button
                key={option}
                type="button"
                className={`add-option-btn ${additionalSelections.includes(option) ? 'selected' : ''}`}
                onClick={() => toggleAdditionalSelection(option)}
              >
                {option}
              </button>
            ))}
          </div>
        );
      case 7:
        return (
          <div>
            <h2>Общее впечатление от тренинга</h2>
            <div className="impression-container">
              <button
                className={`impression-btn ${impression === 'happy' ? 'happy-selected' : ''}`}
                onClick={() => setImpression('happy')}
              >
                <img src={happyGif} alt="Happy" />
              </button>
              <button
                className={`impression-btn ${impression === 'neutral' ? 'neutral-selected' : ''}`}
                onClick={() => setImpression('neutral')}
              >
                <img src={loyalGif} alt="Neutral" />
              </button>
              <button
                className={`impression-btn ${impression === 'sad' ? 'sad-selected' : ''}`}
                onClick={() => setImpression('sad')}
              >
                <img src={sadGif} alt="Sad" />
              </button>
            </div>
          </div>
        );
      case 8:
        return (
          <div>
            <h2>Я чувствовал себя на мероприятии...</h2>
            <div className="mood-container">
              <button
                className={`mood-btn ${mood === 'sad' ? 'mood-selected' : ''}`}
                onClick={() => setMood('sad')}
              >
                😢
              </button>
              <button
                className={`mood-btn ${mood === 'tired' ? 'mood-selected' : ''}`}
                onClick={() => setMood('tired')}
              >
                😩
              </button>
              <button
                className={`mood-btn ${mood === 'fun' ? 'mood-selected' : ''}`}
                onClick={() => setMood('fun')}
              >
                😄
              </button>
              <button
                className={`mood-btn ${mood === 'thoughtful' ? 'mood-selected' : ''}`}
                onClick={() => setMood('thoughtful')}
              >
                🤔
              </button>
              <button
                className={`mood-btn ${mood === 'cringe' ? 'mood-selected' : ''}`}
                onClick={() => setMood('cringe')}
              >
                😬
              </button>
            </div>
          </div>
        );
      case 9:
        return (
          <div>
            <h2>Насколько готовы рекомендовать тренинг?</h2>
            <p>Оцените по шкале от 0 до 10</p>
            <div className="recommendation-slider-container">
              <input
                type="range"
                min="1"
                max="10"
                step="1"
                value={recommendation !== null ? recommendation : 5}
                onChange={(e) => setRecommendation(parseInt(e.target.value, 10))}
                className="recommendation-slider"
              />
              <div
                className="recommendation-value"
                style={{ color: getRecommendationColor(recommendation !== null ? recommendation : 5) }}
              >
                {recommendation !== null ? recommendation : 5}
              </div>
              <div className="recommendation-label">
                {getRecommendationLabel(recommendation !== null ? recommendation : 5)}
              </div>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="feedback-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="content-wrapper" {...swipeHandlers}>

        {renderStep()}

        {/* Кнопка "Назад" – отображаем, если шаг больше 1 */}
        {currentStep > 1 && (
          <div className="back-btn-container">
            <button className="back-btn" onClick={handlePrev}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M12 20l-8-8 8-8" />
              </svg>
            </button>
          </div>
        )}

        {/* Кнопка "Далее" (или "Завершить") */}
        <div className="top-btn-container">
          {currentStep < 9 ? (
            <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M12 4l8 8-8 8" />
              </svg>
            </button>
          ) : (
            <button className="top-btn" onClick={handleFinish} disabled={!canGoNext()}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M5 13l4 4L19 7" />
              </svg>
            </button>
          )}
        </div>

      </div>
    </div>
  );
};

export default FeedbackPage;
