import React, { useEffect, useState } from 'react';
import './fonts.css';  // Подключаем файл со шрифтами
import './Register.css';
import logo from './logo.png';  // Логотип

const Register = () => {
  const [email, setEmail] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  return (
    <div className="register-container">
      <img src={logo} alt="Логотип" className="logo" /> {/* Логотип */}
      <p className="register-subtext">Введите вашу корпоративную почту</p>

      {/* Поле ввода почты */}
      <input
        type="email"
        className="register-input"
        placeholder="Введите email"
        value={email}
        onChange={handleEmailChange}
      />

      {/* Кнопка регистрации */}
      <button className="register-btn">Зарегистрироваться</button>

      {/* Кнопка входа как гость */}
      <button className="guest-btn">Войти как гость</button>
    </div>
  );
};

export default Register;
