// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // — состояния —
  const [step, setStep]                   = useState(1);
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null); // 'мужской' или 'женский'
  const [calcType, setCalcType]           = useState(null);
  const [amountRaw, setAmountRaw]         = useState('');
  const [amountDisplay, setAmountDisplay] = useState('');
  const [amountError, setAmountError]     = useState('');
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);

  // вычисление возраста
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) age--;
    return age;
  }

  // проверка суммы
  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) || (calcType === 'sum' && n >= 450000);
  }

  // можно ли двигаться дальше
  function canGoNext() {
    return (
      step === 2 &&
      getAge(birthDate) >= 18 &&
      Boolean(gender) &&
      Boolean(calcType) &&
      Boolean(amountRaw) &&
      isAmountValid()
    );
  }

  // кнопка «Продолжить» на шаге 1
  function handleEmailNext() {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  }

  // ввод суммы + форматирование
  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum'     && n < 450000) setAmountError('Мин. 450 000 ₽');
    else                                           setAmountError('');
  }

  // кнопка «Далее →» отправляет на /api/proxy/snp/save
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,      // 'мужской' или 'женский'
      payment:     calcType === 'premium'
                   ? 'Страхового взноса'
                   : 'Страховой суммы',
      sum:         parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        const txt = await res.text();
        console.error('Ошибка сервера', res.status, txt);
        alert(`Ошибка ${res.status}: проверьте консоль`);
      } else {
        const data = await res.json();
        setResultData(data);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  // возврат «Домой»
  function goHome() {
    navigate('/MainMenu');
  }

  // экран «выполняется расчёт…»
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <p>Выполняется расчёт...</p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // экран с результатом / параметрами
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });

    return (
      <div
        className="snp-container"
        style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        {/* Шапка */}
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
            </svg>
          </button>
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
        </div>

        {/* Главный заголовок */}
        <h1 className="polls-title" style={{ marginTop: '12px' }}>
          Расчёт по программе «Стратегия на пять. Гарант»
        </h1>

        {/* Параметры */}
        <div className="params-container">
          <h2>Параметры</h2>
          <p>Дата расчёта: {todayStr}</p>
          <p>Возраст: {getAge(birthDate)} лет</p>
          <p>Пол: {gender}</p>
          <p>
            Вариант расчёта:{' '}
            {calcType === 'premium'
              ? 'Страхового взноса'
              : 'Страховой суммы'}
          </p>
        </div>
      </div>
    );
  }

  // основной интерфейс — шаги 1 и 2
  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Шапка */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
          </svg>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        {step === 2 && (
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">→</span>
          </button>
        )}
      </div>

      {/* Шаг 1 */}
      {step === 1 && (
        <>
          <div className="email-step">
            <label className="email-label">
              Корпоративная почта ВТБ:
            </label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && (
            <div className="input-error">{emailError}</div>
          )}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              Продолжить
            </button>
          </div>
        </>
      )}

      {/* Шаг 2 */}
      {step === 2 && (
        <>
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          <div className="date-section">
            <label className="section-label">
              Дата рождения:
            </label>
            <DateWheelPicker
              value={birthDate}
              onChange={val => {
                if (val instanceof Date) setBirthDate(val);
                else if (val?.day)
                  setBirthDate(
                    new Date(val.year, val.month - 1, val.day)
                  );
              }}
            />
          </div>

          <div className="section-gap" />
          <div className="section-subtitle">
            Выберите пол:
          </div>
          <div className="gender-section">
            <button
              className={`insurance-btn ${
                gender === 'мужской' ? 'selected' : ''
              }`}
              onClick={() =>
                setGender(prev =>
                  prev === 'мужской' ? null : 'мужской'
                )
              }
            >
              <div>Мужской</div>
              <div className="emoji">👨</div>
            </button>
            <button
              className={`insurance-btn ${
                gender === 'женский' ? 'selected' : ''
              }`}
              onClick={() =>
                setGender(prev =>
                  prev === 'женский' ? null : 'женский'
                )
              }
            >
              <div>Женский</div>
              <div className="emoji">👩</div>
            </button>
          </div>

          <div className="section-gap" />
          <div className="section-subtitle">
            Расчёт от…
          </div>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${
                calcType === 'premium' ? 'selected' : ''
              }`}
              onClick={() =>
                setCalcType(prev =>
                  prev === 'premium' ? null : 'premium'
                )
              }
            >
              <div>Страхового</div>
              <div>взноса</div>
            </button>
            <button
              className={`insurance-btn ${
                calcType === 'sum' ? 'selected' : ''
              }`}
              onClick={() =>
                setCalcType(prev =>
                  prev === 'sum' ? null : 'sum'
                )
              }
            >
              <div>Страховой</div>
              <div>суммы</div>
            </button>
          </div>

          {calcType && (
            <>
              <div className="section-gap" />
              <div className="section-subtitle">Сумма (₽):</div>
              <div className="sum-section">
                <input
                  type="text"
                  placeholder={
                    calcType === 'premium'
                      ? 'Мин. 60 000'
                      : 'Мин. 450 000'
                  }
                  value={amountDisplay}
                  onChange={onAmountChange}
                />
              </div>
              {amountError && (
                <div className="input-error">{amountError}</div>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
}
