import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EmployeePage.css';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import mainmenuIcon    from './components/icons/mainmenu.png';

export default function EmployeePage() {
  const navigate = useNavigate();
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    // Запускаем анимацию появления
    setAnimate(true);
  }, []);

  // Универсальный ripple + отложенный навигейт
  const handleClick = (e, to) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width  = circle.style.height = `${d}px`;
    circle.style.left   = `${e.clientX - btn.offsetLeft - r}px`;
    circle.style.top    = `${e.clientY - btn.offsetTop  - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    setTimeout(() => navigate(to), 300);
  };

  const buttons = [
    { to: '/justincase', label: 'На всякий случай' },
    { to: '/snp',        label: 'Стратегия на пять. Гарант' },
    { to: '/carefuture', label: 'Забота о будущем' }
  ];

  return (
    <div
      className="employee-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Glass-overlay */}
      <div className="employee-overlay" />

      {/* Кнопка «Домой» */}
      <button
        className="back-btn"
        onClick={() => navigate('/main-menu')}
      >
        <img src={mainmenuIcon} alt="Домой" />
      </button>

      {/* Анимированный логотип */}
      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="Логотип" className="employee-logo" />
      </div>

      {/* Заголовок */}
      <h1 className={`employee-title ${animate ? 'animate-title' : ''}`}>
        Страница для сотрудника
      </h1>

      {/* Кнопки */}
      <div className={`employee-buttons ${animate ? 'animate-buttons' : ''}`}>
        {buttons.map((b, i) => (
          <button
            key={i}
            className="employee-btn"
            onClick={e => handleClick(e, b.to)}
          >
            {b.label}
          </button>
        ))}
      </div>
    </div>
  );
}
