import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // —Å–æ—Å—Ç–æ—è–Ω–∏—è —à–∞–≥–æ–≤
  const [step, setStep]                   = useState(1);
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null);
  const [calcType, setCalcType]           = useState(null);
  const [amountRaw, setAmountRaw]         = useState('');
  const [amountDisplay, setAmountDisplay] = useState('');
  const [amountError, setAmountError]     = useState('');
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);

  // –¥–ª—è –∫–∞—Ä—É—Å–µ–ª–∏
  const [carouselIndex, setCarouselIndex] = useState(0);

  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) age--;
    return age;
  }

  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) ||
           (calcType === 'sum'     && n >= 450000);
  }

  function canGoNext() {
    return step === 2 &&
           getAge(birthDate) >= 18 &&
           gender &&
           calcType &&
           amountRaw &&
           isAmountValid();
  }

  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('–ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—É—é –ø–æ—á—Ç—É –¥–æ–º–µ–Ω–∞ @vtb.ru');
      return;
    }
    setEmailError('');
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000) setAmountError('–ú–∏–Ω. 60 000 ‚ÇΩ');
    else if (calcType === 'sum'     && n < 450000) setAmountError('–ú–∏–Ω. 450 000 ‚ÇΩ');
    else                                           setAmountError('');
  }

  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: {'Content-Type':'application/json'},
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞', res.status, await res.text());
        alert(`–û—à–∏–±–∫–∞ ${res.status}: —Å–º–æ—Ç—Ä–∏—Ç–µ –∫–æ–Ω—Å–æ–ª—å`);
      } else {
        const data = await res.json();
        setResultData(data);
        setCarouselIndex(0);  // —Å—Ç–∞—Ä—Ç—É–µ–º —Å –ø–µ—Ä–≤–æ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã
      }
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:', err);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –≤—ã–ø–æ–ª–Ω–∏—Ç—å –∑–∞–ø—Ä–æ—Å');
    } finally {
      setIsProcessing(false);
    }
  }

  function goHome() {
    navigate(-1);
  }

  // --- —ç–∫—Ä–∞–Ω "–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç..." ---
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" />
        </div>
        <h2 className="processing-title">
          –í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ<br/>
          ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª
        </h2>
        <div className="arrow-animation">‚Üí</div>
      </div>
    );
  }

  // --- —ç–∫—Ä–∞–Ω —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ —Å –¥–≤—É–º—è –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞–º–∏ –∏ –∫–∞—Ä—É—Å–µ–ª—å—é ---
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU',{
      day:'2-digit', month:'2-digit', year:'numeric'
    });
    // –ø–æ–ª—É—á–∏–º –∏ –æ—Ç—Ñ–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º c13 –∏ c16
    const c13 = Number(resultData.c13 || 0);
    const c16 = Number(resultData.c16 || 0);
    const fmt = new Intl.NumberFormat('ru-RU',{
      minimumFractionDigits:2, maximumFractionDigits:2
    });
    const c13f = fmt.format(c13);
    const c16f = fmt.format(c16);

    return (
      <div className="snp-container" style={{backgroundImage:`url(${backgroundImage})`}}>
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø"/>
          </div>

          <button
            className="repeat-btn"
            onClick={()=>{
              setResultData(null);
              setGender(null);
              setCalcType(null);
              setAmountRaw('');
              setAmountDisplay('');
              setAmountError('');
              setStep(2);
              setCarouselIndex(0);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="currentColor"/>
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          {/* –ø–µ—Ä–≤—ã–π –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä */}
          <h1 className="polls-title">
            –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª
          </h1>
          <div className="params-container">
            <h2>–ü–∞—Ä–∞–º–µ—Ç—Ä—ã</h2>
            <p>–î–∞—Ç–∞ —Ä–∞—Å—á—ë—Ç–∞: {todayStr}</p>
            <p>–í–æ–∑—Ä–∞—Å—Ç: {getAge(birthDate)} –ª–µ—Ç</p>
            <p>–ü–æ–ª: {gender}</p>
            <p>
              –í–∞—Ä–∏–∞–Ω—Ç —Ä–∞—Å—á—ë—Ç–∞:{' '}
              {calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã'}
            </p>
          </div>

          {/* –≤—Ç–æ—Ä–æ–π –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä-–∫–∞—Ä—É—Å–µ–ª—å */}
          <div className="carousel-container">
            {/* –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä—ã */}
            <div className="carousel-indicators">
              {[0,1,2].map(i => (
                <span key={i} className={i===carouselIndex?'active':''}/>
              ))}
            </div>

            {/* —Å—Ç—Ä–∞–Ω–∏—Ü—ã */}
            <div
              className="carousel-wrapper"
              style={{transform:`translateX(-${carouselIndex*100}%)`}}
            >
              {/* ‚Äî –ü–µ—Ä–≤–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ ‚Äî */}
              <div className="carousel-page">
                <p className="header-line">–í —Ç–µ—á–µ–Ω–∏–µ 5 –ª–µ—Ç</p>
                <p className="subheader">–í—ã –≤–Ω–æ—Å–∏—Ç–µ —Å—Ä–µ–¥—Å—Ç–≤–∞ –≤ –ø—Ä–æ–≥—Ä–∞–º–º—É</p>
                <p className="amount-large">{c13f} ‚ÇΩ</p>
                <p className="unit">–ï–∂–µ–≥–æ–¥–Ω–æ</p>
                <p className="amount-large">{c16f} ‚ÇΩ</p>
                <p className="unit">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</p>
              </div>
              {/* ‚Äî –í—Ç–æ—Ä–∞—è –∏ —Ç—Ä–µ—Ç—å—è (–ø—É—Å—Ç—ã–µ) ‚Äî */}
              <div className="carousel-page">{/* –ø–æ–∑–∂–µ */}</div>
              <div className="carousel-page">{/* –ø–æ–∑–∂–µ */}</div>
            </div>

            {/* —Å—Ç—Ä–µ–ª–∫–∏ */}
            {carouselIndex > 0 && (
              <button
                className="carousel-control prev"
                onClick={()=>setCarouselIndex(i=>i-1)}
              >‚Äπ</button>
            )}
            {carouselIndex < 2 && (
              <button
                className="carousel-control next"
                onClick={()=>setCarouselIndex(i=>i+1)}
              >‚Ä∫</button>
            )}
          </div>
        </div>
      </div>
    );
  }

  // --- —à–∞–≥–∏ 1 –∏ 2 ---
  return (
    <div className="snp-container" style={{backgroundImage:`url(${backgroundImage})`}}>
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
          </svg>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø"/>
        </div>
        <button
          className="forward-btn"
          onClick={step===1?handleEmailNext:handleNext}
          disabled={step===1?!emailValid:!canGoNext()}
        >
          <span className="arrow-icon">‚Üí</span>
        </button>
      </div>

      <div className="content-wrapper">
        {step===1 && (
          <>
            <div className="email-step">
              <label className="email-label">
                –ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:
              </label>
              <input
                type="email"
                placeholder="example@vtb.ru"
                value={email}
                onChange={e=>setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}
            <button
              className="polls-btn btn1"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              –ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å
            </button>
          </>
        )}
        {step===2 && (
          <>
            <h1 className="polls-title">
              –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª
            </h1>
            <div className="date-section">
              <label className="section-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val=>{
                  if(val instanceof Date) setBirthDate(val);
                  else if(val?.day)
                    setBirthDate(new Date(val.year,val.month-1,val.day));
                }}
              />
            </div>
            <div className="section-subtitle">–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender==='–º—É–∂—Å–∫–æ–π'?'selected':''}`}
                onClick={()=>setGender('–º—É–∂—Å–∫–æ–π')}
              >
                <div>–ú—É–∂—Å–∫–æ–π</div><div className="emoji">üë®</div>
              </button>
              <button
                className={`insurance-btn ${gender==='–∂–µ–Ω—Å–∫–∏–π'?'selected':''}`}
                onClick={()=>setGender('–∂–µ–Ω—Å–∫–∏–π')}
              >
                <div>–ñ–µ–Ω—Å–∫–∏–π</div><div className="emoji">üë©</div>
              </button>
            </div>
            <div className="section-subtitle">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                onClick={()=>setCalcType('premium')}
              >
                <div>–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ</div><div>–≤–∑–Ω–æ—Å–∞</div>
              </button>
              <button
                className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                onClick={()=>setCalcType('sum')}
              >
                <div>–°—Ç—Ä–∞—Ö–æ–≤–æ–π</div><div>—Å—É–º–º—ã</div>
              </button>
            </div>
            {calcType && (
              <>
                <div className="section-subtitle">–°—É–º–º–∞ (‚ÇΩ):</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={calcType==='premium'?'–ú–∏–Ω. 60 000':'–ú–∏–Ω. 450 000'}
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
