// src/SNPPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // — Шаги и карусель —
  const [step, setStep]                   = useState(1);     // 1 – ввод почты, 2 – ввод данных
  const [carouselIndex, setCarouselIndex] = useState(0);     // 0, 1, 2 – страницы карусели
  const [showContact, setShowContact]     = useState(false); // показать форму контактов (ФИО, Город)
  const [isProcessing, setIsProcessing]   = useState(false); // экран «выполняется расчёт»
  const [resultData, setResultData]       = useState(null);  // данные из сервера

  // — Шаг 1: ввод корпоративной почты —
  const [email, setEmail]           = useState('');
  const [emailError, setEmailError] = useState('');
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  // — Шаг 2: личные данные —
  const [birthDate, setBirthDate]   = useState(new Date());
  const [gender, setGender]         = useState(null);    // 'мужской' | 'женский' | null
  const [calcType, setCalcType]     = useState(null);    // 'premium' | 'sum' | null
  const [amountRaw, setAmountRaw]   = useState('');
  const [amountDisplay, setAmountDisplay] = useState('');
  const [amountError, setAmountError]     = useState('');

  // — Шаг 3: контактная форма (ФИО, Город) —
  const [surname, setSurname]       = useState('');
  const [firstName, setFirstName]   = useState('');
  const [patronymic, setPatronymic] = useState('');
  const [city, setCity]             = useState('');

  // — Утилиты: расчёт возраста и проверка суммы —
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) {
      age--;
    }
    return age;
  }

  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) || (calcType === 'sum' && n >= 450000);
  }

  function canGoNext() {
    return (
      step === 2 &&
      getAge(birthDate) >= 18 &&
      gender &&
      calcType &&
      amountRaw &&
      isAmountValid()
    );
  }

  // — Перейти с шага 1 на шаг 2 —
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // Сброс полей второго шага:
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  // — Форматирование ввода суммы («число → строка с точками») —
  function onAmountChange(e) {
    const raw  = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);

    // Делим на триады
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000 ) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum'     && n < 450000) setAmountError('Мин. 450 000 ₽');
    else                                           setAmountError('');
  }

  // — Искать данные для отправки —
  function goHome() {
    navigate(-1);
  }

  // — Обработка перехода со второго шага на «Выполнить расчёт» и получение данных —
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type':'application/json' },
        body:    JSON.stringify(payload)
      });
      if (!res.ok) {
        console.error('Ошибка прокси', res.status, await res.text());
        alert(`Ошибка ${res.status}`);
      } else {
        const data = await res.json();
        setResultData(data);
        setCarouselIndex(0);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  // — Экран «Выполняется расчёт…» —
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <h2 className="processing-title">
          Выполняется расчёт по программе<br/>
          «Стратегия на пять. Гарант»
        </h2>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // — Экран результата + Карусель или форма контакта —
  if (resultData) {
    // текущая дата
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day:'2-digit', month:'2-digit', year:'numeric'
    });

    // Форматер для денег (два знака после запятой)
    const fmtMoney = new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    });

    // Из результата от сервера (server.py) берём именно эти ключи:
    //   contribution        ← C13
    //   totalContribution   ← C16
    //   accrualPercent      ← H12 (например 44)
    //   accrualAmount       ← H13 (например 52800)
    //   totalAccrual        ← H16 (например 264000)
    const c13         = Number(resultData.contribution)      || 0;
    const c16         = Number(resultData.totalContribution) || 0;
    const ratePercent = Number(resultData.accrualPercent)    || 0;
    const gainPerYear = Number(resultData.accrualAmount)     || 0;
    const totalGain   = Number(resultData.totalAccrual)      || 0;

    // Отформатированные строки
    const c13f = fmtMoney.format(c13);
    const c16f = fmtMoney.format(c16);
    const gainPerYearF = fmtMoney.format(gainPerYear);
    const totalGainF   = fmtMoney.format(totalGain);

    // Функции переключения страниц карусели
    function prevPage() {
      if (carouselIndex > 0) setCarouselIndex(carouselIndex - 1);
    }
    function nextPage() {
      if (carouselIndex < 2) setCarouselIndex(carouselIndex + 1);
    }

    return (
      <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>
        {/* ===== Шапка: Домой, Логотип, Повторить ===== */}
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип"/>
          </div>

          <button className="repeat-btn" onClick={() => {
              // при повторном расчёте — сброс локальных состояний и открываем шаг 2
              setResultData(null);
              setStep(2);
              setShowContact(false);
              setCarouselIndex(0);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="white"
              />
            </svg>
          </button>
        </div>

        {/* ===== Заголовок «Расчёт по программе …» ===== */}
        <div className="content-wrapper">
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>
        </div>

        {/* ===== Блок «Параметры» ===== */}
        <div className="content-wrapper">
          <div className="params-container">
            <h2>Параметры</h2>
            <div className="param-row">Дата расчёта: {todayStr}</div>
            <div className="param-row">Возраст: {getAge(birthDate)} лет</div>
            <div className="param-row">Пол: {gender}</div>
            <div className="param-row">
              Вариант расчёта: {calcType === 'premium'
                ? 'Страхового взноса'
                : 'Страховой суммы'}
            </div>
          </div>
        </div>

        {/*
          ===== Второй полупрозрачный контейнер: карусель (3 страницы) =====
          overflow: hidden, фиксированная ширина и высота.
        */}
        <div className="content-wrapper">
          <div className="carousel-container">

            {/* Полоски-индикаторы (3 штуки) */}
            <div className="carousel-indicators">
              <span className={carouselIndex === 0 ? 'active' : ''} />
              <span className={carouselIndex === 1 ? 'active' : ''} />
              <span className={carouselIndex === 2 ? 'active' : ''} />
            </div>

            {/* Содержимое карусели: 3 «страницы» */}
            <div className="carousel-slider" style={{
              transform: `translateX(-${carouselIndex * 100}%)`
            }}>
              {/* === Страница 0: «В течение 5 лет…» === */}
              <div className="carousel-page">
                <div className="carousel-text-large">В течение 5 лет</div>
                <div className="carousel-text-small">Вы вносите средства в программу</div>
                <div className="carousel-text-number">{c13f} ₽</div>
                <div className="carousel-text-small">Ежегодно</div>
                <div className="carousel-text-number">{c16f} ₽</div>
                <div className="carousel-text-small">Сумма ваших взносов</div>
              </div>

              {/* === Страница 1: «Начисляем дополнительно…» === */}
              <div className="carousel-page">
                <div className="carousel-text-large">Начисляем дополнительно</div>
                <div className="carousel-text-small"> {ratePercent}% от каждого взноса</div>
                <div className="carousel-text-number">{fmtMoney.format(gainPerYear)} ₽</div>
                <div className="carousel-text-small">Ежегодно</div>
                <div className="carousel-text-number">{fmtMoney.format(totalGain)} ₽</div>
                <div className="carousel-text-small">Общая сумма начислений</div>
              </div>

              {/* === Страница 2: (заполнится позднее, пока пустая) === */}
              <div className="carousel-page">
                {/* Оставляем «заглушку» пустую, чтобы потом наполнить */}
              </div>
            </div>

            {/* Стрелки «‹» и «›» поверх контейнера (оставляем их position:absolute) */}
            {carouselIndex > 0 && (
              <button className="carousel-arrow left" onClick={prevPage}>
                ‹
              </button>
            )}
            {carouselIndex < 2 && (
              <button className="carousel-arrow right" onClick={nextPage}>
                ›
              </button>
            )}
          </div>
        </div>

        {/* ===== Кнопка «Связаться с менеджером» ===== */}
        <div className="content-wrapper" style={{ marginBottom: '40px' }}>
          <button
            className="polls-btn btn1"
            onClick={() => setShowContact(true)}
          >
            Связаться с менеджером
          </button>
        </div>

        {/*
          ===== Шаг 3: Форма «Ваши контактные данные» (ФИО + Город) =====
          Показываем только если showContact = true
        */}
        {showContact && (
          <div className="content-wrapper">
            <h1 className="polls-title">Ваши контактные данные</h1>

            <div className="email-step">
              <label className="email-label">Фамилия:</label>
              <input
                type="text"
                placeholder="Введите фамилию"
                value={surname}
                onChange={e => setSurname(e.target.value)}
              />
            </div>
            <div className="email-step">
              <label className="email-label">Имя:</label>
              <input
                type="text"
                placeholder="Введите имя"
                value={firstName}
                onChange={e => setFirstName(e.target.value)}
              />
            </div>
            <div className="email-step">
              <label className="email-label">Отчество:</label>
              <input
                type="text"
                placeholder="Введите отчество"
                value={patronymic}
                onChange={e => setPatronymic(e.target.value)}
              />
            </div>
            <div className="email-step">
              <label className="email-label">Город работы:</label>
              <input
                type="text"
                placeholder="Введите город"
                value={city}
                onChange={e => setCity(e.target.value)}
              />
            </div>

            <button
              className="polls-btn btn1"
              disabled={!surname || !firstName || !city}
              onClick={() => {
                // Здесь можно сделать отправку данных менеджеру через proxy_send_manager
                const contactPayload = {
                  email,
                  surname,
                  firstName,
                  patronymic,
                  city,
                  program: 'Стратегия на пять. Гарант',
                  contribution: c13,
                  totalContribution: c16,
                  accrualPercent: ratePercent,
                  accrualAmount: gainPerYear,
                  totalAccrual: totalGain
                };
                fetch('/api/proxy/snp/send_manager', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(contactPayload)
                })
                  .then(r => {
                    if (r.ok) {
                      alert('Менеджеру отправлены ваши данные');
                      // Можно дальше что-то делать...
                    } else {
                      alert('Ошибка отправки, попробуйте позже');
                    }
                  })
                  .catch(err => {
                    console.error(err);
                    alert('Ошибка связи');
                  });
              }}
            >
              Отправить менеджеру
            </button>
          </div>
        )}

      </div>
    );
  }

  // ===== Основной UI (step 1 или step 2) =====
  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>

      {/* ===== Шапка: кнопки «Домой», «Логотип», «Далее/→» ===== */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
          </svg>
        </button>

        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип"/>
        </div>

        <button
          className="forward-btn"
          onClick={step === 1 ? handleEmailNext : handleNext}
          disabled={step === 1 ? !emailValid : !canGoNext()}
        >
          <span className="arrow-icon">→</span>
        </button>
      </div>

      {/* ===== Контент ниже шапки ===== */}
      <div className="content-wrapper">

        {step === 1 && (
          <>
            {/* --- Лейбл «Корпоративная почта ВТБ:» --- */}
            <label className="email-label email-step-label">
              Корпоративная почта ВТБ:
            </label>

            {/* --- Поле ввода email --- */}
            <input
              type="email"
              className="email-input"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />

            {emailError && <div className="input-error">{emailError}</div>}

            {/* --- Кнопка «Продолжить» (та же, что и «Далее» справа, но текстовая) --- */}
            <button
              className="polls-btn btn1"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              Продолжить
            </button>
          </>
        )}

        {step === 2 && (
          <>
            {/* --- Заголовок (шаг 2) --- */}
            <h1 className="polls-title polls-title-step2">
              Расчёт по программе «Стратегия на пять. Гарант»
            </h1>

            {/* --- Дата рождения --- */}
            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) setBirthDate(val);
                  else if (val?.day) {
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                  }
                }}
              />
            </div>

            {/* --- Выберите пол --- */}
            <div className="section-subtitle">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender('мужской')}
              >
                <div>Мужской</div>
                <div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender('женский')}
              >
                <div>Женский</div>
                <div className="emoji">👩</div>
              </button>
            </div>

            {/* --- Расчёт от… --- */}
            <div className="section-subtitle">Расчёт от&hellip;</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => setCalcType('premium')}
              >
                <div>Страхового</div>
                <div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => setCalcType('sum')}
              >
                <div>Страховой</div>
                <div>суммы</div>
              </button>
            </div>

            {/* --- Сумма (₽) --- */}
            {calcType && (
              <>
                <div className="section-subtitle">Сумма (₽):</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={calcType === 'premium' ? 'Мин. 60 000' : 'Мин. 450 000'}
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}

      </div>
    </div>
  );
}
