// src/SNPPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './SNPPage.css';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';

export default function SNPPage() {
  const navigate = useNavigate();
  const [theme, setTheme] = useState('dark');
  const [animate, setAnimate] = useState(false);
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    setAnimate(true);
  }, []);

  const toggleTheme = () => {
    setTheme(t => (t === 'dark' ? 'light' : 'dark'));
  };

  // ripple + –∑–∞–¥–µ—Ä–∂–∫–∞ –ø–µ—Ä–µ–¥ –¥–µ–π—Å—Ç–≤–∏–µ–º
  const handleClick = (e, action) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width = circle.style.height = `${d}px`;
    circle.style.left = `${e.clientX - btn.offsetLeft - r}px`;
    circle.style.top = `${e.clientY - btn.offsetTop - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    setTimeout(action, 300);
  };

  const handleNext = () => {
    if (!email.endsWith('@vtb.ru')) {
      setError('–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—ã–π e-mail VTB');
      return;
    }
    navigate('/snp/form');
  };

  return (
    <div className={`snp-container ${theme}`}>
      <div className="snp-overlay" />

      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? 'üåû' : 'üåô'}
      </button>

      <button className="back-btn" onClick={() => navigate('/main-menu')}>
        {/* SVG –¥–æ–º–∏–∫–∞ */}
        <svg width="24" height="24" viewBox="0 0 24 24">
          <path
            d="M3 11l9-9 9 9v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"
            fill="none"
            stroke="#fff"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </button>

      <div className={`logo-wrapper ${animate ? 'animate-logo' : ''}`}>
        <img src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" className="main-logo" />
      </div>

      <h1 className="snp-title">
        –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª
      </h1>

      <div className="form-group">
        <label htmlFor="email">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
        <input
          id="email"
          type="email"
          value={email}
          onChange={e => { setEmail(e.target.value); setError(''); }}
          placeholder="name@vtb.ru"
        />
      </div>
      {error && <div className="input-error">{error}</div>}

      <button
        className="primary-btn"
        disabled={!email}
        onClick={e => handleClick(e, handleNext)}
      >
        –ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–∞—Å—á—ë—Ç—É
      </button>
    </div>
  );
}
